import{_ as p,o as t,c as r,e as o}from"./app.fffb9a3b.js";const n={},s=o('<h1 id="\u7B2C\u4E94\u5341\u516D\u7AE0-\u8109\u51B2\u661F" tabindex="-1"><a class="header-anchor" href="#\u7B2C\u4E94\u5341\u516D\u7AE0-\u8109\u51B2\u661F" aria-hidden="true">#</a> \u7B2C\u4E94\u5341\u516D\u7AE0 \u8109\u51B2\u661F</h1><p>\u8FD9\u662F\u7269\u7406\u79D1\u5B66\u4E2D\u7684\u4E00\u4E2A\u663E\u8457\u7684\u4E8B\u5B9E\uFF0C\u6211\u4EEC\u56E0\u4E3A\u7406\u667A\u800C\u5FFD\u7565\u4E86\u8FD9\u4E2A\u4E8B\u5B9E\uFF0C\u65F6\u95F4\u6CA1\u6709\u771F\u6B63\u7684\u610F\u4E49\u3002\u5B83\u53EA\u662F\u4E00\u4E2A\u6570\u5B66\u610F\u4E49\u4E0A\u7684\u7BAD\u5934\uFF0C\u662F\u4E00\u79CD\u56E0\u679C\u5173\u7CFB\uFF0C\u6307\u5411\u53EF\u9884\u6D4B\u6027\u7684\u65B9\u5411\uFF0C\u8FD9\u5C31\u662F\u65F6\u95F4\u7684\u5168\u90E8\u3002\u5982\u679C\u4F60\u4ECE\u5B57\u9762\u4E0A\u7406\u89E3\u8FD9\u4E9B\u65B9\u7A0B\u5F0F\uFF0C\u6211\u4EEC\u7684\u547D\u8FD0\u65E9\u5DF2\u88AB\u4E66\u5199\uFF0C\u76F4\u81F3\u65F6\u95F4\u7684\u5C3D\u5934\uFF0C\u9664\u975E\u4F60\u80FD\u4ECE\u591A\u7EF4\u7684\u89C6\u89D2\u53BB\u89C2\u6D4B\u5B87\u5B99\u3002</p><p>\u90A3\u4E48\uFF0C\u73B0\u5728\u5DF2\u7ECF\u5230\u4E86 \u201C\u90A3\u4E00\u523B\u201D \u4E86\u561B\uFF1F\u53EA\u751F\u6D3B\u5728\u4E00\u6761\u65F6\u95F4\u7EBF\u91CC\u610F\u5473\u7740\u4EC0\u4E48\uFF1F\u8BF4\u5230\u5E95\uFF0C\u7075\u9B42\u5230\u5E95\u662F\u4EC0\u4E48\uFF1F\u5982\u679C\u662F\u8FD9\u6837\uFF0C\u7075\u9B42\u80FD\u6539\u53D8\u547D\u8FD0\u5417\uFF1F</p><p>\u2014\u2014 \u514B\u83B1\u4E3D\u4E1D \u51E1 \u7F57\u65AF\uFF0C\u4E2A\u4EBA\u535A\u5BA2\u63A8\u9001\uFF0CMSY\u201C\u5E95\u6BD4\u65AF \u201D \u793E\u533A\u535A\u5BA2\u5E73\u53F0</p><p>\u201C\u5F88\u9AD8\u5174\u77E5\u9053\u5B87\u5B99\u662F\u81EA\u6D3D\u7684\uFF0C\u8DB3\u4EE5\u5904\u7406\u4E00\u4E9B\u5C0F\u5C0F\u7684\u53D8\u6570\uFF0C\u4E00\u4E9B\u8FDD\u53CD\u5B87\u5B99\u89C4\u5F8B\u7684\u884C\u4E3A\uFF0C\u800C\u4E0D\u81F3\u4E8E\u5B8C\u5168\u5D29\u6E83\u3002\u6BD5\u7ADF\uFF0C\u5F53\u4F60\u5904\u4E8E\u771F\u5B9E\u7684\u65F6\u95F4\u7EBF\u4E0A\uFF0C\u4F60\u670B\u53CB\u7684\u4E00\u4E9B\u672C\u4F53\u8BBA\u6096\u8BBA\u53C8\u7B97\u5F97\u4E0A\u4EC0\u4E48\u5462\uFF1F\u201D</p><p>\u2014 \u9676\u5C11\u6770\u4E3B\u4EFB\uFF0C\u79C1\u4EBA\u8BAF\u606F\uFF0C2441\u3002</p><hr><p>\u897F\u8499\u5A1C \u5FB7\u5C14 \u9A6C\u6208\u771F\u7684\u5F88\u60CA\u8BB6\u770B\u5230\u5979\u4EEC\u90FD\u5835\u5728\u5979\u7684\u95E8\u53E3\u3002</p><p>\u5979\u4EEC\u53EF\u4EE5\u770B\u51FA\u8FD9\u79CD\u60CA\u8BB6\uFF0C\u56E0\u4E3A\u897F\u8499\u5A1C\u8BA9\u5979\u4EEC\u5728\u95E8\u524D\u7B49\u4E86\u4E00\u4F1A\uFF0C\u5F53\u95E8\u6253\u5F00\u7684\u65F6\u5019\u5979\u4EEC\u770B\u5230\u6E05\u6D01\u65E0\u4EBA\u673A\u6B63\u5728\u5979\u8EAB\u540E\u6E05\u7406\u7740\u6B8B\u7FB9\u5269\u996D\u3002</p><p>\u201C\u54E6\u543C\uFF0C\u7A00\u5BA2\uFF0C\u201D \u897F\u8499\u5A1C\u5FEB\u901F\u73AF\u89C6\u56DB\u5468 \u201C\u4EC0\u4E48\u4E8B\u554A\uFF1F\u201D</p><p>\u826F\u5B50\u611F\u89C9\u5230\u897F\u8499\u5A1C\u7684\u76EE\u5149\u77A5\u5230\u4E86\u81EA\u5DF1\u5E76\u505C\u5728\u81EA\u5DF1\u8EAB\u4E0A\u3002</p><p>\u826F\u5B50\u4E0D\u7531\u5730\u611F\u5230\u4E0D\u81EA\u5728\uFF0C\u5373\u4FBF\u5979\u5DF2\u7ECF\u9F13\u52A8\u8FC7\u81EA\u5DF1\u3002\u5979\u672C\u5E0C\u671B\u8FD9\u6CA1\u5FC5\u8981\u8FD9\u4E48\u505A\uFF0C\u4F46\u662F\u5927\u5BB6\u65E9\u5DF2\u6253\u5B9A\u4E3B\u610F\u3002\u5979\u4EEC\u5C1D\u8BD5\u4E86\u5E15\u7279\u4E3D\u590F\u5173\u4E8E\u65E0\u4EBA\u673A\u7684\u4E3B\u610F\uFF0C\u5F17\u62C9\u5FB7\u52C9\u5F3A\u540C\u610F\uFF0C\u4F46\u5F17\u62C9\u5FB7\u544A\u8BC9\u4ED6\u4EEC \u2014\u2014 \u897F\u8499\u5A1C\u5728\u7A7A\u95F4\u7AD9\u7684\u751F\u6D3B\u51E0\u4E4E\u53EF\u4EE5\u7528\u65E0\u804A\u6765\u5F62\u5BB9\uFF0C\u9664\u4E86\u6BCF\u5929\u5728\u98DF\u5802\uFF0C\u9605\u89C8\u5BA4\u548C\u5A31\u4E50\u533A\u6765\u56DE\u6E38\u8361\u4E4B\u5916\u6CA1\u6709\u4EFB\u4F55\u7684\u53D1\u73B0\u3002</p><p>\u5979\u4EEC\u672C\u53EF\u4EE5\u518D\u7B49\u4E00\u6BB5\u65F6\u95F4\uFF0C\u4F46\u5979\u8BA4\u4E3A\u8FD9\u6BEB\u65E0\u4EF7\u503C\u3002\u5979\u4EEC\u4E4B\u95F4\u552F\u4E00\u7684\u5206\u6B67\u5C31\u662F\u662F\u5426\u8BA9\u514B\u83B1\u4E3D\u4E1D\u30FB\u8303\u30FB\u7F57\u68EE\u52A0\u5165\u5979\u4EEC\uFF0C\u4F46\u5979\u4E0D\u8BA4\u4E3A\u514B\u83B1\u4E3D\u4E1D\u60F3\u8FD9\u4E48\u505A \u2014\u2014 \u514B\u83B1\u4E3D\u4E1D\u5728\u4E4B\u524D\u5979\u4EEC\u5C1D\u8BD5\u8C08\u53CA\u8FD9\u4E2A\u8BDD\u9898\u7684\u65F6\u5019\u5DF2\u7ECF\u975E\u5E38\u8C28\u614E\u4E86\u3002</p><p>\u8FD9\u5C31\u662F\u5979\u4EEC\u73B0\u5728\u7684\u5904\u5883\u3002</p><p>\u201C\u6211\u4EEC\u53EA\u60F3\u95EE\u4F60\u51E0\u4E2A\u95EE\u9898\u201D \u963F\u5179\u745E\u5C14\u4ED7\u7740\u81EA\u5DF1\u6700\u6709\u7ECF\u9A8C\u5F00\u59CB\u8BF4\u3002\u201C\u4F60\u4E0D\u4ECB\u610F\u6211\u4EEC\u8FDB\u95E8\u5427\uFF1F\u201D</p><p>\u201C\u5F53\u7136\u4E0D\u4ECB\u610F\u201D \u897F\u8499\u5A1C\u8BF4\uFF0C\u5C3D\u7BA1\u5979\u7684\u4E0D\u60C5\u613F\u90FD\u5199\u5728\u4E86\u8138\u4E0A\u3002\u201C\u6211\u8FD9\u513F\u6709\u4E9B\u65E0\u4EBA\u673A\u53EF\u4EE5\u51C6\u5907\u70B9\u70B9\u5FC3\u3002\u201D</p><p>\u5979\u4EEC\u72B9\u8C6B\u4E0D\u51B3\u5730\u8FDB\u5165\u4E86\u623F\u95F4\uFF0C\u897F\u8499\u5A1C\u5047\u88C5\u4E3A\u623F\u95F4\u91CC\u6742\u4E71\u7684\u684C\u5B50\u800C\u5927\u60CA\u5C0F\u602A\u7684\u3002\u82F1\u7406\u548C\u7231\u5170\u5C3C\u65AF\u7AD9\u5728\u95E8\u7684\u4E24\u4FA7\uFF0C\u8FD9\u65E2\u662F\u5979\u4EEC\u4F5C\u4E3A\u4FDD\u9556\u7684\u6807\u5FD7\uFF0C\u4E5F\u662F\u4E00\u6B21\u5C0F\u5C0F\u7684\u6B66\u529B\u5C55\u793A\u3002</p><p>\u6700\u7EC8\u5979\u4EEC\u5750\u4E86\u4E0B\u6765\uFF0C\u6CA1\u4EC0\u522B\u7684\u53EF\u803D\u6401\u7684\u4E86\u3002</p><p>\u201C\u884C\uFF0C\u4F60\u4EEC\u4E0D\u7528\u544A\u8BC9\u6211\u662F\u4EC0\u4E48\u95EE\u9898\u201D \u897F\u8499\u5A1C\u62A2\u5728\u5927\u5BB6\u53D1\u8BDD\u4E4B\u524D\u8BF4 \u201C\u6211\u4E00\u6765\u5230\u8FD9\u513F\u5C31\u60F3\u5230\u4E86\u8FD9\u79CD\u60C5\u51B5\u3002\u201D</p><p>\u5979\u6DF1\u5438\u4E86\u4E00\u53E3\u6C14\uFF0C\u660E\u663E\u4E0D\u5B89\u5730\u6525\u7D27\u4E86\u624B\u3002\u5979\u4E0D\u518D\u76EF\u7740\u826F\u5B50\u3002</p><p>\u201C\u4ECE\u54EA\u513F\u8BF4\u8D77\u5462... \u6211\u5DE5\u4F5C\u7684\u5C0F\u7EC4\u5F88\u4E45\u4EE5\u524D\u5C31\u9884\u6D4B\u6211\u4EEC\u4F1A\u5728\u8FD9\u91CC\uFF0C\u7814\u7A76\u866B\u6D1E\u88C5\u7F6E\u3002\u636E\u9884\u6D4B\uFF0C\u6211\u9700\u8981\u6765\u8FD9\u91CC\uFF0C\u628A\u67D0\u4E9B\u7406\u8BBA\u5E26\u5230\u5B9E\u9A8C\u5BA4\uFF0C\u8FD9\u5BF9\u4E8E\u7406\u89E3\u866B\u6D1E\u7684\u4E0D\u7A33\u5B9A\u6027\uFF0C\u9884\u9632\u707E\u96BE\u662F\u5FC5\u8981\u7684\u3002\u6700\u8FD1\u5B9E\u9A8C\u7684\u6210\u529F\u90E8\u5206\u662F\u57FA\u4E8E\u6211\u5E26\u6765\u7684\u4E1C\u897F\u3002\u201D</p><p>\u8F7B\u6613\u6D88\u5316\u8FD9\u6837\u7684\u4FE1\u606F\u51E0\u4E4E\u662F\u4E0D\u53EF\u80FD\u7684\uFF0C\u5979\u4EEC\u5750\u5728\u90A3\u513F\u7728\u5DF4\u7728\u5DF4\u773C\u775B\uFF0C\u5076\u5C14\u8E66\u51FA\u70B9\u8BCD\u3002</p><p>\u201C\u56E2\u961F\uFF1F\u9884\u6D4B\uFF1F\u5565\uFF1F\u201D \u5E15\u7279\u4E3D\u590F\u6700\u7EC8\u628A\u8BDD\u8BB2\u4E86\u51FA\u6765\u3002</p><p>\u201C\u6211\u4E0D\u6E05\u695A\uFF0C\u201D \u897F\u8499\u5A1C\u8BF4\u3002\u201C\u6211\u670D\u52A1\u7684\u4E0A\u7EA7\u58F0\u79F0\u4ED6\u4EEC\u4E3A\u6267\u653F\u4F53\u670D\u52A1\uFF0C\u4F46\u662F\u6267\u653F\u4F53\u672C\u8EAB\u4E0D\u77E5\u9053\u8FD9\u4E2A\u90E8\u95E8\u7684\u5B58\u5728\u3002\u81F3\u4E8E\u4ED6\u4EEC\u600E\u4E48\u505A\u7684\u9884\u6D4B\uFF0C\u6211\u4E0D\u77E5\u9053\uFF0C\u4F46\u662F\u5927\u591A\u6570\u60C5\u51B5\u4E0B\u4ED6\u4EEC\u7684\u9884\u6D4B\u90FD\u662F\u5BF9\u7684\u3002\u6211\u77E5\u9053\u7684\u4E0D\u591A\uFF0C\u6BD4\u5982\u8BF4\u4ED6\u4EEC\u751A\u81F3\u6CA1\u544A\u8BC9\u6211\u4F60\u5728\u8FD9\u513F\u3002\u201D</p><p>\u201C\u4F60\u5230\u5E95\u662F\u8C01\uFF1F\u201D \u826F\u5B50\u8D28\u95EE.\u201C\u4E3A\u4EC0\u4E48\u627E\u4E0A\u6211\uFF1F\u201D</p><p>\u897F\u8499\u5A1C\u770B\u4E0A\u53BB\u660E\u663E\u4E0D\u662F\u5F88\u9AD8\u5174\u3002</p><p>\u201C\u6211\u77E5\u9053\u4F60\u5BF9\u6211\u7684\u770B\u6CD5\uFF0C\u201D \u5979\u8BF4\uFF0C\u4F46\u662F\u8FD9\u4E00\u5207\u5E76\u4E0D\u5168\u662F\u8C0E\u8A00\uFF0C\u6211\u662F\u4E2A\u9752\u5C11\u5E74\uFF0C\u6211\u6700\u8FD1\u624D\u7B7E\u7684\u7EA6\u3002\u6211\u771F\u7684\u662F\u4F60\u7684\u670B\u53CB\u3002\u6211\u88AB\u6D3E\u5230\u8FD9\u513F\u662F\u4E3A\u4E86...... \u770B\u62A4\u4F60\uFF0C\u6211\u88AB\u544A\u77E5\u4F60\u975E\u5E38\u5173\u952E\u3002\u9664\u6B64\u4E4B\u5916\u6211\u4EC0\u4E48\u90FD\u4E0D\u77E5\u9053\u3002</p><p>\u201C\u6211\u4E0D\u80FD\u63A5\u53D7\uFF0C\u201D \u963F\u5179\u745E\u5C14\u8BF4\uFF0C\u5E26\u7740\u4E00\u4E1D\u5A01\u80C1\u7684\u610F\u601D\u5411\u524D\u503E\uFF0C\u8FD9\u59FF\u52BF\u4E0E\u5979\u5A07\u5C0F\u7684\u8EAB\u6750\u6709\u70B9\u4E0D\u76F8\u79F0\u3002\u201C\u4F60\u5728\u9690\u7792\u3002\u5982\u679C\u4F60\u63A5\u7740\u8FD9\u4E48\u505A\u6211\u4EEC\u4F1A\u63A5\u7740\u95EE\u3002\u201D</p><p>\u201C\u4F60\u77E5\u9053\u6211\u89C9\u5F97\u53D7\u5230\u4E86\u591A\u5927\u7684\u80CC\u53DB\u561B\uFF1F\u201D \u826F\u5B50\u5FAE\u5FAE\u54AC\u7740\u7259\u8BF4\u3002\u201C\u6211\u4FE1\u4EFB\u4F60\u3002\u6211\u8BA4\u4E3A\u4F60\u662F\u6211\u7684\u670B\u53CB\u3002\u6211\u548C\u6211\u7684\u670B\u53CB\u4EEC\u63A5\u7EB3\u4E86\u4F60\uFF0C\u56E0\u4E3A\u6211\u4EEC\u60F3\u7167\u987E\u4E00\u4E2A\u770B\u4E0A\u53BB\u5F88\u5B64\u72EC\u7684\u4EA4\u6362\u751F\u3002\u6211\u672C\u4EE5\u4E3A\u6211\u4EEC\u5173\u7CFB\u5F88 \u201C\u597D\u201D\u3002\u201D</p><p>\u897F\u8499\u5A1C\u770B\u4E0A\u53BB\u88AB\u5413\u4E86\u4E00\u8DF3\uFF0C\u7136\u540E\u72B9\u8C6B\u4E0D\u51B3\u5730\u54AC\u7D27\u4E86\u5634\u5507\u3002\u201C\u6211\u4E0D\u77E5\u9053...... \u6211\u6CA1\u628A\u8FD9\u4E8B\u770B\u5F97\u90A3\u4E48\u91CD\uFF0C\u201D \u5979\u8BF4\u3002\u201C\u6BCF\u4E2A\u4EBA\u90FD\u6709\u81EA\u5DF1\u7684\u79D8\u5BC6\uFF0C\u6211\u5E76\u4E0D\u60F3\u4F24\u5BB3\u4F60\u3002\u6211\u53EA\u60F3\u5F53\u4F60\u7684\u670B\u53CB\u3002\u201D</p><p>\u201C\u4F60\u88AB\u547D\u4EE4\u6210\u4E3A\u6211\u7684\u670B\u53CB\u3002\u201D</p><p>\u201C\u6211\u4E0D\u662F\u8FD9\u4E2A\u610F\u601D\uFF01\u4E00\u5F00\u59CB\u662F\u8FD9\u6837\u7684\uFF0C\u76F4\u5230\u6211\u8BFB\u4E86\u4E00\u4E2A\u6587\u4EF6\uFF0C\u8FD9\u4EFD\u6587\u4EF6\u8BA9\u6211\u89C9\u5F97......\u201D \u5979\u7684\u58F0\u97F3\u6E10\u6E10\u8F7B\u4E86\u4E0B\u6765\uFF0C\u770B\u8D77\u6765\u5979\u5BF9\u63A5\u4E0B\u6765\u7684\u8BDD\u6709\u70B9\u72B9\u8C6B\u3002</p><p>\u201C\u4EC0\u4E48\uFF1F\u89C9\u5F97\u4EC0\u4E48\uFF1F\u201D \u826F\u5B50\u534A\u95EE\u534A\u8D23\u3002</p><p>\u201C\u4F60\uFF0C\u548C\u4F60\u7684\u670B\u53CB\u4EEC\uFF0C\u53EF\u4EE5\u6210\u4E3A\u6211\u771F\u6B63\u4E86\u89E3\u7684\u4EBA\u3002\u81EA\u4ECE\u6211\u8FD8\u662F\u4E2A\u5B69\u5B50\u8D77\u6211\u4ECE\u6CA1......\u201D</p><p>\u897F\u8499\u5A1C\u95ED\u4E0A\u773C\u775B\uFF0C\u7D27\u7F29\u53CC\u5507\uFF0C\u770B\u4E0A\u53BB\u5F88\u53D7\u4F24\uFF0C\u826F\u5B50\u4E0D\u7531\u5730\u611F\u5230\u4E00\u4E1D\u540C\u60C5\u3002\u65E0\u8BBA\u5982\u4F55\u5979\u4EEC\u90FD\u5F53\u8FC7\u670B\u53CB\u3002</p><p>\u201C\u6211\u4E0D\u80FD\u544A\u8BC9\u4F60\u4E00\u5207\uFF0C\u56E0\u4E3A\u6211\u4E0D\u77E5\u9053\u4E00\u5207\uFF0C\u597D\u5417\uFF1F\u6211\u662F\u4E2A\u5B9E\u9A8C\u5BA4\u91CC\u7684\u8BD5\u9A8C\u54C1\uFF0C\u5F53\u4E8B\u60C5\u8FD8\u6CA1\u6709\u8FD9\u4E48\u7CDF\u7CD5\u7684\u65F6\u5019\u53EA\u662F\u4E00\u9879\u65E9\u671F\u7684\u6218\u4E89\u8BA1\u5212\u3002\u6211\u88AB\u5F53\u505A\u4E00\u4E2A\u666E\u901A\u7684\u5B69\u5B50\u517B\u5927\uFF0C\u4F46\u6700\u7EC8\u8FD9\u53EA\u662F\u4E2A\u6D4B\u8BD5\uFF0C\u5F88\u591A\u65B9\u9762\u7684\u6D4B\u8BD5\u3002\u4E00\u4E2A\u89C2\u6D4B\u9B54\u6CD5\u5C11\u5973\u7684\u7B7E\u7EA6\u662F\u5426\u53EF\u4EE5\u88AB\u64CD\u63A7\uFF0C\u751A\u81F3\u4E8E\u4EE3\u66FF TCF \u690D\u5165\u88C5\u7F6E\u7684\u7814\u7A76\u3002\u201D</p><p>\u5979\u6447\u7740\u5934\uFF0C\u81EA\u8A00\u81EA\u8BED\u4E86\u597D\u4E00\u4F1A\u3002</p><p>\u201C\u4E4B\u540E\uFF0C\u6211\u4EEC\u5E76\u6CA1\u6709\u5EA6\u8FC7\u4E00\u4E2A\u6B63\u5E38\u7684\u7AE5\u5E74\uFF0C\u4E5F\u6CA1\u7528\u4EFB\u4F55\u670B\u53CB\u3002\u6211\u4EEC\u662F\u4EE5\u7279\u5DE5\u7684\u8EAB\u4EFD\u53C2\u4E0E\u8FD9\u4E2A\u8BA1\u5212\u7684\u3002\u4F5C\u4E3A\u8BA1\u5212\u7684\u5173\u952E\uFF0C\u4E00\u4E2A\u6267\u653F\u4F53\u5E94\u5BF9\u53EF\u6015\u7684\u60C5\u51B5\u65F6\u7684\u5BC6\u7EA6\u3002\u4F60\u5E94\u8BE5\u660E\u767D\u6211\u4ECE\u6CA1\u6709\u628A\u4FDD\u5B88\u79D8\u5BC6\u770B\u505A\u662F\u4EC0\u4E48\u4E0D\u5BFB\u5E38\u7684\u4E8B\u60C5\u3002\u201D</p><p>\u5979\u8868\u73B0\u51FA\u4E00\u79CD\u6124\u6012\u7684\u795E\u60C5\uFF0C\u6B63\u51C6\u5907\u8BF4\u4E9B\u4EC0\u4E48\u7684\u4E9A\u7EB1\u7F8E\u4E0D\u5F97\u4E0D\u53BB\u5B89\u629A\u5979</p><p>\u201C\u4F60\u8BF4\u4F60\u4F5C\u4E3A\u4E00\u4E2A\u5B69\u5B50\u88AB\u5229\u7528\uFF1F\u201D \u5E15\u7279\u4E3D\u590F\u5403\u60CA\u5730\u95EE\u9053\u3002</p><p>\u201C\u4E0D\u5B8C\u5168\u662F\uFF0C\u201D \u897F\u8499\u5A1C\u7684\u58F0\u97F3\u6709\u4E9B\u5206\u795E\u3002\u201C\u6211\u4EEC\u7684\u6210\u957F\u901F\u5EA6\u88AB\u52A0\u5FEB\u4E86\uFF0C\u6240\u4EE5\u6211\u4EEC\u66F4\u50CF\u662F\u5341\u51E0\u5C81\u65F6\u88AB\u62DB\u52DF\u7684\uFF0C\u8FD9\u6837\u8BF4\u51FA\u53BB\u4F1A\u5149\u5F69\u4E00\u70B9\u3002\u201D</p><p>\u201C\u4F60\u4E00\u76F4\u6253\u7B97\u544A\u8BC9\u6211\u5417\uFF1F\u201D \u826F\u5B50\u6447\u7740\u5934\u95EE\u9053\u3002\u5979\u610F\u8BC6\u5230\u5979\u6240\u5173\u5FC3\u7684\u4E1C\u897F\u5BF9\u5979\u521A\u521A\u542C\u5230\u7684\u5185\u5BB9\u800C\u8A00\u663E\u5F97\u591A\u4E48\u7684\u8352\u8C2C\u3002</p><p>\u201C\u6211\u672C\u6765\u60F3\u544A\u8BC9\u4F60\u7684\uFF0C\u201D \u897F\u8499\u5A1C\u4E0D\u5B89\u5730\u8BF4 \u201C\u4F60\u8FD8\u8BB0\u5F97\uFF0C\u6211\u4EEC\u5728\u5824\u5CB8\u4E0A\u88AB\u9B54\u517D\u88AD\u51FB\u7684\u65F6\u5019\u5417\uFF1F\u201D</p><p>\u826F\u5B50\u770B\u5411\u522B\u5904\u6C89\u601D\uFF0C\u5979\u9A6C\u4E0A\u60F3\u8D77\u4E86\u3002\u4F46\u786E\u5207\u7684\u7EC6\u8282\u2026\u2026 \u90A3\u4EF6\u4E8B\u4F3C\u4E4E\u5F88\u9065\u8FDC\uFF0C\u4EFF\u4F5B\u53D1\u751F\u5728\u53E6\u4E00\u4E2A\u4E16\u754C\uFF0C\u53EA\u662F\u5979\u751F\u6D3B\u6539\u53D8\u90A3\u4E00\u523B\u7684\u80CC\u666F\u3002</p><p>\u201C\u662F\u7684\u201D \u5979\u91CD\u65B0\u770B\u5411\u897F\u8499\u5A1C\u3002\u201C\u4F60\u60F3\u548C\u6211\u8C08\u70B9\u4EC0\u4E48\u3002\u201D</p><p>\u201C\u6211\u60F3\u544A\u8BC9\u4F60\u771F\u76F8\uFF01\u201D \u897F\u8499\u5A1C\u6124\u6012\u5730\u4E3E\u8D77\u4E86\u624B\u3002\u201C\u4E0D\u662F\u5168\u90E8\u544A\u8BC9\u4F60\uFF0C\u4F46\u662F\u6211\u5F53\u65F6\u786E\u5B9E\u60F3\u8FD9\u4E48\u505A\u3002\u7136\u540E\u8FD9\u4E00\u5207\u5C31\u53D1\u751F\u4E86\uFF0C\u4F60\u7B7E\u8BA2\u4E86\u5951\u7EA6\uFF0C\u5DF4\u9EBB\u7F8E \u2014\u2014 \u73B0\u4ECA\u6700\u9AD8\u7B49\u7EA7\u7684\u9B54\u6CD5\u5C11\u5973\u4E4B\u4E00\u544A\u8BC9\u6211\u8981\u5B88\u53E3\u5982\u74F6\uFF0C\u6211\u4E0D\u60F3\u6253\u6270\u4F60\uFF0C\u6240\u4EE5\u6211\u5C31\u7167\u505A\u4E86\u3002\u201D</p><p>\u201C\u7B49\u4E0B\uFF0C\u9EBB\u7F8E\u3055\u3093\u77E5\u9053\u8FD9\u4E00\u5207\uFF1F\u201D \u963F\u5179\u745E\u5C14\u95EE\u9053\u3002</p><p>\u201C\u4E0D\u662F\u7684\uFF01\u201D \u897F\u8499\u5A1C\u61CA\u6094\u5730\u8BF4\uFF0C\u201C\u4E0D \u2014\u2014 \u4E0D\u662F\u8FD9\u4EF6\u4E8B\u3002\u662F\u53E6\u4E00\u4EF6\u4E8B\uFF0C\u4F46\u6211\u8FD8\u662F\u660E\u767D\u4E86\u3002\u6211\u4E00\u76F4\u60F3\u7B49\u5230\u4F60\u51C6\u5907\u597D\u77E5\u9053\u4E00\u5207\u7684\u65F6\u5019\u518D\u544A\u8BC9\u4F60\u3002\u663E\u7136\u5C31\u662F\u4ECA\u5929\u3002\u201D \u5979\u6101\u7709\u82E6\u8138\u5730\u6447\u5934</p><p>\u201C\u5BF9\u4E0D\u8D77\uFF0C\u201D \u5979\u8BF4 \u201C\u8FD9\u5C31\u662F\u751F\u6D3B\u7684\u672C\u8D28\u5427\uFF0C\u5373\u4F7F\u4F60\u4E0D\u8BA4\u4E3A\u81EA\u5DF1\u505A\u9519\u4E86\u4EC0\u4E48\uFF0C\u4E5F\u8981\u9053\u6B49\u3002\u6211\u5F88\u62B1\u6B49\u6211\u4E00\u76F4\u4FDD\u5B88\u79D8\u5BC6\u3002\u201D</p><p>\u826F\u5B50\u770B\u7740\u773C\u524D\u8FD9\u4E2A\u5973\u5B69\uFF0C\u5979\u7684\u9ED1\u53D1\u5782\u5728\u773C\u524D\uFF0C\u770B\u4E0A\u53BB\u662F\u90A3\u4E48\u7684\u695A\u695A\u53EF\u601C\u3002\u5979\u6CA1\u6709\u8001\u7956\u5B97\u4EEC\u7684\u90A3\u79CD\u6C14\u8D28\uFF0C\u751A\u81F3\u6CA1\u6709\u4EC5\u4EC5\u662F\u5979\u8FD9\u4E2A\u5E74\u7EAA\u8BE5\u6709\u7684\u611F\u89C9\u3002\u4E0E\u5176\u8BF4\u6C14\u8D28\u4E0D\u5982\u8BF4\u7A1A\u6C14\u3002\u5979\u548C\u5979\u4EEC\u4E00\u6837\u968F\u6CE2\u9010\u6D41\u3002\u771F\u7684\u5F88\u96BE\u5BF9\u5979\u751F\u6C14\u3002</p><p>\u201C\u6211\u4E0D\u80FD\u8BF4\u6211\u539F\u8C05\u4F60\u4E86\uFF0C\u4F46\u662F\u6211\u63A5\u53D7\u4F60\u7684\u9053\u6B49\uFF0C\u201D \u5979\u8BF4\u3002\u201C\u6211\u4E0D\u77E5\u9053\u8FD9\u662F\u5426\u5408\u9002\u3002\u201D</p><p>\u897F\u8499\u5A1C\u70B9\u70B9\u5934\uFF0C\u8F7B\u8F7B\u5730\u8038\u80A9\u3002\u201C\u8FD9\u6BD4\u6211\u671F\u671B\u7684\u8981\u597D\u4E86\u201D \u5979\u8BF4\u3002</p><p>\u5E15\u7279\u4E3D\u590F\u5411\u524D\u63A2\u53BB\uFF0C\u5979\u7684\u5934\u53D1\u5F2F\u66F2\u5730\u5413\u4EBA\u3002\u201C\u5982\u679C\u4F60\u6CA1\u9A97\u6211\u4EEC\u7684\u8BDD\uFF0C\u90A3\u4E48\u6211\u731C\u4F60\u548C\u826F\u5B50\u7B7E\u7EA6\u4E4B\u524D\u88AD\u51FB\u5979\u7684\u9B54\u517D\u6CA1\u4EC0\u4E48\u5173\u7CFB\u5427\uFF1F\u201D</p><p>\u897F\u8499\u5A1C\u62AC\u8D77\u5934\u6765\uFF0C\u773C\u91CC\u6D41\u9732\u51FA\u60CA\u8BB6\u7684\u795E\u60C5\u3002</p><p>\u201C\u5F53\u7136\u4E0D\u662F\u3002\u4F60\u5728\u542C\u5417\uFF1F\u9B54\u517D\u6253\u65AD\u4E86\u6211\u7684\u8BA1\u5212\u3002\u4F60\u96BE\u9053\u5728\u8BF4\u8FD9\u4E0D\u662F\u4E00\u4E2A\u5DE7\u5408\uFF1F\u201D</p><p>\u5E15\u7279\u4E3D\u590F\u53F9\u4E86\u53E3\u6C14\u3002\u201C\u4E0D\uFF0C\u8FD9\u4E0D\u662F\u5DE7\u5408\u3002\u4E8B\u5B9E\u4E0A\uFF0C\u6211\u6765\u8FD9\u513F\u4E4B\u524D\u7684\u731C\u6D4B\u4E4B\u4E00\u5C31\u662F\u4F60\u4E0E\u8FD9\u4E8B\u6709\u5173\u3002\u201D</p><p>\u826F\u5B50\u76B1\u4E86\u76B1\u7709\u5934\u3002\u5979\u548C\u5979\u7684\u6218\u672F\u7535\u8111\u4E5F\u8003\u8651\u8FC7\u8FD9\u79CD\u53EF\u80FD\uFF0C\u4F46\u662F\u5979\u8FD8\u6CA1\u7EC6\u60F3\u3002\u7B80\u5355\u5730\u8BF4\uFF0C\u5979\u6000\u7591\u5982\u679C\u6CA1\u6709\u9EBB\u7F8E\u78B0\u5DE7\u8DEF\u8FC7\u7684\u8BDD\uFF0C\u897F\u8499\u5A1C\u8FD9\u4E48\u505A\u4F1A\u8BA9\u5979\u4EEC\u4E00\u8D77\u9677\u5165\u51E0\u4E4E\u8981\u88AB\u6740\u6B7B\u7684\u5371\u9669\u5883\u5730\u3002</p><p>\u201C\u4F60\u5BF9\u4E8E\u4E54\u5B89\u59AE \u74E6\u4F26\u4E01\u77E5\u9053\u4E9B\u4EC0\u4E48\uFF1F\u201D \u826F\u5B50\u4F4E\u5934\u770B\u7740\u897F\u8499\u5A1C\u95EE\u9053\u3002</p><p>\u201C\u74E6\u4F26\u4E01\u4E3B\u4EFB\uFF1F\u201D \u897F\u8499\u5A1C\u95EE\u3002\u201C\u4E0D\u662F...... \u7279\u522B\u591A\u3002\u6211\u5F88\u5C11\u542C\u8BF4\u5979\u3002\u5979\u662F\u6211\u5728 MSY \u7684\u66FE\u7ECF\u8054\u7EDC\u4EBA\uFF0C\u8FD9\u8BF4\u660E\u5979\u5BF9\u5F88\u591A\u654F\u611F\u7684\u77E5\u8BC6\u90FD\u5F88\u4FE1\u4EFB\u3002\u8FD9\u5F88\u5947\u602A\uFF0C\u4F46\u6211\u89C9\u5F97\u6211\u9047\u5230\u5979\u7684\u6B21\u6570\u6BD4\u6211\u5E94\u8BE5\u89C1\u5230\u7684\u8FD8\u591A\u3002\u201D</p><p>\u201C\u55EF\uFF0C\u8FD9\u4E5F\u662F\u6211\u7684\u89C2\u70B9\uFF0C\u201D \u826F\u5B50\u8BF4\uFF0C\u4F46\u5979\u8BDD\u91CC\u4ECD\u5E26\u7740\u70B9\u8BBD\u523A\u7684\u610F\u5473\u3002\u201C\u4F46\u662F\uFF0C\u5979\u662F\u4F60\u7684\u8054\u7EDC\u4EBA\uFF1F\u201D</p><p>\u897F\u8499\u5A1C\u95ED\u4E0A\u4E86\u773C\u775B\uFF0C\u9760\u5728\u81EA\u5DF1\u7684\u624B\u81C2\u4E0A\u3002\u201C\u4E3A\u4E86\u5206\u4EAB\u60C5\u62A5\uFF0C\u201D \u5979\u8BF4 \u201C\u8BF7\u5141\u8BB8\u6211\u89E3\u91CA\u4E00\u4E0B\u6211\u79BB\u5F00\u540E\u4E00\u76F4\u5728\u505A\u4EC0\u4E48\u3002\u90A3\u6837\u7684\u8BDD\u4E8B\u60C5\u90FD\u66F4\u597D\u8BF4\u7684\u901A\u4E86\uFF0C\u867D\u7136\u53EA\u662F\u4E00\u70B9\u70B9\u3002\u201D</p><p>\u5979\u53CD\u590D\u6E05\u4E86\u6E05\u55D3\u5B50\uFF0C\u7ED9\u4EBA\u4E00\u79CD\u660E\u663E\u5DF2\u7ECF\u6392\u7EC3\u8FC7\u5F88\u591A\u6B21\u7684\u611F\u89C9\u3002</p><p>\u201C\u4F60\u79BB\u5F00\u540E\u4E0D\u4E45\uFF0C\u6211\u5C31\u88AB\u53EC\u56DE\u4E86\uFF0C\u5B9E\u9645\u4E0A\u6211\u4EAB\u53D7\u4E86\u4E00\u4E9B\u7A7A\u95F2\u65F6\u95F4\u3002\u201D \u5979\u8BF4\uFF1A\u201D \u5F53\u7136\u53EA\u662F\u5C3D\u6211\u6240\u80FD\uFF0C\u5728\u4E8B\u60C5\u53D1\u751F\u540E\u3002\u5F53\u6211\u542C\u8BF4\u4F60\u5728\u90A3\u6B21\u7AE0\u9C7C\u88AD\u51FB\u4E2D\u7684\u906D\u9047\u65F6\uFF0C\u6211\u2026\u2026 \u521A\u4ECE\u4E00\u53EA\u5B75\u5316\u8005\u90A3\u91CC\u5F97\u5230\u4E86\u7B7E\u7EA6\u7684\u9080\u8BF7\uFF0C\u6211\u5C31\u63A5\u53D7\u4E86\u3002\u201D</p><p>\u826F\u5B50\u770B\u4E86\u773C\u4E9A\u7EB1\u7F8E\uFF0C\u53C8\u770B\u4E86\u770B\u5176\u4ED6\u4EBA\u3002\u8FD9\u53E5\u8BDD\u660E\u663E\u85CF\u7740\u70B9\u95EE\u9898\uFF0C\u4F46\u5979\u4E0D\u786E\u5B9A\u662F\u5426\u8BE5\u95EE\u3002</p><p>\u5979\u51B3\u5B9A\u8BA9\u897F\u8499\u5A1C\u7EE7\u7EED\u3002</p><p>\u201C\u5728\u90A3\u4E4B\u540E\uFF0C\u6211\u5F97\u5230\u4E86\u4E00\u4E2A\u65B0\u7684\u4EFB\u52A1\uFF0C\u201D \u5979\u8BF4\u201D \u6211\u672C\u6765\u60F3\u62BD\u7A7A\u770B\u4F60\u7684\u3002\u6211\u82B1\u4E86\u4E00\u6BB5\u65F6\u95F4\u624D\u627E\u5230\u4F60\u3002\u76F4\u5230\u4F60\u5728\u5DF4\u9ECE\u90A3\u6B21\u3002\u6211\u53C2\u52A0\u4E86\u4F60\u5F53\u65F6\u7684\u9B54\u517D\u88AD\u51FB\u3002\u4E0D\uFF0C\u6211\u4E5F\u6CA1\u6709\u7B56\u5212\u90A3\u6B21\u88AD\u51FB \u2014\u2014 \u53EA\u662F\u6709\u4EBA\u544A\u8BC9\u6211\u6211\u5F88\u53EF\u7591\u3002\u201D</p><p><strong>\u5979\u662F\u90A3\u65F6\u5019\u7684\u795E\u79D8\u9B54\u6CD5\u5C11\u5973\u4E4B\u4E00\uFF01</strong>\u514B\u83B1\u4E3D\u4E1D\u5728\u826F\u5B50\u60F3\u5230\u4E4B\u524D\u8BF4\uFF0C<strong>\u53D1\u5C04\u5149\u675F\u7684\u90A3\u4E2A\uFF0C\u4F46\u662F\u63A5\u7740......</strong></p><p><strong>\u514B\u83B1\u4E3D\u4E1D\uFF1F</strong>\u826F\u5B50\u5FF5\u8BDD\u9053\u3002</p><p><strong>\u7B49\u7B49\uFF0C\u6211\u5F97\u60F3\u60F3\uFF0C\u628A\u8FD9\u4E9B\u4FE1\u606F\u8054\u7CFB\u8D77\u6765...... \u8BA9\u5979\u63A5\u7740\u8BB2\u3002</strong></p><p>\u826F\u5B50\u663E\u7136\u4E0D\u7528\u8FD9\u4E48\u505A\uFF0C\u897F\u8499\u5A1C\u8BF4\u4E2A\u4E0D\u505C\u3002</p><p>\u201C\u6700\u7EC8\uFF0C\u6211\u6765\u8FD9\u91CC\u662F\u56E0\u4E3A\u6211\u88AB\u544A\u77E5\u8981\u505A\u4E00\u7B14\u4EA4\u6613\u3002\u6211\u4EEC\u4F1A\u7ED9\u8FD9\u91CC\u7684\u79D1\u5B66\u5BB6\u4E00\u4E9B\u4FE1\u606F\uFF0C\u4F5C\u4E3A\u4EA4\u6362\uFF0C\u6211\u5C06\u88AB\u5141\u8BB8\u5230\u8FD9\u91CC\u65C5\u884C\uFF0C\u52A0\u5165\u4F60\u4EEC\u7684\u884C\u5217\uFF0C\u826F\u5B50\u3002\u4E0D\u7BA1\u53D1\u751F\u4EC0\u4E48\uFF0C\u6211\u90FD\u60F3\u5728\u4F60\u8EAB\u8FB9\u3002\u6211\u4EEC\u4E0D\u662F\u6545\u610F\u4F24\u5BB3\u4F60 \u2014\u2014 \u6211\u7EDD\u5BF9\u4E0D\u4F1A\u4F24\u5BB3\u4F60\u7684\u3002\u5982\u679C\u771F\u662F\u8FD9\u6837\uFF0C\u6211\u4F1A\u5BF9\u4F60\u5FE0\u8BDA\u800C\u4E0D\u662F\u4ED6\u4EEC\u3002\u6211\u4ECD\u7136\u662F\u4F60\u7684\u670B\u53CB\u3002\u6211......\u201D</p><p>\u63A5\u7740\u5C31\u662F\u4E00\u9635\u6C89\u9ED8\uFF0C\u53EA\u6709\u514B\u83B1\u4E3D\u4E1D\u5728\u80CC\u5730\u5DE5\u4F5C\u7684\u5947\u602A\u611F\u89C9\u4E00\u76F4\u5728\u6253\u65AD\u826F\u5B50\u3002</p><p>\u201C\u6211\u8BB8\u613F\u80FD\u591F\u4FDD\u62A4\u5FD7\u7B51\u826F\u5B50\uFF0C\u8BA9\u5979\u7406\u89E3\u6211\uFF0C\u201D \u897F\u8499\u5A1C\u6700\u7EC8\u63A9\u9762\u8BF4\u51FA\u4E86\u8FD9\u53E5\u8BDD\u3002\u201C\u4F60\u5F53\u7136\u53EF\u4EE5\u76F8\u4FE1\u6211\u3002\u6211\u53EA\u662F\u4E0D\u77E5\u9053\u600E\u4E48\u8BA9\u4F60\u76F8\u4FE1\u3002\u201D</p><p>\u826F\u5B50\u53EF\u4EE5\u770B\u5230\u5979\u8138\u4E0A\u6EDA\u6EDA\u7684\u6CEA\u73E0\u3002</p><p>\u5979\u4E0D\u77E5\u9053\u600E\u4E48\u770B\u5F85\u8FD9\u4E00\u5207\u3002\u4E00\u4E2A\u613F\u671B\uFF1F\u4E3A\u4E86\u5979\u7684\u613F\u671B\uFF1F</p><p>\u5982\u679C\u662F\u771F\u7684\u8BDD\uFF0C\u8FD9\u633A\u4EE4\u4EBA\u611F\u52A8\u7684\uFF0C\u4F46\u662F\u826F\u5B50\u53EF\u4EE5\u611F\u53D7\u5230\u8EAB\u8FB9\u4E9A\u7EB1\u7F8E\u7684\u773C\u795E\uFF0C\u77E5\u9053\u4E8B\u60C5\u4E0D\u90A3\u4E48\u7B80\u5355\u4E86\u3002</p><p><strong>\u5979\u5BF9\u7B7E\u7EA6\u8FD9\u4EF6\u4E8B\u6CA1\u6709\u5F00\u73A9\u7B11\uFF0C\u63D2\u53E5\u8BDD\uFF0C</strong>\u514B\u83B1\u4E3D\u4E1D\u60F3\u3002<strong>\u6211\u4EEC\u521A\u521A\u6536\u5230\u4E86\u5168\u65B0\u7684\uFF0CMSY \u7684\u5185\u90E8\u81EA\u6BC1\u673A\u5BC6\u547D\u4EE4\uFF0C\u4EE5\u4E25\u683C\u4FDD\u5BC6\u6211\u4EEC\u5728\u8FD9\u91CC\u77E5\u9053\u7684\u4E00\u5207\uFF0C\u6211\u751A\u81F3\u4E0D\u77E5\u9053\u6709\u8FD9\u79CD\u4E1C\u897F\u3002</strong></p><p><strong>\u6240\u4EE5\u81F3\u5C11\u8FD9\u662F\u771F\u7684\uFF0C</strong>\u826F\u5B50\u60F3\u3002</p><p><strong>\u8001\u5B9E\u8BF4\uFF0C\u8FD9\u4E00\u5207\u8BA9\u6211\u89C9\u5F97\u592A\u5947\u602A\u4E86\uFF0C\u4E0D\u53EF\u80FD\u662F\u5047\u7684\u3002\u5B83\u89E3\u51B3\u4E86\u6211\u4E00\u76F4\u5728\u601D\u8003\u7684\u67D0\u4E9B\u77DB\u76FE\u70B9\u3002\u4F46\u662F\u5173\u4E8E\u8FD9\u4E2A\u613F\u671B\uFF0C\u8FD8\u6709\u4F60\u548C \u2014\u2014</strong></p><p><strong>\u662F\u7684\uFF0C\u6211\u77E5\u9053\u3002\u6211\u4E0D\u4F1A\u4E0D\u7BA1\u8FD9\u4E8B\u7684\u3002</strong></p><p>\u201C\u897F\u8499\u5A1C\uFF0C\u6211......\u201D \u5979\u8BF4\uFF0C\u201C\u5C31\u4E2A\u4EBA\u800C\u8A00\u3002\u4F60\u5F97\u77E5\u9053\u6211\u5DF2\u7ECF\u627E\u5230\u4F34\u4E86\u3002\u4F60\u5E94\u8BE5\u4E00\u5B9A\u77E5\u9053\u4E9A\u7EB1\u7F8E\u548C\u6211\u6B63\u5728\u4EA4\u5F80\u3002\u6211...... \u8FD9\u6837\u8BF4\u6709\u70B9\u4E0D\u597D\uFF0C\u4F46\u662F\u5982\u679C\u4F60\u89C9\u5F97\u4F60\u7231\u4E0A\u4E86\u6211\uFF0C\u5982\u679C\u4F60\u60F3\u63A5\u7740\u5F85\u5728\u6211\u8EAB\u8FB9\uFF0C\u6211\u5F97\u786E\u4FDD\u4F60\u4F1A\u5C0A\u91CD\u6211\u4EEC\uFF0C\u800C\u73B0\u5728\u6211\u4EEC\u4E0D\u80FD\u771F\u6B63\u76F8\u4FE1\u4F60\u3002\u76F4\u5230\u6211\u4E86\u89E3\u66F4\u591A\u7684\u771F\u76F8\u3002\u201D</p><p>\u897F\u8499\u5A1C\u7684\u773C\u775B\u62BD\u6410\u7740\uFF0C\u5979\u505A\u4E86\u4E00\u4E2A\u660E\u663E\u7684\u6DF1\u547C\u5438\u3002</p><p>\u201C\u6211\u77E5\u9053\uFF0C\u201D \u5979\u8BF4\uFF0C\u201C\u547D\u8FD0\u662F\u6B8B\u9177\u7684\u3002\u201D</p><p>\u4E9A\u7F8E\u838E\u770B\u7740\u897F\u8499\u5A1C\u826F\u4E45\uFF0C\u540C\u65F6\u77A5\u4E86\u773C\u826F\u5B50\u3002\u663E\u7136\u5979\u6709\u8BDD\u8981\u8BF4\u3002<br><strong>\u989D\uFF0C\u5192\u6627\u5730\u8BF4\uFF0C</strong>\u514B\u83B1\u4E3D\u4E1D\u8BF4\uFF0C<strong>\u8003\u8651\u5230\u6211\u4EEC\u53D1\u4E86\u8FD9\u4E48\u4E45\u5446\u4E86\uFF0C\u6211\u60F3\u5206\u4EAB\u4E00\u4E0B\u6211\u7684\u89C1\u89E3\u3002</strong></p><p><strong>\u4EC0\u4E48\u89C1\u89E3\uFF1F</strong>\u826F\u5B50\u95EE\u3002</p><p><strong>\u4E0D\u662F\u4EC0\u4E48\u7279\u522B\u60CA\u4EBA\u7684\u90A3\u79CD\uFF0C\u4F46\u662F\u6211\u628A\u5DF4\u9ECE\u90A3\u4EF6\u4E8B\u7684\u7EBF\u7D22\u6574\u7406\u4E86\u4E00\u4E0B\u3002\u53E6\u4E00\u4F4D\u795E\u79D8\u7684\u9B54\u6CD5\u5C11\u5973\u662F\u5FD7\u7B51\u6C99\u8036\u52A0\u5BF9\u5427\uFF0C\u5979\u4E00\u5B9A\u4F1A\u6CE8\u610F\u5230\u897F\u8499\u5A1C\u7684\u3002</strong></p><p><strong>\u5979\u5F53\u65F6\u548C\u6211\u4EEC\u4E00\u8D77\u4F2A\u88C5\u7740\u65C5\u884C\u6765\u7740\uFF0C</strong>\u826F\u5B50\u60F3\u3002</p><p><strong>\u786E\u5B9E\uFF0C\u4F46\u662F\u4F60\u5728\u72E9\u730E\u9B54\u517D\u7684\u65F6\u5019\u628A\u5979\u7559\u4E0B\u4E86\uFF0C\u76F4\u5230\u535A\u7269\u9986\u4F60\u624D\u518D\u6B21\u89C1\u5230\u5979\u3002\u5C31\u5728\u8FD9\u65F6\uFF0C\u9999\u83DC\u7684\u4EE3\u8868\u63A5\u53D7\u4E86 \u201C\u65B0\u7684\u6D88\u606F\u201D \u53D6\u6D88\u4E86\u8FD9\u6B21\u534F\u8BAE\u3002</strong></p><p><strong>\u8FD9\u4E5F\u6B63\u662F\u6211\u88AB\u544A\u77E5\u6211\u8111\u5B50\u91CC\u90A3\u4E2A\u4E1C\u897F\u7684\u65F6\u5019\uFF0C</strong>\u826F\u5B50\u60F3\uFF0C<strong>\u6574\u4EF6\u4E8B\u60C5\u53EA\u662F\u4E00\u4E2A\u4E3A\u4E86\u5F97\u5230\u6211\u5927\u8111\u626B\u63CF\u6570\u636E\u7684\u8BE1\u8BA1\u3002</strong></p><p><strong>\u662F\u7684\uFF0C</strong>\u514B\u83B1\u4E3D\u4E1D\u8BF4\uFF0C<strong>\u6240\u6709\u8FF9\u8C61\u90FD\u8868\u660E\u6C99\u8036\u52A0\uFF0C\u9999\u83DC\u548C\u897F\u8499\u5A1C\u90FD\u6216\u591A\u6216\u5C11\u5377\u5165\u4E86\u8FD9\u4EF6\u4E8B\u60C5\u3002\u5979\u4EEC\u90FD\u5728\u90A3\u513F\u89C1\u5230\u4E86\u897F\u8499\u5A1C\uFF0C\u53C8\u6216\u8005\u8BF4\u5979\u4EEC\u4E00\u5F00\u59CB\u5C31\u53C2\u4E0E\u4E86\u8FD9\u4E00\u5207\u3002</strong></p><p><strong>\u6211\u4E0D\u8BA4\u4E3A\u5979\u4EEC\u5728\u5DF4\u9ECE\u90A3\u4F1A\u5728\u5411\u6211\u6492\u8C0E\uFF0C\u9274\u4E8E\u5979\u4EEC\u6240\u77E5\u9053\u7684\u3002</strong></p><p><strong>\u6211\u4E5F\u8FD9\u4E48\u89C9\u5F97\uFF0C</strong>\u514B\u83B1\u4E3D\u4E1D\u8BF4\uFF0C<strong>\u4F60\u5F97\u53BB\u95EE\u95EE\u3002</strong></p><p>\u201C\u6211\u6709\u4E9B\u95EE\u9898\u8981\u95EE\uFF0C\u201D \u4E9A\u7F8E\u838E\u62A2\u5728\u826F\u5B50\u4E4B\u524D\u8BF4\u3002\u201C\u4F60\u63D0\u5230\u4F60\u5728\u6218\u4E89\u65E9\u671F\u4F5C\u4E3A\u4E00\u4E2A\u5B9E\u9A8C\u5BA4\u7684\u8BD5\u9A8C\u54C1\u3002\u8FD9\u662F\u5426\u610F\u5473\u7740\u4F60\u662F\u4E2A\uFF0C\u989D\uFF0C\u4EBA\u9020\u4EBA\uFF1F\u201D</p><p>\u201C\u53EF\u4EE5\u8FD9\u4E48\u8BF4\uFF0C\u201D \u897F\u8499\u5A1C\u6709\u70B9\u607C\u6012\u5730\u8BF4\u3002\u201C\u81F3\u5C11\u4F60\u8FD9\u4E48\u60F3\uFF0C\u4F46\u6211\u4ECE\u4E0D\u60F3\u627F\u8BA4\u3002\u201D</p><p>\u201C\u90A3\u4E48\u4F60\u5BF9\u4E8E\u6211\u8111\u5B50\u4E86\u7684\u90A3\u4E9B\u82AF\u7247\u6709\u4EC0\u4E48\u5934\u7EEA\u561B\uFF1F\u201D \u826F\u5B50\u5411\u524D\u503E\u60F3\u63D2\u5165\u5979\u4EEC\u7684\u8BDD\u9898\u3002\u201C\u6211\u731C\u4F60\u77E5\u9053\u70B9\u4EC0\u4E48\u3002\u201D</p><p>\u897F\u8499\u5A1C\u770B\u4E0A\u53BB\u601D\u8003\u4E86\u597D\u4E00\u4F1A\u8FD9\u4E2A\u95EE\u9898\u3002</p><p>\u201C\u6211\u542C\u8BF4\u8FC7\uFF0C\u201D \u897F\u8499\u5A1C\u6447\u7740\u5934\uFF0C\u201C\u4F46\u6211\u53EA\u77E5\u9053\u5979\u4EEC\u544A\u8BC9\u6211\u7684\u90A3\u90E8\u5206\uFF0C\u6211\u89C9\u5F97\u548C\u4F60\u6240\u77E5\u9053\u7684\u5DEE\u4E0D\u4E86\u591A\u5C11\u3002\u4E00\u4E9B\u5E26\u6709\u5F02\u5E38\u57FA\u56E0\u7F16\u7801\u7684\u5927\u8111\u533A\u57DF\uFF0C\u5E76\u4E14\u6CA1\u6709\u5176\u4F59\u7684\u690D\u5165\u7269\uFF1F\u201D</p><p>\u201C\u5DEE\u4E0D\u591A\u201D \u5E15\u7279\u4E3D\u590F\u8BC1\u5B9E\u3002</p><p>\u201C\u76EE\u524D\u7684\u7406\u8BBA\u662F\uFF0C\u5DF4\u9ECE\u7684\u9B54\u517D\u88AD\u51FB\u662F\u4E3A\u4E86\u963B\u6B62\u826F\u5B50\u3061\u3083\u3093\u83B7\u5F97\u8BE6\u7EC6\u7684\u8111\u90E8\u626B\u63CF\u6570\u636E\u3002\u201D \u897F\u8499\u5A1C\u8BF4 \u201C\u4E0D\u662F\u5F88\u6709\u6548\uFF0C\u8BF7\u6CE8\u610F\uFF0C\u591A\u4E8F\u4E86\u6211\u3002\u201D</p><p>\u5979\u70B9\u70B9\u5934\uFF0C\u5FAE\u5FAE\u4E00\u7B11\uFF0C\u5BF9\u81EA\u5DF1\u611F\u5230\u6EE1\u610F\uFF0C\u81F3\u5C11\u5728\u8FD9\u4EF6\u4E8B\u4E0A\u662F\u8FD9\u6837\u3002</p><p>\u201C\u4F46\u6211\u4E0D\u77E5\u9053\u53D1\u751F\u4E86\u4EC0\u4E48\u4E8B\uFF0C\u201D \u5979\u4E13\u6CE8\u5730\u81EA\u8BF4\u81EA\u8BDD\uFF0C\u201C\u8FD9\u4E5F\u56F0\u6270\u7740\u6211\u3002\u201D</p><p>\u201C\u4F60\u662F\u4ECE\u5FD7\u7B51\u6C99\u8036\u52A0\u90A3\u513F\u4E86\u89E3\u5230\u7684\u8FD9\u4E9B\uFF0C\u5BF9\u5427\uFF1F\u201D \u826F\u5B50\u8BF4\u3002\u201C\u8FD8\u662F\u522B\u4EBA\u544A\u8BC9\u5979\u7684\uFF1F\u201D</p><p>\u897F\u8499\u5A1C\u60CA\u8BB6\u5730\u770B\u4E86\u4E00\u4F1A\uFF0C\u5C3D\u7BA1\u5979\u4E0D\u60F3\u8868\u9732\u51FA\u8FD9\u79CD\u795E\u60C5\u3002</p><p>\u201C\u4E0D\u662F\u5979\u76F4\u63A5\u544A\u8BC9\u6211\u7684\uFF0C\u867D\u7136\u5979\u5728\u5DF4\u9ECE\u548C\u6211\u8054\u7CFB\u8FC7\uFF0C\u4F46\u4F60\u8BF4\u5F97\u5BF9\u3002\u6211\u662F\u4ECE\u6211\u5DE5\u4F5C\u7684\u90E8\u95E8\u90A3\u91CC\u77E5\u9053\u7684\u3002\u201D</p><p>\u201C\u5982\u679C\u662F\u8FD9\u6837\u7684\u8BDD\uFF0C\u5982\u679C\u4F60\u6216\u4F60\u7684\u7EC4\u7EC7\u6CA1\u6709\u5728\u826F\u5B50\u7684\u5927\u8111\u6539\u9020\u91CC\u63D2\u4E00\u811A\uFF0C\u4F60\u53C8\u662F\u600E\u4E48\u77E5\u9053\u8FD9\u4EF6\u4E8B\u7684\uFF1F\u201D \u963F\u5179\u745E\u5C14\u5C16\u9510\u5730\u95EE\u9053\uFF0C\u5979\u7684\u7FC5\u8180\u5FAE\u5FAE\u9876\u8D77\u886C\u886B\u540E\u80CC\u3002\u201C\u5982\u679C\u4F60\u4E0D\u4ECB\u610F\u6211\u8FD9\u4E48\u8BF4\u7684\u8BDD\uFF0C\u8FD9\u5C31\u6709\u70B9\u53EF\u7591\u4E86\u3002\u201D</p><p>\u897F\u8499\u5A1C\u7684\u8868\u60C5\u6709\u70B9\u75DB\u82E6\u3002</p><p>\u201C\u8FD9\u542C\u8D77\u6765\u6709\u70B9\u95EA\u70C1\u5176\u8BCD\uFF0C\u4F46\u6211\u771F\u7684\u4E0D\u77E5\u9053\u3002\u6709\u4EBA\u544A\u8BC9\u6211\uFF0C\u6211\u4EEC\u6709\u5F88\u591A\u652F\u6301\u8005\u4E3A\u6211\u4EEC\u63D0\u4F9B\u4FE1\u606F\u3002\u4F46\u6211\u4E5F\u4ECE\u672A\u8BD5\u8FC7\u53BB\u4E86\u89E3\u3002\u8FD9\u662F\u4E00\u4EF6\u4F60\u77E5\u9053\u7684\u8D8A\u5C11\uFF0C\u4F60\u5C31\u8D8A\u96BE\u544A\u8BC9\u522B\u4EBA\u4F60\u73B0\u5728\u7684\u5904\u5883\u6709\u591A\u5C34\u5C2C\u7684\u4E8B\u60C5\u3002\u201D</p><p>\u201C\u4F60\u5C31\u4FE1\u4E86\uFF1F\u201D \u963F\u5179\u745E\u5C14\u8BDD\u8BED\u4E2D\u5E26\u7740\u4E00\u4E1D\u5A01\u80C1\u3002</p><p>\u201C\u5979\u4EEC\u628A\u6211\u517B\u5927\uFF0C\u81F3\u5C11\u5979\u4EEC\u7684\u5E72\u5458\u8FD9\u4E48\u505A\u4E86\uFF0C\u5982\u679C\u4E0D\u8FD9\u6837\u6211\u7684\u7236\u6BCD\u5F88\u5FEB\u5C31\u4F1A\u56E0\u4E3A\u8FD9\u4E00\u5207\u800C\u88AB\u5BA1\u95EE\uFF0C\u4E5F\u8BB8\u6211\u662F\u4E2A\u50BB\u74DC\uFF0C\u4F46\u6211\u4ECE\u6CA1\u6740\u8FC7\u4EBA\uFF0C\u6211\u4E5F\u6CA1\u505A\u8FC7\u6211\u8BA4\u4E3A\u9519\u7684\u4E8B\u60C5\u3002\u6211\u53EA\u80FD\u8FD9\u4E48\u8BF4\u3002\u5982\u679C\u6211\u8BF4\u6211\u4ECE\u6765\u6CA1\u6709\u6000\u7591\u8FC7\u5468\u56F4\u7684\u4E00\u5207\uFF0C\u90A3\u6211\u80AF\u5B9A\u662F\u5728\u6492\u8C0E\u3002\u201D</p><p>\u6F2B\u957F\u7684\u5BC2\u9759\uFF0C\u5728\u8FD9\u671F\u95F4\u963F\u5179\u745E\u5C14\u76EF\u7740\u897F\u8499\u5A1C\uFF0C\u597D\u50CF\u8981\u7528\u5979\u7684\u773C\u775B\u5728\u5979\u7684\u5934\u9AA8\u4E0A\u94BB\u4E00\u4E2A\u6D1E\u3002\u4E0D\u9700\u8981\u4EC0\u4E48\u7279\u522B\u7684\u6D1E\u5BDF\u529B\u5C31\u80FD\u77E5\u9053\u963F\u5179\u745E\u5C14\u662F\u60F3\u8BFB\u61C2\u897F\u8499\u5A1C\u7684\u5FC3\u601D\uFF0C\u6216\u8005\u662F\u60F3\u548C\u5979\u8C08\u8C08\u3002</p><p>\u5E15\u7279\u4E3D\u590F\u4FEF\u4E0B\u8EAB\u6293\u4E86\u5757\u897F\u8499\u5A1C\u4E4B\u524D\u6446\u51FA\u6765\u7684\u4E00\u5757\u70B9\u5FC3\uFF0C\u81F3\u4ECA\u6CA1\u4EBA\u52A8\u8FC7\u3002\u5979\u7D27\u5F20\u5730\u54AC\u4E86\u4E00\u53E3\uFF0C\u826F\u5B50\u89C9\u5F97\u6700\u597D\u8FD8\u662F\u522B\u5403\u7684\u597D\u3002</p><p>\u201C\u4F60\u7684\u80FD\u529B\u662F\u4EC0\u4E48\uFF1F\u201D \u963F\u5179\u745E\u5C14\u95EE\u3002\u201C\u4F20\u9001\uFF1F\u201D</p><p>\u897F\u8499\u5A1C\u6447\u6447\u5934\u3002</p><p>\u201C\u4E0D\uFF0C\u6211\u521A\u7B7E\u5B8C\u5951\u7EA6\u5C31\u63A5\u53D7\u4E86\u8BAD\u7EC3\u3002\u6211\u7684\u5B9E\u9645\u6280\u80FD\u662F\u80FD\u529B\u5F3A\u5316\u3002\u4E0D\u7BA1\u4F60\u6709\u4EC0\u4E48\u80FD\u529B\uFF0C\u6211\u90FD\u4F1A\u8BA9\u5B83\u66F4\u5F3A\u5927\u3002\u7EAF\u7CB9\u7684\u8F85\u52A9\u3002\u201D</p><p>\u201C\u90A3\u4E48\u5C31\u662F\u7075\u9B42\u7CFB\u7684\u80FD\u529B\u4E86\uFF0C\u201D \u963F\u5179\u745E\u5C14\u4EE5\u4E00\u79CD\u51E0\u4E4E\u5192\u72AF\u7684\u8BED\u6C14\u8BF4\u3002\u201C\u548C\u4F20\u9001\u5F88\u50CF\u3002\u201D</p><p>\u201C\u55EF\uFF0C\u633A\u65B9\u4FBF\u7684\u80FD\u529B\uFF0C\u201D \u897F\u8499\u5A1C\u8038\u8038\u80A9\u8BF4.\u201C\u5982\u679C\u4F60\u4E0D\u4ECB\u610F\u7684\u8BDD\uFF0C\u6211\u771F\u4E0D\u60F3\u628A\u6240\u6709\u7684\u4E8B\u5B9E\u90FD\u5C55\u73B0\u7ED9\u4F60\u4EEC\u770B\u3002\u53EF\u4EE5\u5417\uFF1F\u201D</p><p>\u201C\u5982\u679C\u4F60\u771F\u7684\u50CF\u4F60\u6240\u8BF4\u7684\u90A3\u6837\u8BDA\u5B9E\uFF0C\u6211\u5E0C\u671B\u6709\u4E00\u5929\u4F60\u80FD\u544A\u8BC9\u6211\u4EEC\u6240\u6709\u7684\u771F\u76F8\uFF0C\u201D \u963F\u5179\u745E\u5C14\u8BF4\u3002\u201C\u4F60\u5E94\u8BE5\u660E\u767D\uFF0C\u9664\u975E\u4F60\u8FD9\u513F\u505A\uFF0C\u5426\u5219\u4F60\u662F\u4E0D\u4F1A\u5F97\u5230\u6211\u4EEC\u7684\u4FE1\u4EFB\u7684\u3002\u201D</p><p>\u963F\u5179\u745E\u5C14\u7ED9\u4E86\u826F\u5B50\u4E00\u4E2A\u773C\u795E\u3002</p><p><strong>\u6211\u4EEC\u8981\u79C1\u5E95\u4E0B\u8C08\u8C08\uFF0C\u4E00\u5C0F\u4F1A\u3002</strong></p><p>\u826F\u5B50\u4E0D\u5047\u601D\u7D22\u5730\u540C\u610F\u4E86\u3002</p><p>\u201C\u6211\u6CA1\u4EC0\u4E48\u522B\u7684\u53EF\u8BF4\u7684\u4E86\uFF0C\u201D \u897F\u8499\u5A1C\u6446\u7740\u75DB\u82E6\u7684\u8868\u60C5\u3002\u201C\u6211\u60F3\u4E0D\u5230\u8FD8\u80FD\u8BF4\u70B9\u4EC0\u4E48\u6765\u8BA9\u4F60\u4EEC\u76F8\u4FE1\u6211\u3002\u53EA\u80FD\u9760\u884C\u52A8\u624D\u884C\u3002\u201D</p><p>\u597D\u5427\uFF0C\u5982\u679C\u5979\u5728\u620F\u800D\u6211\u4EEC\uFF0C\u90A3\u5979\u8FD8\u633A\u4E0A\u9053\u7684\uFF0C\u963F\u5179\u745E\u5C14\u60F3\uFF0C\u5979\u5C0F\u5FC3\u7FFC\u7FFC\u5730\u907F\u514D\u8868\u73B0\u51FA\u4EFB\u4F55\u8868\u60C5\u4E0A\u7684\u53D8\u5316\u3002\u5979\u6709\u7740\u60CA\u4EBA\u7684\u826F\u597D\u7684\u5FC3\u7075\u611F\u5E94\u9632\u5FA1\u80FD\u529B\uFF0C\u4F46\u6211\u4ECD\u89C9\u5F97\u8FD9\u80CC\u540E\u7684\u9690\u60C5\u6BD4\u5979\u5E0C\u671B\u6211\u770B\u5230\u7684\u591A\u5F97\u591A\u3002\u4E0D\u8FC7\uFF0C\u6211\u8FD8\u662F\u6CA1\u6709\u611F\u5E94\u5230\u5979\u5728\u6492\u8C0E\u3002\u53EA\u662F\u5979\u771F\u7684\u5FD8\u4E86\u4E00\u4E9B\u4E8B\u60C5\u3002</p><p><strong>\u8FD9\u4E00\u70B9\u4E5F\u4E0D\u5947\u602A\uFF0C</strong>\u826F\u5B50\u5FEB\u901F\u6D4F\u89C8\u7740\u963F\u5179\u745E\u5C14\u4F20\u8F93\u7684\u4F8B\u8BC1\u3002</p><p><strong>\u662F\u7684\uFF0C</strong>\u963F\u5179\u745E\u5C14\u5FF5\u8BDD\u9053\u3002</p><p><strong>\u6211\u5728\u8FD9\u513F\u4E5F\u5B89\u88C5\u4E86\u4E00\u4E2A\u65E0\u4EBA\u673A\uFF0C</strong>\u5E15\u7279\u91CC\u590F\u8BF4\uFF0C<strong>\u5982\u679C\u8FD0\u6C14\u597D\u7684\u8BDD\u5979\u4E0D\u4F1A\u53D1\u73B0\u8FD9\u4E2A\u3002\u6211\u9700\u8981\u66F4\u53EF\u9760\u7684\u57FA\u56E0\u6837\u672C\u3002</strong></p><p><strong>\u806A\u660E\u7684\u51B3\u5B9A\uFF0C</strong>\u963F\u5179\u745E\u5C14\u8BF4\u3002</p><p>\u5E15\u7279\u91CC\u590F\u7981\u4E0D\u4F4F\u6D41\u9732\u51FA\u4E00\u4E1D\u6109\u60A6\uFF0C\u56E0\u4E3A\u5979\u8D62\u5F97\u4E86\u8FD9\u4F4D\u8001\u592A\u592A\u7684\u8D5E\u626C\u3002\u8C22\u5929\u8C22\u5730\uFF0C\u897F\u8499\u5A1C\u6CA1\u6709\u770B\u7740\u5979\u3002</p><p>\u201C\u4F60\u4EEC\u5C31\u8BBE\u8EAB\u5904\u5730\u5730\u60F3\u4E00\u4E0B\u561B\u3002\u201D \u897F\u8499\u5A1C\u8BF4 \u201C\u4ECE\u751F\u4E0B\u6765\u6211\u7684\u751F\u6D3B\u51E0\u4E4E\u5C31\u5145\u65A5\u7740\u5404\u79CD\u5404\u6837\u7684\u4EFB\u52A1\u3002\u6211\u77E5\u9053\u8FD9\u4E0D\u5408\u7406\uFF0C\u4F46\u5BF9\u6211\u6765\u8BF4\uFF0C\u826F\u5B50\u624D\u662F\u6700\u91CD\u8981\u7684\uFF0C\u597D\u5417\uFF1F\u6211\u6700\u597D\u8FD9\u4E48\u627F\u8BA4\uFF0C\u4E0D\u8BBA\u8FD9\u8981\u6211\u4ED8\u51FA\u4EC0\u4E48\u4EE3\u4EF7\u3002\u8BF4\u771F\u7684\uFF0C\u6211\u4E5F\u6CA1\u6709\u4EC0\u4E48\u522B\u7684\u53EF\u4F9D\u8D56\u7684\u4E86\u3002\u201D</p><p>\u826F\u5B50\u89C9\u5F97\u8FD9\u51FA\u4E4E\u610F\u6599\u5730\u89E6\u52A8\u4E86\u5979\u5185\u5FC3\u7684\u4E00\u6839\u5F26\uFF0C\u4EE5\u81F3\u4E8E\u5979\u522B\u8FC7\u8138\u53BB\u3002\u5979\u77E5\u9053\u5728\u4E00\u4E2A\u7EB7\u4E71 \u7684\u4E16\u754C\u91CC\u53EA\u6709\u4E00\u4EBA\u53EF\u4EE5\u4F9D\u9760\u662F\u4EC0\u4E48\u611F\u89C9\u3002</p><p><strong>\u73B0\u5728\u600E\u4E48\u529E\uFF1F</strong>\u963F\u5179\u745E\u5C14\u5FF5\u8BDD\u9053\u3002</p><p><strong>\u6211\u4E0D\u77E5\u9053\uFF0C</strong>\u826F\u5B50\u8BF4\uFF0C<strong>\u4F46\u662F\u6211\u4E0D\u8BA4\u4E3A\u5979\u5BF9\u6211\u4EEC\u662F\u4E2A\u5A01\u80C1\u3002\u6211\u60F3\u4F60\u5982\u679C\u4E0D\u8BD5\u7740\u7AA5\u63A2\u5979\u7684\u5185\u5FC3\u7684\u8BDD\u6211\u4EEC\u4E5F\u6CA1\u522B\u7684\u53EF\u505A\u7684\u4E86\uFF0C\u6211\u4E5F\u4E0D\u8BA4\u4E3A\u5F17\u62C9\u5FB7\u4F1A\u5BB9\u5FCD\uFF0C\u6216\u8005\u6211\u4F1A\u5E0C\u671B\u4F60\u8FD9\u4E48\u505A\u3002\u65E0\u8BBA\u5982\u4F55\uFF0C\u5979\u2026\u2026\u66FE\u7ECF\u662F\u6211\u7684\u670B\u53CB\u3002\u5DEE\u4E0D\u591A\u5F97\u4E86\u3002\u8BA9\u6211\u4EEC\u770B\u770B\u5979\u4E4B\u540E\u4F1A\u4F5C\u4F55\u9009\u62E9\u3002</strong></p><p><strong>\u8BF4\u7684\u6709\u9053\u7406\uFF0C</strong>\u963F\u5179\u745E\u5C14\u8BF4\uFF0C<strong>\u4F46\u662F\u6211\u5BF9\u6211\u4EEC\u6240\u4E0D\u77E5\u9053\u7684\u4E1C\u897F\u6709\u70B9\u62C5\u5FC3\u3002</strong></p><p>\u201C\u6211\u4EEC\u7B49\u4F1A\u518D\u804A\uFF0C\u201D \u826F\u5B50\u76EF\u7740\u897F\u8499\u5A1C\u7684\u773C\u775B\u8BF4\u3002\u201C\u6211\u4E0D\u77E5\u9053\u4F60\u7684\u76EE\u7684\u662F\u4EC0\u4E48\u4F46\u662F\u2026\u2026\u6211\u4E0D\u8BA4\u4E3A\u4F60\u4F1A\u5750\u89C6\u4E0D\u7BA1\u3002\u8BA9\u6211\u4EEC\u770B\u770B\u4F1A\u53D1\u751F\u4EC0\u4E48\u3002\u201D</p><p>\u201C\u6211\u77E5\u9053\u6211\u4F1A\u6709\u673A\u4F1A\u7684\uFF0C\u201D \u897F\u8499\u5A1C\u76EE\u4E0D\u8F6C\u775B\uFF0C\u201C\u6211\u88AB\u544A\u77E5\u4F60\u9A6C\u4E0A\u4F1A\u88AB\u6D3E\u5230\u4E00\u4E2A\u5730\u65B9\uFF0C\u6211\u4E5F\u4F1A\u4E00\u8D77\u53BB\u3002\u201D</p><p>\u826F\u5B50\u89C9\u5F97\u81EA\u5DF1\u7684\u8868\u60C5\u6709\u70B9\u90C1\u95F7\uFF0C\u4F46\u5979\u514B\u5236\u4F4F\u4E86\u3002</p><p>\u201C\u5979\u4EEC\u771F\u7684\u80FD\u9884\u77E5\u672A\u6765\uFF1F\u201D \u5979\u95EE\uFF0C\u5373\u4F7F\u963F\u5179\u745E\u5C14\u53D1\u51FA\u4E86\u607C\u6012\u7684\u58F0\u97F3\u3002</p><p>\u201C\u6211\u4E0D\u6E05\u695A\u3002\u5979\u4EEC\u4E4B\u524D\u4E5F\u5931\u8BEF\u8FC7\uFF0C\u201D \u897F\u8499\u5A1C\u8BF4\u3002\u201C\u6216\u8005\u5979\u4EEC\u5728\u8BF4\u8C0E\u3002\u4F46\u662F\u6211\u5E0C\u671B\u4E0D\u662F\u3002\u5728\u4F60\u53D1\u95EE\u4E4B\u524D\uFF0C\u6211\u5F97\u91CD\u7533\u6211\u5BF9\u9884\u6D4B\u7684\u65B9\u6CD5\u4E00\u65E0\u6240\u77E5\u3002\u201D</p><p>\u5E15\u7279\u91CC\u590F\u9760\u5728\u6905\u5B50\u4E0A\u8BF4\uFF1A\u201C\u6211\u4EE5\u4E3A\u9884\u8A00\u80FD\u529B\u5E94\u8BE5\u5DF2\u7ECF\u7EDD\u8FF9\u4E86\u3002\u201D</p><p>\u201C\u90A3\u50CF\u6211\u6240\u5728\u7684\u6D41\u6D6A\u6B96\u6C11\u5730\u4E5F\u672C\u4E0D\u8BE5\u5B58\u5728\uFF0C\u201D \u963F\u5179\u745E\u5C14\u8BF4\u3002</p><p>\u826F\u5B50\u6CAE\u4E27\u5730\u6447\u6447\u5934\u3002\u201C\u6211\u76F8\u4FE1\u4F60\uFF0C\u201D \u5979\u8BF4\uFF0C\u201C\u4F46\u662F\u6211\u4E0D\u786E\u5B9A\u6211\u559C\u6B22\u88AB\u9884\u544A\u81EA\u5DF1\u7684\u672A\u6765\u7684\u611F\u89C9\u201D</p><p>\u201C\u6211\u4E5F\u4E00\u76F4\u8FD9\u4E48\u60F3\uFF0C\u201D \u897F\u8499\u5A1C\u5FAE\u7B11\u7740\u8BF4\u3002\u201C\u90A3\u4E48\u8FD9\u662F\u6211\u4EEC\u7684\u5171\u540C\u70B9\u4E86\u3002\u201D</p><p>\u201C\u662F\u662F\u662F\u3002\u201D</p><p>\u8BF4\u5B8C\uFF0C\u826F\u5B50\u7AD9\u8D77\u6765\u8F6C\u8FC7\u8EAB\uFF0C\u8D70\u51FA\u623F\u95F4\uFF0C\u611F\u89C9\u5230\u897F\u8499\u5A1C\u7684\u76EE\u5149\u843D\u5728\u5979\u7684\u80CC\u4E0A\u3002\u5979\u770B\u4E0A\u53BB\u5782\u5934\u4E27\u6C14\uFF0C\u5374\u53C8\u6EE1\u6000\u5E0C\u671B\uFF0C\u6216\u8005\u4E24\u8005\u90FD\u6709\u3002</p><hr><p>\u201C\u8FD9\u662F\u8D85\u8FDC\u7A0B\u91CD\u529B\u626B\u63CF\u4EEA\u7684\u539F\u578B\u201D \u5F17\u62C9\u5FB7\u89E3\u91CA\u8BF4\uFF0C\u6307\u7740\u88C5\u5728\u5899\u4E0A\u7684\u4E00\u4E2A\u5DE8\u5927\u7684\u88C5\u7F6E\u3002\u770B\u4E0A\u53BB\u5C31\u50CF\u4E00\u4E2A\u7531\u5145\u6EE1\u4E86\u5947\u602A\u7684\u8FB9\u89D2\u7684\u65B0\u96F6\u4EF6\u548C\u660E\u663E\u5373\u5174\u7684\u9644\u4EF6\u6742\u4E71\u62FC\u63A5\u8D77\u6765\u7684\u4E1C\u897F\uFF0C\u663E\u7136\u53EB\u5B83\u539F\u578B\u5F88\u5408\u9002\u3002</p><p>\u826F\u5B50\u548C\u4E9A\u7F8E\u838E\u793C\u8C8C\u5730\u70B9\u70B9\u5934\uFF0C\u9676\u4E3B\u4EFB\u6B63\u5BF9\u7740\u4E00\u5F20\u53EA\u6709\u4ED6\u80FD\u770B\u5230\u7684\u56FE\u8868\u76B1\u7709\u5934\u3002</p><p>\u201C\u8FD9\u662F\u6211\u81EA\u5DF1\u7684\u4F5C\u54C1\uFF0C\u7075\u611F\u6765\u81EA\u4E8E\u6211\u4EEC\u4E4B\u524D\u5B9E\u9A8C\u7684\u4E00\u4E9B\u6210\u679C\u3002\u8FD9\u662F\u6211\u4E00\u76F4\u60F3\u505A\u7684\u4E8B\u60C5\uFF0C\u4F46\u6211\u4ECE\u6765\u6CA1\u6709\u8DB3\u591F\u591A\u7684\u8D44\u6E90\u3002\u201D</p><p>\u4ED6\u68C0\u67E5\u4E86\u4E00\u904D\u786E\u4FDD\u5979\u4EEC\u90FD\u80FD\u542C\u61C2\u3002\u5979\u4EEC\u518D\u4E00\u6B21\u793C\u8C8C\u5730\u70B9\u70B9\u5934\u3002</p><p>\u201C\u6211\u4F1A\u5C3D\u91CF\u7B80\u660E\u627C\u8981\u7684\uFF0C\u201D \u4ED6\u8BF4\u3002\u201C\u6211\u4E00\u76F4\u5728\u8FD0\u884C\u8FD9\u4E00\u8BBE\u5907\u3002\u7136\u540E\u6211\u53D1\u73B0\u4E86\u8FD9\u4E2A\u3002\u201D</p><p>\u4ED6\u4F38\u51FA\u624B\uFF0C\u4E00\u4E2A\u4E94\u989C\u516D\u8272\u7684\u7403\u4F53\u51FA\u73B0\u5728\u4E0A\u9762\uFF0C\u4E00\u773C\u5C31\u80FD\u770B\u51FA\u662F\u91CD\u529B\u56FE\u3002</p><p>\u201C\u6839\u636E\u6211\u4EEC\u7684\u6570\u636E\u548C\u519B\u65B9\u6700\u8FD1\u7684\u4E00\u4E9B\u8C03\u67E5\uFF0C\u8FD9\u4F3C\u4E4E\u662F\u67D0\u79CD\u4E3B\u8981\u7684\u5916\u661F\u7AE0\u9C7C\u8BBE\u65BD\u3002\u53F8\u4EE4\u90E8\u5F88\u60F3\u77E5\u9053\u5B83\u662F\u4EC0\u4E48\uFF0C\u6211\u4E5F\u60F3\u77E5\u9053\u5B83\u662F\u4EC0\u4E48\u3002\u4F46\u5982\u679C\u6CA1\u6709\u66F4\u591A\u7684\u79EF\u6781\u53C2\u4E0E\uFF0C\u6211\u65E0\u6CD5\u6536\u96C6\u5230\u5982\u6B64\u8BE6\u7EC6\u7684\u4FE1\u606F\u3002\u8FD9\u5C31\u662F\u4F60\u6765\u8FD9\u513F\u7684\u539F\u56E0\u3002\u201D</p><p>\u201C\u6211\u662F\u4E0D\u662F\u8BE5\u8BF4\u6211\u4E0D\u662F\u5F88\u559C\u6B22\u90A3\u513F\u7684\u58F0\u97F3\uFF0C\u201D \u4E9A\u7EB1\u7F8E\u8BF4\uFF0C\u5979\u7684\u8BED\u8C03\u542C\u8D77\u6765\u53EA\u662F\u534A\u5F00\u73A9\u7B11\u3002</p><p>\u201C\u662F\u7684\uFF0C\u201D \u5F17\u62C9\u5FB7\u8868\u793A\u540C\u610F\u3002\u201D \u4F46\u8FD9\u5E76\u4E0D\u5371\u9669\uFF0C\u6211\u53EA\u9700\u8981\u4F60\u4EEC\u4FE9\u505A\u4F60\u4EEC\u5E73\u65F6\u505A\u7684\u4E8B\uFF0C\u53BB\u90A3\u513F\u7A0D\u5FAE\u901B\u4E00\u4E0B\u3002\u201D</p><p>\u201C\u600E\u4E48\u901B\uFF1F\u201D</p><p>\u201C\u53EA\u8981\u4F60\u6CE8\u610F\u8BFB\u6570\u5C31\u884C\u4E86\u3002\u201D \u5F17\u62C9\u5FB7\u8BF4 \u201C\u4EE5\u524D\uFF0C\u5F53\u4F60\u5C1D\u8BD5\u505A\u5176\u4ED6\u4E8B\u60C5\u7684\u65F6\u5019\uFF0C\u5B83\u7684\u6570\u636E\u90FD\u662F\u968F\u673A\u7684\uFF0C\u4F46\u662F\u6211\u6000\u7591\u5982\u679C\u6709\u4E86\u4E00\u4E9B\u79EF\u6781\u7684\u53CD\u9988\uFF0C\u4F60\u53EF\u80FD\u4F1A\u505A\u5F97\u66F4\u597D\u3002\u6211\u4F1A\u786E\u4FDD\u4F60\u5728\u884C\u52A8\u65F6\u80FD\u770B\u5230\u5B83\u3002\u201D</p><p>\u201C\u6211\u4EEC\u7684\u76EE\u7684\u662F\u4EC0\u4E48\uFF1F\u201D</p><p>\u201C\u66F4\u597D\u7684\u5206\u8FA8\u7387\uFF0C\u66F4\u5F3A\u7684\u4FE1\u53F7\uFF0C\u8BF8\u5982\u6B64\u7C7B\uFF0C\u201D \u5F17\u62C9\u5FB7\u8BF4 \u201C\u6211\u4EEC\u4ECD\u7136\u5728\u731C\u6D4B\u8BE5\u5C55\u793A\u4EC0\u4E48\u6837\u7684\u4E1C\u897F\u3002\u4E0D\u5FC5\u7B2C\u4E00\u6B21\u5C31\u6210\u529F\u3002\u201D</p><p>\u201C\u4E00\u4E2A\u65B0\u9896\u7684\u5EFA\u8BAE\uFF0C\u201D \u9676\u62F1\u8D77\u7709\u6BDB\u8BF4 \u201C\u8BA9\u4ED6\u4EEC\u8C03\u6574\u65B9\u6CD5\uFF0C\u800C\u4E0D\u662F\u8C03\u6574\u4EEA\u5668\u3002\u201D</p><p>\u201C\u597D\u5427\uFF0C\u6211\u4EEC\u4E5F\u53EF\u4EE5\u91CD\u65B0\u6821\u51C6\u5176\u4ED6\u8981\u7D20\uFF0C\u201D \u5F17\u62C9\u5FB7\u8BF4 \u201C\u4F46\u5982\u679C\u53EA\u5173\u6CE8\u8FD9\u4E00\u70B9\u800C\u5FFD\u7565\u4E86\u80FD\u6E90\uFF0C\u90A3\u5C31\u592A\u50BB\u4E86\u3002 \u201D</p><p>\u4ED6\u7528\u4E00\u79CD\u8FC7\u4E8E\u610F\u5473\u6DF1\u957F\u7684\u773C\u795E\u770B\u7740\u4E9A\u7EB1\u7F8E\u3002</p><p>\u201C\u975E\u5F97\u6211\u6765\u561B\uFF1F\u201D \u826F\u5B50\u95EE \u201C\u901A\u5E38\u6211\u6765\u8FD9\u91CC\u662F\u4E3A\u4E86\u8BD5\u9A8C\u866B\u6D1E\u7684\u5F62\u6210\uFF0C\u800C\u4E0D\u662F\u4E3A\u4E86\u522B\u7684\u3002\u542C\u8D77\u6765\u6211\u4EEC\u597D\u50CF\u4E0D\u5728\u5E72\u8FD9\u4E8B\uFF1F\u201D</p><p>\u201C\u4E5F\u8BB8\u4E0D\u662F\uFF0C\u201D \u5F17\u62C9\u5FB7\u5FAE\u5FAE\u6B6A\u7740\u5934\u8BF4\uFF0C\u201C\u4F46\u53EF\u80FD\u8FD9\u4E48\u505A\u4E5F\u6CA1\u4EC0\u4E48\u4E0D\u59A5\uFF0C\u6211\u60F3\u4F60\u4EEC\u4E24\u4E2A\u66F4\u613F\u610F\u5728\u4E00\u8D77\u3002\u201D</p><p>\u5F17\u62C9\u5FB7\u8BF4\u7684\u8BDD\u8BA9\u5979\u8138\u7EA2\u4E86\uFF0C\u60F3\u628A\u8138\u85CF\u8D77\u6765\uFF0C\u8FD9\u8BA9\u4E9A\u7EB1\u7F8E\u89C9\u5F97\u597D\u7B11\u3002</p><p>\u201C\u90A3\u6211\u4EEC\u5C31\u8FD9\u6837\u5427\uFF0C\u201D \u4E9A\u7EB1\u7F8E\u7B11\u7740\u8BF4\u3002</p><p>\u90A3\u4E24\u4F4D\u79D1\u5B66\u5BB6\u524D\u5F80\u89C2\u6D4B\u573A\uFF0C\u5C31\u8FDC\u8DDD\u79BB\u91CD\u529B\u4F20\u611F\u5668\u7684\u8BBE\u8BA1\u5C55\u5F00\u8BA8\u8BBA\u3002</p><p>\u201C\u662F\u7684\uFF0C\u6211\u5F53\u7136\u66F4\u559C\u6B22\u5728\u4E00\u8D77\uFF0C\u201D \u4E9A\u7EB1\u7F8E\u8BF4\uFF0C\u8FD9\u65F6\uFF0C\u826F\u5B50\u8F6C\u5411\u5979\u4EEC\u7528\u4F5C\u66F4\u8863\u5BA4\u7684\u5C0F\u623F\u95F4 \u201C\u4F46\u662F\uFF0C\u5509\uFF0C\u5F53\u6211\u4EEC\u6B63\u8BD5\u56FE\u56DE\u623F\u95F4\u7684\u65F6\u5019\uFF0C\u6709\u4EBA\u51B3\u5B9A\u53EC\u96C6\u6211\u4EEC\u505A\u4E00\u4E2A\u7279\u522B\u7684\u5B9E\u9A8C\u3002\u6211\u81EA\u5DF1\u4E5F\u6709\u70B9\u5931\u671B\u3002\u201D</p><p>\u826F\u5B50\u7B11\u4E86\uFF0C\u56DE\u5FC6\u8D77\u4E9A\u7EB1\u7F8E\u770B\u5230\u8FD9\u6D88\u606F\u662F\u591A\u4E48\u7684\u607C\u706B\uFF0C\u7136\u540E\u8D70\u8FDB\u4E86\u5979\u5B58\u653E\u8863\u670D\u7684\u623F\u95F4\uFF0C\u4E9A\u7EB1\u7F8E\u8DDF\u7740\u5979\u3002</p><p>\u8FC7\u4E86\u4E00\u4F1A\u513F\uFF0C\u95E8\u8F7B\u8F7B\u4E00\u58F0\u5173\u4E0A\u4E86\u3002</p><p>\u5979\u5F00\u59CB\u8131\u4E0B\u5DF2\u7ECF\u89E3\u5F00\u6263\u5B50\u7684\u4E0A\u8863\uFF0C\u4F46\u5F53\u5979\u6CE8\u610F\u5230\u4E9A\u7EB1\u7F8E\u5728\u770B\u7740\u5979\u65F6\uFF0C\u5979\u505C\u4E86\u4E0B\u6765\u3002\u89C6\u7EBF\u8FFD\u8E2A\u5E76\u4E0D\u96BE\u3002</p><p><strong>\u53EB\u5979\u89C4\u77E9\u70B9\uFF0C</strong>\u514B\u83B1\u4E3D\u4E1D\u60F3\uFF0C\u8BED\u6C14\u5E7D\u9ED8\uFF0C\u5F88\u660E\u663E\u5979\u5728\u5F00\u73A9\u7B11\u3002</p><p>\u826F\u5B50\u8F6C\u4E86\u8F6C\u773C\u775B\uFF0C\u51B3\u5B9A\u8F6C\u8FC7\u5934\u53BB\u3002\u90A3\u513F\u771F\u6CA1\u4EC0\u4E48\u597D\u770B\u7684 \u867D\u7136\u4E9A\u7EB1\u7F8E\u64C5\u957F\u8BA9\u4EBA\u89C9\u5F97\u5979\u5728\u9690\u85CF\u4E00\u4E9B \u201C\u597D\u201D \u4E1C\u897F\uFF0C\u4F46\u5979\u5E76\u6CA1\u6709\u770B\u5230\u3002</p><p>\u826F\u5B50\u77A5\u4E86\u4E00\u773C\uFF0C\u770B\u5230\u4E9A\u7EB1\u7F8E\u4E5F\u5F00\u59CB\u6362\u8863\u670D\uFF0C\u5C31\u628A\u4E0A\u8863\u8131\u4E86\u4E0B\u6765\u3002\u4E0D\u80FD\u7A7F\u592A\u591A\u7684\u8863\u670D\u5728\u771F\u7A7A\u670D\u91CC\u633A\u4E0D\u65B9\u4FBF\u7684\uFF0C\u4F46\u5979\u53EF\u4EE5\u7406\u89E3\uFF0C\u8FD9\u79CD\u65B9\u4FBF\u6BEB\u65E0\u610F\u4E49\u3002</p><p>\u5728\u514B\u83B1\u4E3D\u4E1D\u7684\u4E00\u58F0\u53EE\u5F53\u58F0\u7684\u63D0\u793A\u4E0B\uFF0C\u5979\u4ECE\u601D\u7EEA\u4E2D\u8D70\u51FA\u6765\uFF0C\u770B\u89C1\u4E9A\u7EB1\u7F8E\u53C8\u5728\u770B\u81EA\u5DF1\u3002\u56E0\u4E3A\u53E6\u4E00\u4E2A\u5973\u5B69\u4E5F\u6CA1\u6709\u7A7F\u597D\u8863\u670D\uFF0C\u5979\u4E5F\u53D1\u73B0\u81EA\u5DF1\u7684\u773C\u795E\u5411\u4E0B\u6E38\u8361\u2026</p><p>\u201C\u522B\u5427\uFF0C\u201D \u826F\u5B50\u4E00\u8FB9\u8BF4\uFF0C\u4E00\u8FB9\u7528\u4E00\u53EA\u624B\u505A\u4E86\u4E2A\u624B\u52BF \u201C\u6211\u5F88\u9AD8\u5174\u4F60\u8FD9\u4E48\u5173\u6CE8\u6211\uFF0C\u4F46\u73B0\u5728\u771F\u7684\u4E0D\u662F\u65F6\u5019\u3002\u4F60\u4ECA\u5929\u600E\u4E48\u4E86\uFF1F\u6211\u4EEC\u4EE5\u524D\u53EF\u6CA1\u5728\u5BF9\u65B9\u9762\u524D\u6362\u8FC7\u8863\u670D\u3002\u201D</p><p>\u5979\u53C8\u679C\u65AD\u5730\u8F6C\u8FC7\u8EAB\u53BB\uFF0C\u4E13\u6CE8\u5730\u62FF\u8D77\u771F\u7A7A\u670D\u7684\u51E0\u5757\u914D\u4EF6\uFF0C\u5F2F\u8170\u628A\u5B83\u4EEC\u8D34\u5728\u817F\u4E0A\u3002\u6BCF\u4E00\u4E2A\u6A21\u5757\u4EF6\u7D27\u5BC6\u5730\u5B89\u88C5\u5728\u9002\u5F53\u7684\u5730\u65B9\uFF0C\u53D1\u51FA\u8F7B\u5FAE\u7684\u5438\u9644\u97F3\u3002</p><p>\u4E9A\u7EB1\u7F8E\u4EE4\u4EBA\u60CA\u8BB6\u5730\u5728\u5979\u8F6C\u8EAB\u65F6\u6293\u4F4F\u4E86\u5979\u7684\u624B\u81C2\u3002</p><p>\u4E9A\u7EB1\u7F8E\u8D81\u673A\u6DF1\u6DF1\u5730\u543B\u4E0A\u4E86\u5979\u7684\u5634\u5507\uFF0C\u5728\u826F\u5B50\u610F\u8BC6\u5230\u8FD9\u4E00\u70B9\u4E4B\u524D\uFF0C\u5979\u611F\u5230\u81EA\u5DF1\u88AB\u62C9\u4E86\u8FDB\u53BB\uFF0C\u5411\u540E\u9760\u5728\u5899\u4E0A\u4EE5\u63D0\u4F9B\u4E00\u4E2A\u89D2\u5EA6\u3002\u5979\u611F\u5230\u4E9A\u7EB1\u7F8E\u7684\u624B\u653E\u5728\u5979\u7684\u4E0B\u80CC\u90E8\uFF0C\u5192\u9669\u5730\u79FB\u52A8\u7740\u3002</p><p>\u201C\u597D\u5427\uFF0C\u771F\u7684\uFF0C\u201D \u5979\u8BF4\u7740\uFF0C\u628A\u5934\u5F80\u540E\u62C9\uFF0C\u6293\u4F4F\u4E86\u90A3\u53EA\u624B \u201C\u73B0\u5728\u4E0D\u884C\u3002\u201D</p><p>\u201C\u522B\u7D27\u5F20\uFF0C\u6211\u4E0D\u4F1A\u8BA9\u53EF\u601C\u7684\u9676\u4E3B\u4EFB\u96BE\u582A\u7684\uFF0C\u201D \u4E9A\u7EB1\u7F8E\u9000\u540E\u8BF4 \u201C\u6211\u53EA\u662F\u60F3\u8BA9\u81EA\u5DF1\u653E\u5FC3\uFF0C\u4F60\u4ECD\u7136\u5C5E\u4E8E\u6211\u3002\u201D</p><p>\u5979\u7684\u773C\u775B\u770B\u8D77\u6765\u4E0D\u592A\u597D\u8272\uFF0C\u4E0D\u5B8C\u5168\u662F\u3002\u800C\u662F\u663E\u5F97\u66F4\u6709\u2026 \u5360\u6709\u6B32\u3002</p><p>\u826F\u5B50\u75B2\u5026\u5730\u6447\u4E86\u6447\u5934\u3002</p><p>\u201C\u4F60\u77E5\u9053\uFF0C\u4E0D\u662F\u6240\u6709\u7684\u4E8B\u60C5\u90FD\u548C\u6027\u6709\u5173\uFF0C\u201D \u5979\u8BF4 \u201C\u4E00\u6BB5\u611F\u60C5\u4E0D\u6B62\u4E8E\u6B64\u3002\u4F60\u4E0D\u662F\u4E00\u76F4\u5728\u770B\u60C5\u611F\u6307\u5357\u5417\uFF1F\u201D</p><p>\u8F6E\u5230\u4E9A\u7EB1\u7F8E\u7FFB\u767D\u773C\u4E86\u3002</p><p>\u201C\u662F\u7684\uFF0C\u4F46\u5B83\u4EEC\u5E76\u6CA1\u8003\u8651\u5230\u6211\u4EEC\u7684\u60C5\u51B5\uFF0C\u4E0D\u662F\u5417\uFF1F\u5728\u8FD9\u4E2A\u7A7A\u95F4\u7AD9\u4E0A\u6CA1\u4EC0\u4E48\u53EF\u505A\u7684\uFF0C\u6211\u611F\u5230\u5F88\u7D27\u5F20\u800C\u4E14\u5FB7\u5C14\u9A6C\u6208\u8FD8\u5728\u9644\u8FD1\u3002\u201D</p><p>\u6700\u540E\u4E00\u53E5\u8BDD\u662F\u968F\u4FBF\u8BF4\u7684\u2026 \u592A\u968F\u4FBF\u4E86\u3002</p><p>\u201C\u6211\u77E5\u9053\u5979\u5728\u8FD9\u513F\u8BA9\u4F60\u5F88\u4E0D\u5B89\uFF0C\u201D \u826F\u5B50\u8BF4\u7740\uFF0C\u628A\u8863\u670D\u7684\u8EAF\u5E72\u90E8\u5206\u7CFB\u4E0A\u4E86 \u201C\u4F46\u4F60\u77E5\u9053\u6211\u5BF9\u5979\u6CA1\u6709\u5174\u8DA3\u3002\u4E0D\u662F\u90A3\u6837\u7684\u3002\u201D</p><p>\u201C\u6211\u77E5\u9053\uFF0C\u201D \u4E9A\u7EB1\u7F8E\u8BF4 \u201C\u4F46\u8FD9\u662F\u4E0D\u5408\u7406\u7684\uFF0C\u4F60\u61C2\u5417\uFF1F\u6211\u53EA\u662F\u89C9\u5F97\uFF0C\u6709\u65F6\u5019\uFF0C\u6211\u4EEC\u5728\u4E00\u8D77\u53EA\u505A\u8FC7\u4E00\u4EF6\u4E8B\uFF0C\u800C\u4F60\u751A\u81F3\u90FD\u4E0D\u559C\u6B22\u3002\u201D</p><p>\u826F\u5B50\u5446\u5446\u5730\u770B\u7740\u53C8\u4E00\u5757\u592A\u7A7A\u670D\u5728\u5979\u7684\u624B\u81C2\u4E0A\u627E\u5230\u4E86\u4F4D\u7F6E\u3002</p><p>\u201C\u989D\uFF0C\u8003\u8651\u5230\u8FD9\u4E2A\u7684\u8BDD\uFF0C\u5373\u4F7F\u5728\u6728\u536B\u4E09\u5341\u4E8C\u4E0A\uFF0C\u6211\u4EEC\u4E5F\u53EA\u662F\u5FD9\u4E8E\u5E73\u5E38\u7684\u4E8B\u60C5\u3002\u514B\u83B1\u4E3D\u4E1D\u60F3\u8BA9\u6211\u6307\u51FA\uFF0C\u4ECE\u6280\u672F\u4E0A\u8BB2\uFF0C\u6211\u4EEC\u751A\u81F3\u4ECE\u6765\u6CA1\u6709\u771F\u6B63\u7EA6\u4F1A\u8FC7\u3002\u201D</p><p>\u4E9A\u7EB1\u7F8E\u5657\u55E4\u5730\u7B11\u51FA\u4E86\u58F0</p><p>\u201C\u5979\u771F\u662F\u4E00\u5982\u65E2\u5F80\u5730\u7167\u987E\u6211\u4EEC\uFF0C\u201D \u5979\u5C34\u5C2C\u5730\u7B11\u7740\u8BF4 \u201C\u4E0D\uFF0C\u6211\u4EEC\u6CA1\u6709\u3002\u4F46\u4E0D\u5982\u6211\u4EEC\u5728\u8FD9\u513F\u505A\u5462\uFF1F\u201D</p><p>\u201C\u989D\uFF0C\u6211\u4EEC\u603B\u6709\u673A\u4F1A\u53BB\u50CF\u963F\u5179\u745E\u5C14\u60F3\u7684\u90A3\u6837\u53BB\u4E00\u8FB9\u98DE\u4E00\u8FB9\u8DF3\u821E\u4EC0\u4E48\u7684\uFF0C\u201D \u826F\u5B50\u8BF4 \u201C\u8FD8\u6709\u865A\u62DF\u73B0\u5B9E\u91CC\u90A3\u4E9B\u4E1C\u897F\u3002\u6211\u4EEC\u90A3\u4F1A\u5DF2\u7ECF\u73A9\u7684\u633A\u5F00\u5FC3\u7684\u4E86\u3002\u201D</p><p>\u826F\u5B50\u671B\u5411\u522B\u5904\uFF0C\u628A\u8FD9\u5F53\u4F5C\u601D\u8003\u7684\u501F\u53E3\u3002\u9664\u4E86\u865A\u62DF\u73B0\u5B9E\uFF0C\u5728\u8FD9\u91CC\u6CA1\u6709\u5F88\u591A\u597D\u7684\u7EA6\u4F1A\u65B9\u5F0F\u3002\u865A\u62DF\u73B0\u5B9E\u633A\u597D\u7684\uFF0C\u4F46\u5728\u73B0\u5B9E\u751F\u6D3B\u4E2D\u4E00\u8D77\u53BB\u67D0\u4E2A\u5730\u65B9\u4F3C\u4E4E\u2026 \u66F4\u5408\u9002\u4E00\u70B9\u3002\u5F53\u5979\u5FD9\u7740\u4E3A\u897F\u8499\u5A1C\u8003\u8651\u7684\u65F6\u5019\uFF0C\u4E3A\u4EC0\u4E48\u4E0D\u4E3A\u4E9A\u7EB1\u7F8E\u60F3\u60F3\u5462\uFF1F</p><p>\u4E0B\u4E00\u6B21\u6211\u4EEC\u79BB\u5F00\u8FD9\u4E2A\u592A\u7A7A\u7AD9\u7684\u65F6\u5019\uFF0C\u6211\u4EEC\u9884\u5B9A\u70B9\u4E1C\u897F\u5427\uFF0C\u201D \u826F\u5B50\u5411\u524D\u503E\u4E86\u503E\uFF0C\u8BA9\u4E9A\u7EB1\u7F8E\u80FD\u770B\u4E00\u773C\u5979\u8FD8\u66B4\u9732\u5728\u5916\u7684\u80A9\u90E8\uFF0C\u201C\u6211\u76F8\u4FE1\u4F50\u4ED3\u3055\u3093\u4F1A\u63A8\u8350\u4E00\u4E9B\u4E1C\u897F\u3002\u201D</p><p>\u4E9A\u7EB1\u7F8E\u8FC5\u901F\u7728\u4E86\u7728\u773C\u3002</p><p>\u201C\u597D\u5427\uFF0C\u201D \u5979\u8BF4 \u201C\u4E00\u5B9A\u5F97\u6211\u4EEC\u90FD\u80FD\u4E50\u5728\u5176\u4E2D\u7684\u4E1C\u897F\u3002\u6211\u4E0D\u60F3\u89C9\u5F97\u4F60\u8FD9\u6837\u505A\u53EA\u662F\u4E3A\u4E86\u6211\u3002\u201D</p><p>\u201C\u5373\u4F7F\u6211\u8FD9\u4E48\u505A\uFF0C\u4E5F\u4E0D\u4F1A\u90A3\u4E48\u7CDF\uFF0C\u201D \u826F\u5B50\u8BF4\u3002</p><p>\u4E9A\u7EB1\u7F8E\u8F6C\u8FC7\u8EAB\u53BB\uFF0C\u770B\u7740\u95E8\u53E3\uFF0C\u5979\u628A\u6700\u540E\u51E0\u4EF6\u592A\u7A7A\u670D\u62FC\u5728\u4E00\u8D77\u3002</p><p>\u201C\u522B\u6025\u7740\u505A\u5B9E\u9A8C\uFF0C\u201D \u826F\u5B50\u5EFA\u8BAE\u9053\uFF0C\u4E9A\u7EB1\u7F8E\u7684\u624B\u5957\u6B63\u7F20\u5728\u5979\u7684\u624B\u4E0A \u201C\u5982\u679C\u6211\u4EEC\u80FD\u9A6C\u4E0A\u5B8C\u6210\uFF0C\u6211\u4EEC\u4EE5\u540E\u4F1A\u6709\u66F4\u591A\u81EA\u5DF1\u7684\u7A7A\u95F2\u65F6\u95F4\u3002\u8FD9\u633A\u503C\u7684\u3002\u201D</p><p>\u4E9A\u7EB1\u7F8E\u505A\u4E86\u4E00\u4E2A\u5947\u602A\u7684\u8868\u60C5\uFF0C\u77AC\u95F4\u88AB\u5212\u8FC7\u7684\u5934\u76D4\u906E\u4F4F\u4E86\u8138\uFF0C\u5979\u7684\u5934\u53D1\u4EE5\u81EA\u5DF1\u7684\u65B9\u5F0F\u79FB\u52A8\u3002</p><p>\u201C\u4F60\u5728\u8FD9\u65B9\u9762\u505A\u5F97\u8D8A\u6765\u8D8A\u597D\u4E86\uFF0C\u201D \u5979\u5728\u65E0\u7EBF\u7535\u4E0A\uFF0C\u6C89\u91CD\u5730\u6447\u7740\u5934\u8BF4 \u201C\u8FD9\u8BA9\u6211\u5F88\u9AD8\u5174\u3002\u201D</p><p>\u201C\u6211\u4EEC\u8D70\u5427\uFF0C\u201D \u826F\u5B50\u901A\u8FC7\u81EA\u5DF1\u7684\u65E0\u7EBF\u7535\u8BF4\u3002</p><p>\u4E4B\u540E\u7684\u7A0B\u5E8F\u975E\u5E38\u719F\u6089\uFF0C\u53EF\u4EE5\u8BF4\u662F\u4F8B\u884C\u516C\u4E8B\uFF1A\u8D70\u51FA\u95E8\uFF0C\u53F3\u8F6C\uFF0C\u8FDB\u5165\u8D34\u6709\u660E\u4EAE\u6807\u7B7E\u7684\u6C14\u95F8\u5BA4\uFF0C\u7136\u540E\u5728\u7A7A\u6C14\u88AB\u6CF5\u51FA\u623F\u95F4\u65F6\u77ED\u6682\u7B49\u5F85\u3002</p><p>\u5F53\u826F\u5B50\u8D70\u51FA\u6C14\u95F8\u65F6\uFF0C\u770B\u7740\u4E9A\u7EB1\u7F8E\u8FDB\u5165\u6D4B\u8BD5\u533A\u57DF\u4E2D\u5FC3\u9644\u8FD1\u7684\u4F4D\u7F6E\uFF0C\u5979\u610F\u8BC6\u5230\u80FD\u591F\u6F02\u6D6E\u5728\u5899\u58C1\u9644\u8FD1\u89C2\u770B\u662F\u591A\u4E48\u7684\u653E\u677E\u3002\u8FD9\u91CC\u4EC0\u4E48\u4E5F\u4E0D\u7528\u5979\u6765\u5E72\u3002</p><p>\u4E0D\uFF0C\u4E5F\u4E0D\u662F\u4EC0\u4E48\u90FD\u4E0D\u7528\u5E72\u3002</p><p><strong>\u5F88\u597D\uFF0C\u4E9A\u7EB1\u7F8E\u3061\u3083\u3093\uFF0C\u4F60\u53EF\u4EE5\u505A\u5230\u7684\uFF0C</strong>\u5979\u5FF5\u8BDD\u9053\uFF0C<strong>\u6211\u76EF\u7740\u4F60\u5462\u3002</strong></p><p><strong>\u7B49\u4E0B\uFF0C\u4E9A\u7EB1\u7F8E\u3061\u3083\u3093\uFF1F</strong>\u5979\u7684\u5973\u670B\u53CB\u60F3\uFF0C<strong>\u4F60\u4ECE\u6CA1\u8FD9\u4E48\u53EB\u8FC7\u6211\u3002</strong></p><p><strong>\u6211\u60F3\u6211\u5F97\u7F13\u89E3\u4E0B\u6C14\u6C1B</strong></p><p><strong>\u5929\u54EA\uFF0C\u6211\u2026\u2026\u4F60\u8981\u7ED9\u6211\u52A0\u6CB9\u561B\uFF1F</strong></p><p>\u826F\u5B50\u5FCD\u4E0D\u4F4F\u7B11\u4E86\u7B11\uFF0C\u5F88\u8F7B\u58F0\u7684\u90A3\u79CD\u3002</p><p><strong>\u55EF\uFF0C\u662F\u7684\u3002\u8FD9\u5C31\u662F\u6211\u5E94\u8BE5\u505A\u7684\u3002\u514B\u83B1\u4E3D\u4E1D\u5C31\u662F\u8FD9\u4E48\u8BF4\u7684\u3002</strong></p><p><strong>\u5F53\u7136\u514B\u83B1\u4E3D\u4E1D\u4E5F\u8BF4\u8FC7\u522B\u89E3\u91CA\u8FD9\u662F\u5979\u8BF4\u7684\uFF0C</strong>\u514B\u83B1\u4E3D\u4E1D\u5FC3\u7D2F\u5730\u8BF4\uFF0C<strong>\u4F60\u5C31\u8BF4\u4F60\u81EA\u5DF1\u8FD9\u4E48\u60F3\u7684\uFF0C\u4F1A\u6B7B\u554A\uFF1F</strong></p><p><strong>\u6211\u53EA\u662F\u89C9\u5F97\u5F88\u5C34\u5C2C\uFF0C\u4F60\u77E5\u9053\uFF0C</strong>\u826F\u5B50\u60F3\u3002</p><p>\u826F\u5B50\u4E0D\u77E5\u9053\u514B\u83B1\u4E3D\u4E1D\u662F\u5982\u4F55\u7528\u4E00\u79CD\u60C5\u611F\u6765\u4F20\u8FBE\u7FFB\u767D\u773C\u7684\u611F\u89C9\u7684\u3002</p><p>\u201C\u6211\u2026\u2026\u989D\uFF0C\u8C22\u8C22\uFF0C\u201D \u4E9A\u7EB1\u7F8E\u663E\u7136\u5F88\u614C\u5F20\u5730\u5927\u58F0\u8BF4\u3002</p><p>\u201C\u6211\u4EEC\uFF0C\u5443\uFF0C\u5F88\u5FEB\u5C31\u8981\u5F00\u59CB\u8BD5\u9A8C\u4E86\uFF0C\u201D \u5F17\u62C9\u5FB7\u5728\u4ED6\u4EEC\u7684\u592A\u7A7A\u670D\u65E0\u7EBF\u7535\u4E0A\u8BF4 \u201C\u8BF7\u51C6\u5907\u597D\u3002\u201D</p><p>\u4E9A\u7EB1\u7F8E\u72B9\u8C6B\u4E86\u4E00\u4E0B\uFF0C\u624D\u6446\u51FA\u5979\u4E60\u60EF\u7684\u59FF\u52BF\uFF0C\u4E3E\u624B\u6BD4\u5E73\u65F6\u663E\u5F97\u6709\u70B9\u62DB\u6447\u3002\u826F\u5B50\u5F00\u59CB\u62C5\u5FC3\uFF0C\u4E5F\u8BB8\u662F\u5979\u5206\u6563\u4E86\u5979\u7684\u6CE8\u610F\u529B\u3002</p><p><strong>\u6709\u4E9B\u5DF2\u5A5A\u592B\u5987\u5F7C\u6B64\u76F8\u5904\u7684\u65F6\u95F4\u6BD4\u4F60\u4EEC\u4E24\u4E2A\u8FD8\u5C11\uFF0C</strong>\u514B\u83B1\u4E3D\u4E1D\u62B1\u6028\uFF0C<strong>\u800C\u4F60\u4EEC\u7684\u8868\u73B0\u4ECD\u7136\u50CF\u4E00\u5BF9\u5FC3\u614C\u610F\u4E71\u7684\u9752\u5C11\u5E74\u3002</strong></p><p><strong>\u6211\u5C31\u662F\u4E2A\u5FC3\u614C\u610F\u4E71\u7684\u9752\u5C11\u5E74\u3002</strong></p><p><strong>\u90A3\u6211\u4E00\u5C81\u90FD\u6CA1\u5230\u5462\uFF01</strong></p><p><strong>\u4F60\u77E5\u9053\u8FD9\u4E0D\u7B97\u6570\u7684\uFF01</strong></p><p>\u663E\u7136\u8FD9\u6B21\u6CA1\u5FC5\u8981\u731C\u6D4B\u662F\u4EC0\u4E48\u4E1C\u897F\u3002\u4E9A\u7EB1\u7F8E\u9762\u524D\u7684\u7A7A\u95F4\u88AB\u626D\u66F2\u4E86\uFF0C\u4E16\u754C\u5411\u5185\u5F2F\u66F2\uFF0C\u5F62\u6210\u4E00\u4E2A\u5B8C\u7F8E\u7684\u9ED1\u8272\u5706\u5708\uFF0C\u663E\u800C\u6613\u89C1\u7684\uFF0C\u5979\u773C\u524D\u7684\u4E1C\u897F\u4E0D\u662F\u522B\u7684\u3002</p><p>\u4E00\u5F00\u59CB\uFF0C\u4E9A\u7EB1\u7F8E\u82B1\u4E86\u534A\u4E2A\u5C0F\u65F6\u624D\u80FD\u505A\u5230\u8FD9\u79CD\u7A0B\u5EA6\u3002\u73B0\u5728\uFF0C\u51E0\u5206\u949F\u5C31\u8DB3\u591F\u4E86\u3002</p><p>\u4E00\u4E2A\u65B0\u7684\u7269\u4F53\u95EA\u70C1\u7740\u8FDB\u5165\u5979\u7684\u89C6\u7EBF\uFF0C\u4E00\u4E2A\u4E94\u5F69\u7F24\u7EB7\u7684\u7403\u4F53\u4F3C\u4E4E\u6F02\u6D6E\u5728\u5979\u65C1\u8FB9\u7684\u7A7A\u95F4\u3002\u8FD9\u662F\u5F17\u62C9\u5FB7\u8BF4\u8BF4\u7684\u9065\u8FDC\u8BBE\u65BD\u7684\u56FE\u50CF\u3002</p><p>\u201C\u6211\u4EEC\u662F\u8981\u8BD5\u7740\u52A0\u5F3A\u8FD9\u513F\u7684\u4FE1\u53F7\u662F\u5417\u201D \u4E9A\u7EB1\u7F8E\u95EE\u3002\u201C\u628A\u8FD9\u4E1C\u897F\u53D8\u6210\u7D2B\u8272\uFF1F\u201D</p><p>\u201C\u662F\u7684\uFF0C\u8FD9\u662F\u7B2C\u4E00\u6B65\uFF0C\u201D \u5F17\u62C9\u5FB7\u8BF4\u3002\u201C\u4F46\u662F\u4E0D\u6B62\u5982\u6B64\u3002\u8FD9\u5468\u56F4\u6709\u8FD9\u4E48\u591A\u7684\u7D0A\u6D41\uFF0C\u4EE5\u81F3\u4E8E\u6211\u76F8\u4FE1\u8FD9\u662F\u4E00\u4E2A\u63A2\u6D4B\u4F2A\u5F71\uFF0C\u4E0D\u662F\u771F\u6B63\u7684\u4FE1\u53F7\u3002\u5982\u679C\u4F60\u80FD\u6446\u8131\u8FD9\u4E9B\u4E1C\u897F\uFF0C\u90A3\u8FD8\u633A\u6709\u52A9\u4E8E\u5206\u6790\u7684\u3002\u201D</p><p>\u201C\u6211\u5C3D\u91CF\uFF0C\u201D \u4E9A\u7EB1\u7F8E\u8BF4\u3002</p><p>\u826F\u5B50\u9759\u9759\u5730\u770B\u7740\u4E9A\u7EB1\u7F8E\u9644\u8FD1\u7684\u6DA1\u6D41\u5E73\u9759\u4E0B\u6765\uFF0C\u540C\u65F6\u5979\u5C1D\u8BD5\u4E86\u9664\u866B\u6D1E\u7A0B\u5E8F\u4EE5\u5916\u7684\u5176\u4ED6\u64CD\u4F5C\u65B9\u5F0F\uFF0C\u63A2\u7D22\u7740\u4F7F\u8BBE\u5907\u5DE5\u4F5C\u5F97\u66F4\u597D\u7684\u65B9\u5F0F\u3002\u8D77\u521D\uFF0C\u8FD9\u4F3C\u4E4E\u662F\u4E00\u4E2A\u75DB\u82E6\u800C\u7F13\u6162\u7684\u8BD5\u9519\u8FC7\u7A0B\uFF0C\u4F46\u540E\u6765\uFF0C\u5C31\u5728\u514B\u83B1\u4E3D\u4E1D\u5F00\u59CB\u5728\u5979\u7684\u8BB0\u4E8B\u672C\u4E0A\u5199\u4E0B\u66F4\u591A\u7684\u5B9E\u9A8C\u62A5\u544A\u65F6\uFF0C\u5706\u7403\u53D8\u6210\u4E86\u4E00\u4E2A\u660E\u4EAE\u800C\u504F\u7EA2\u7684\u7D2B\u8272\uFF0C\u770B\u8D77\u6765\u5F88\u70AB\u76EE\u3002</p><p>\u201C\u4EE4\u4EBA\u5370\u8C61\u6DF1\u523B\u7684\u7A81\u7834\uFF0C\u201D \u5F17\u62C9\u5FB7\u8BC4\u8BBA\u9053\u3002</p><p>\u201C\u662F\u7684\uFF0C\u201D \u4E9A\u7EB1\u7F8E\u627F\u8BA4\uFF0C\u201C\u4E8B\u5B9E\u4E0A\u6211\u89C9\u5F97\u5F88\u5947\u602A\uFF0C\u597D\u50CF\u6211\u4E0D\u77E5\u600E\u4E48\u7406\u89E3\u8FD9\u4E2A\u63A2\u6D4B\u5668\u3002\u8FD9\u672C\u4E0D\u5E94\u8BE5\u8FD9\u4E48\u5BB9\u6613\u7684\u3002\u201D</p><p>\u201C\u8FD9\u5BF9\u4F60\u6765\u8BF4\u53EF\u80FD\u662F\u81EA\u7136\u800C\u7136\u7684\u4E8B\u60C5\uFF0C\u201D \u826F\u5B50\u56DE\u5FC6\u8D77\u5979\u8BFB\u8FC7\u7684\u4E00\u4E9B\u5173\u4E8E\u80FD\u529B\u5F00\u53D1\u7684\u4E66\u3002</p><p>\u201C\u4E5F\u8BB8\u5427\uFF0C\u4F46\u73B0\u5728\u6211\u51E0\u4E4E\u611F\u89C9\u5230\u4E86\u2026\u2026 \u6211\u81EA\u5DF1\u2026\u2026\u201D</p><p>\u8FD9\u65F6\u5979\u505C\u6B62\u4E86\u8BF4\u8BDD\uFF0C\u8D70\u8FD1\u6F29\u6DA1\uFF0C\u8EAB\u4F53\u9760\u4E86\u8FDB\u53BB\uFF0C\u8FD9\u8DB3\u4EE5\u8BA9\u826F\u5B50\u5F00\u59CB\u611F\u5230\u6709\u70B9\u7D27\u5F20\uFF0C\u56E0\u4E3A\u5979\u79BB\u5F97\u592A\u8FD1\u4E86\u3002</p><p>\u4F46\u662F\u826F\u5B50\u6CA1\u8BF4\u4EC0\u4E48\uFF0C\u60F3\u8BA9\u4E9A\u7EB1\u7F8E\u4E13\u5FC3\u4E00\u70B9\u3002\u56F4\u7ED5\u7403\u4F53\u7684\u6F29\u6DA1\u4F3C\u4E4E\u7A0D\u5FAE\u51CF\u5F31\uFF0C\u7136\u540E\u518D\u6B21\u53D8\u5F97\u66F4\u5F3A\u3002\u4E0D\u7BA1\u662F\u4EC0\u4E48\uFF0C\u963F\u8428\u7C73\u90FD\u5F88\u68D8\u624B\u3002</p><p>\u201C\u6211\u89C9\u5F97\u8FD9\u6839\u672C\u4E0D\u662F\u4EC0\u4E48\u4EBA\u9020\u7269\u3002\u201D \u5979\u8BF4\uFF0C \u201C\u6211\u80FD\u611F\u89C9\u5230\u5B83\u5728\u53D8\u5316\u3002\u8FD9\u662F\u4EBA\u9020\u7684\uFF0C\u91CD\u590D\u2026\u2026 \u5B83\u4EEC\u4EE5\u67D0\u79CD\u65B9\u5F0F\u53CD\u5C04\u4E86\u8109\u51B2\u661F\u7684\u65CB\u8F6C\uFF0C\u4F7F\u5916\u754C\u5F88\u96BE\u770B\u5230\u53D1\u751F\u4E86\u4EC0\u4E48\u3002\u201D</p><p>\u201C\u8FD9\u662F\u4E2A\u9690\u533F\u88C5\u7F6E\uFF1F\u201D \u5F17\u62C9\u5FB7\u8BF4\uFF0C \u201C\u4E00\u4E2A\u566A\u97F3\u53D1\u751F\u5668\uFF1F\u4E3A\u4E86\u4EC0\u4E48\uFF1F\u201D</p><p>\u201C\u5C31\u4E3A\u4E86\u8FD9\u4E2A\uFF0C\u201D \u4E9A\u7EB1\u7F8E\u8BF4\uFF0C \u201C\u8FDC\u7A0B\u4F20\u611F\u5668\u63A2\u5934\u3002\u201D</p><p>\u9676\u8BF4\uFF1A\u201C\u6211\u4EEC\u4EE5\u524D\u4ECE\u672A\u5C1D\u8BD5\u8FC7\u8FD9\u6837\u7684\u4E8B\u60C5\u3002</p><p>\u201C\u5982\u679C\u4F60\u662F\u5B83\u4EEC\uFF0C\u4F60\u6562\u5728\u8FD9\u79CD\u4E1C\u897F\u4E0A\u9762\u8D4C\u4E00\u628A\u561B\uFF1F\u201D \u826F\u5B50\u8F6C\u8FF0\u4E86\u514B\u83B1\u4E3D\u4E1D\u7684\u610F\u601D \u201C\u6211\u4E0D\u4F1A\u3002\u201D</p><p>\u4E9A\u7EB1\u7F8E\u53D1\u51FA\u4E00\u79CD\u4E0D\u6109\u5FEB\u7684\u58F0\u97F3\uFF0C\u663E\u7136\u5979\u6709\u70B9\u6CAE\u4E27\u3002</p><p>\u201C\u5982\u679C\u5B83\u4EEC\u8FD9\u4E48\u5927\u52A8\u5E72\u6208\uFF0C\u90A3\u65E0\u8BBA\u8FD9\u662F\u4EC0\u4E48\u4E1C\u897F\uFF0C\u8FD9\u90FD\u5F88\u91CD\u8981\uFF0C\u201D \u5F17\u62C9\u5FB7\u8BF4\u3002\u201C\u5B83\u4EEC\u751A\u81F3\u90FD\u5728\u53CD\u5236\u4E00\u79CD\u6211\u4EEC\u8FD8\u6CA1\u6709\u7684\u63A2\u6D4B\u65B9\u5F0F\u3002\u201D</p><p>\u201C\u4F60\u8BF4\u8FD9\u662F\u4E00\u79CD\u8109\u51B2\u661F\u81EA\u8F6C\u7684\u53CD\u5C04\uFF0C\u201D \u9676\u8BF4\u3002\u201C\u5230\u5E95\u662F\u4EC0\u4E48\u610F\u601D\uFF1F\u201D</p><p>\u201C\u611F\u89C9\u50CF\u662F\u67D0\u79CD\u5DE8\u5927\u7269\u4F53\u7684\u7F29\u5C0F\u7248\uFF0C\u81EA\u8F6C\u975E\u5E38\u5FEB\uFF0C\u201D \u4E9A\u7EB1\u7F8E\u8BF4\u3002\u201C\u4E0D\u505C\u5730\u590D\u5236\uFF0C\u5C31\u50CF\u53CD\u5C04\u56DE\u58F0\u4E00\u6837\u3002\u201D</p><p>\u201C\u6211\u4EEC\u80FD\u8FC7\u6EE4\u8FD9\u79CD\u4FE1\u53F7\u561B\u201D \u9676\u95EE\u9053\uFF0C\u201C\u4E00\u79CD\u91CD\u590D\u7684\u5F62\u5F0F\uFF0C\u50CF\u56DE\u58F0\u4E00\u6837\u4E0D\u505C\u53CD\u5C04......\u201D</p><p>\u201C\u4E0D\u4EC5\u5982\u6B64\uFF0C\u201D \u4E9A\u7EB1\u7F8E\u8BF4\uFF0C\u201C\u4E0A\u9762\u8FD8\u6709\u70B9\u522B\u7684\u4E1C\u897F\u3002\u4E00\u79CD\u4EBA\u4E3A\u7684\u64CD\u63A7\u3002\u201D</p><p>\u201C\u6211\u53EF\u4EE5\u8BD5\u8BD5\uFF0C\u201D \u5F17\u62C9\u5FB7\u8BF4\uFF0C\u201C\u6211\u5DF2\u7ECF\u8BD5\u8FC7\u4E86\uFF0C\u5728\u4EE5\u524D\u7684\u6570\u636E\u4E0A\uFF0C\u4F46\u662F\u6CA1\u6709\u90A3\u4E48\u8BE6\u7EC6\uFF0C\u800C\u4E14\u6211\u6CA1\u6709\u8FD9\u4E9B\u7EBF\u7D22\u3002\u6B64\u5916\u5173\u4E8E\u60EF\u6027\u7CFB\u62D6\u66F3\u6548\u5E94\u6709\u592A\u591A\u7684\u7684\u4E1C\u897F\u8981\u89E3\u91CA......\u201C</p><p>\u5C31\u50CF\u4E9A\u7EB1\u7F8E\u521A\u624D\u90A3\u6837\uFF0C\u4ED6\u7684\u58F0\u97F3\u6E10\u6E10\u53D8\u5C0F\uFF0C\u5F00\u59CB\u4EE5\u81EA\u5DF1\u7684\u65B9\u5F0F\u601D\u8003\u3002</p><p>\u6240\u6709\u4EBA\u518D\u6B21\u9677\u5165\u6C89\u9ED8\uFF0C\u826F\u5B50\u5B50\u53EA\u542C\u5230\u5934\u76D4\u91CC\u81EA\u5DF1\u5B89\u9759\u7684\u547C\u5438\u58F0\uFF0C\u4E9A\u7EB1\u7F8E\u7528\u624B\u629A\u6478\u7740\u626D\u66F2\u7684\u7403\u4F53\uFF0C\u50CF\u5360\u535C\u5E08\u8BE2\u95EE\u6C34\u6676\u7403\u4E00\u6837\u60F3\u77E5\u9053\u70B9\u8272\u3002\u5916\u661F\u8BBE\u65BD\u4E00\u5982\u65E2\u5F80\u5730\u4FDD\u6301\u7740\u5B83\u7684\u795E\u79D8\u6027\uFF0C\u7136\u800C\uFF0C\u4E00\u4E2A\u65CB\u8F6C\u7684\u7D2B\u8272\u7403\u4F53\u63ED\u5F00\u4E86\u8FD9\u5C42\u9762\u7EB1\u3002\u90A3\u4E1C\u897F\u771F\u7684\u66F4\u50CF\u662F\u4E00\u4E2A\u8D1D\u58F3\uFF0C\u5979\u60F3\u3002</p><p>\u201C\u6211\u53EF\u80FD\u6709\u4E9B\u60F3\u6CD5\uFF0C\u201D \u5F17\u62C9\u5FB7\u6700\u540E\u8BF4\uFF0C\u201C\u6211\u4ECE\u7814\u7A76\u4F1A\u90A3\u513F\u627E\u4E86\u51E0\u4E2A\u7B97\u6CD5\uFF0C\u628A\u4E00\u4E9B\u4E1C\u897F\u62FC\u51D1\u8D77\u6765\u3002\u6211\u6CA1\u6709\u8DB3\u591F\u7684\u672C\u5730\u5904\u7406\u80FD\u529B\u6765\u5B9E\u65F6\u6267\u884C\u6B64\u64CD\u4F5C\uFF0C\u56E0\u6B64\u6211\u8BF7\u6C42\u5728\u6267\u653F\u4F53\u8BA1\u7B97\u96C6\u7FA4\u4E0A\u5360\u7528\u70B9\u65F6\u95F4\u3002\u4ED6\u4EEC\u7ED9\u4E86\u6211\u6743\u9650\u3002\u901A\u8FC7 IIC \u4F20\u9001\u8FD9\u4E48\u591A\u6570\u636E\u5BF9\u6211\u4EEC\u7684\u4FDD\u5BC6\u6027\u6CA1\u6709\u597D\u5904 \u2014\u2014 \u6211\u60F3\u5B9E\u9A8C\u5BA4\u5C06\u4E0D\u5F97\u4E0D\u518D\u6B21\u6539\u53D8\u8F68\u9053\u3002\u4E0D\u7BA1\u600E\u6837\uFF0C\u518D\u7ED9\u6211\u4E00\u70B9\u65F6\u95F4\u7B49\u6211\u628A\u8FD9\u4E2A\u6446\u8BBE\u597D\u2026\u2026\u201D</p><p>\u51E0\u79D2\u949F\u540E\uFF0C\u5979\u4EEC\u770B\u7740\u7684\u7403\u4F53\u7EC8\u4E8E\u5F00\u59CB\u79FB\u52A8\u3002\u56F4\u7ED5\u7740\u8FB9\u7F18\u4F5C\u7684\u6301\u7EED\u8FD0\u52A8\u4F3C\u4E4E\u5E73\u9759\u4E0B\u6765\uFF0C\u7136\u540E\u7F13\u6162\u4E0B\u964D\uFF0C\u7403\u4F53\u4E0A\u51FA\u73B0\u4E86\u6591\u70B9\u72B6\u7684\u892A\u8272\uFF0C\u5E76\u6CA1\u6709\u5448\u73B0\u8C61\u5F81\u7740\u4F4E\u4FE1\u53F7\u7684\u7EFF\u8272\u548C\u7EA2\u8272\uFF0C\u800C\u662F\u76F4\u63A5\u6D88\u5931\u5728 \u865A\u65E0\u4E2D\u3002</p><p>\u201C\u6211\u61C2\u4E86\uFF0C\u201D \u4E9A\u7EB1\u7F8E\u8BF4\uFF0C\u201C\u6240\u4EE5\u8FD9\u4E1C\u897F\u5C31\u662F\u8FD9\u4E48\u8FD0\u4F5C\u7684\u3002\u201D</p><p>\u7403\u4F53\u7A81\u7136\u7F29\u5C0F\uFF0C\u76F4\u5230\u4E0D\u8D85\u8FC7\u539F\u6765\u5927\u5C0F\u7684\u5341\u5206\u4E4B\u4E00\uFF0C\u53E6\u4E00\u4E9B\u7269\u4F53\u51FA\u73B0\u4E86\uFF0C\u4ECD\u7136\u67D3\u4E0A\u4E86\u8868\u793A\u4F20\u611F\u5668\u5F3A\u5EA6\u7684\u865A\u62DF\u8272\u3002\u5B83\u4EEC\u770B\u8D77\u6765\u50CF\u4E00\u4E2A\u4E2A\u5C0F\u73E0\u5B50\u5F62\u6210\u7684\u7403\u5F62\u7F51\u683C\uFF0C\u5728\u4E2D\u95F4\u56F4\u7ED5\u7740\u4E00\u4E2A\u5C0F\u7684\u7403\u4F53\u3002\u4E0D\uFF0C\u8FD9\u66F4\u50CF\u8109\u51B2\u661F\u5468\u56F4\u7684\u7A7A\u95F4\u7AD9\u7F51\u683C\u3002</p><p>\u201C\u54E6\uFF0C\u5BF9\u4E0D\u8D77\uFF0C\u8BA9\u6211\u6539\u4E00\u4E0B\u8BFB\u6570\u3002\u201D \u5F17\u5170\u5FB7\u8BF4\u3002</p><p>\u6301\u7EED\u7740\u7684\u7D2B\u8272\u6D88\u6563\u4E86\uFF0C\u53D6\u800C\u4EE3\u4E4B\u7684\u662F\u66F4\u7CBE\u7F8E\u7684\u8BBE\u65BD\u518D\u73B0\u6A21\u578B\uFF0C\u73B0\u5728\u8FD8\u9644\u6709\u5F17\u62C9\u5FB7\u7684\u6807\u7B7E\u3002\u8BF8\u5982\u8109\u51B2\u661F\uFF0C\u7A7A\u95F4\u7AD9\u2026</p><p>\u201C\u4EE4\u4EBA\u5370\u8C61\u6DF1\u523B\u7684\u7B97\u6CD5\uFF0C\u201D \u9676\u8BC4\u8BBA\u9053\u3002</p><p>\u201C\u4E0D\uFF0C\u4E0D\u662F\uFF0C\u201D \u5F17\u62C9\u5FB7\u8BF4\uFF0C\u201C\u4E0D\u662F\u6211\u505A\u7684\u3002\u5979 \u2014\u2014 \u9E23\u539F\u5C0F\u59D0\uFF0C\u770B\u5230\u53D1\u751F\u4E86\u4EC0\u4E48\uFF0C\u5982\u6CD5\u70AE\u5236\u4E86\u3002\u7B97\u6CD5\u51E0\u4E4E\u6CA1\u6709\u53D1\u6325\u4F5C\u7528\u3002\u201D</p><p>\u826F\u5B50\u628A\u76EE\u5149\u4ECE\u5706\u7403\u4E0A\u8F6C\u5230\u4E86\u4E9A\u7EB1\u7F8E\u8EAB\u4E0A\uFF0C\u5979\u4ECD\u7136\u4E13\u6CE8\u5730\u7AD9\u5728\u5979\u7684\u6F02\u6D6E\u7AD9\u4E0A\u3002\u5979\u4E0D\u77E5\u9053\u8BE5\u600E\u4E48\u5F62\u5BB9\u3002\u5979\u770B\u4E0A\u53BB\u2026 \u8BA9\u4EBA\u656C\u4F69\uFF1F</p><p>\u201C\u8FD9\u4E0D\u4EC5\u4EC5\u662F\u4E00\u4E2A\u9690\u533F\u7CFB\u7EDF\uFF0C\u201D \u963F\u8428\u7C73\u8BF4\uFF0C\u201C\u8FD9\u53EA\u662F\u4F5C\u7528\u4E4B\u4E00\u3002\u4ED6\u4EEC\u5229\u7528\u8109\u51B2\u661F\u7684\u5F15\u529B\u6765\u505A\u4E00\u4E9B\u4E8B\u60C5\u3002\u4F60\u770B\u89C1\u8FD9\u4E9B\u5C0F\u70B9\u5728\u592A\u7A7A\u7AD9\u91CC\u98DE\u6765\u98DE\u53BB\u5417\uFF1F\u5B83\u4EEC\u662F\u98DE\u8239\uFF0C\u800C\u4E14\u6709\u5F88\u591A\u3002\u201D</p><p>\u826F\u5B50\u7167\u4E9A\u7EB1\u7F8E\u7684\u6307\u793A\uFF0C\u770B\u5230\u4E86\u5979\u6240\u8BF4\u7684\u8239\u3002\u5B83\u4EEC\u6E90\u6E90\u4E0D\u65AD\u5730\u8FDB\u51FA\uFF0C\u4F46\u901F\u5EA6\u592A\u6162\uFF0C\u51E0\u4E4E\u4E0D\u80FD\u79F0\u4E4B\u4E3A \u201C\u6D41\u52A8\u201D\u3002</p><p>\u201C\u4ED6\u4EEC\u6CA1\u6709\u4F7F\u7528 FTL \u5F15\u64CE\uFF0C\u201D \u4E9A\u7EB1\u7F8E\u8BF4\uFF0C\u201C\u4E5F\u8BB8\u8FD9\u592A\u5192\u9669\u4E86\uFF0C\u6216\u8005\u4F1A\u5E72\u6270\u7535\u53F0\u7684\u5DE5\u4F5C\u3002\u4F46\u8FD9\u4E9B\u8239\u662F\u5DE1\u6D0B\u8230\u7EA7\u7684\u3002\u5B83\u4EEC\u88C5\u8F7D\u7740\u2026\u2026\u201D</p><p>\u4E9A\u7EB1\u7F8E\u53C8\u505C\u4E86\u4E0B\u6765\uFF0C\u826F\u5B50\u53EA\u80FD\u76EF\u7740\u663E\u793A\u51FA\u6765\u7684\u8239\u53EA\u3002\u4E5F\u8BB8\u8FD9\u662F\u5149\u7684\u4E00\u4E2A\u628A\u620F\uFF0C\u4F46\u8FD9\u4E9B\u8239\u4F3C\u4E4E\u8D8A\u6765\u8D8A\u5927\uFF0C\u8D8A\u6765\u8D8A\u7EC6\u3002</p><p>\u201C\u5B83\u4EEC\u643A\u5E26\u7740\u6052\u661F\u5468\u56F4\u65F6\u7A7A\u7684\u788E\u7247\uFF0C\u201D \u4E9A\u7EB1\u7F8E\u6700\u540E\u8BF4\u3002</p><p>\u201C\u4EC0\u4E48\uFF1F\u643A\u5E26\u65F6\u7A7A\u788E\u7247\uFF1F\u201D \u5F17\u62C9\u5FB7\u9644\u548C\u9053\uFF0C\u201C\u539F\u8C05\u6211\u7684\u8001\u751F\u5E38\u8C08\uFF0C\u4F46\u8FD9\u662F\u4E0D\u53EF\u80FD\u7684\u3002\u201D</p><p>\u201C\u4E0D\u592A\u53EF\u80FD\uFF0C\u201D \u9676\u561F\u561F\u56D4\u56D4\u5730\u8BF4\uFF0C\u201C\u88C5\u8F7D\u8FD9\u6837\u4E00\u4E2A\u4E1C\u897F\u7684\u5BC6\u5C01\u88C5\u7F6E\u51E0\u4E4E\u4E0D\u53EF\u80FD\u5EFA\u9020\uFF0C\u800C\u4E14\u9700\u8981\u8017\u8D39\u5927\u91CF\u7684\u80FD\u91CF\u6765\u9632\u6B62\u5B83\u4E0E\u5468\u56F4\u7A7A\u95F4\u91CD\u65B0\u6574\u5408\u3002\u6211\u4EEC\u5BF9\u8FD9\u4EF6\u4E8B\u505A\u4E86\u51E0\u6B21\u7406\u8BBA\u5B9E\u9A8C\uFF0C\u4F46\u6CA1\u6709\u4EFB\u4F55\u6210\u679C\u3002\u201D</p><p>\u201C\u4EC5\u4EC5\u505A\u8FD9\u6837\u7684\u4E8B\u60C5\uFF0C\u5373\u4F7F\u662F\u6682\u65F6\u7684\uFF0C\u4E5F\u9700\u8981\u51E0\u4E4E\u628A\u4E00\u4E2A\u7531\u7C92\u5B50\u76F8\u4E92\u4F5C\u7528\u7EC4\u6210\u7684\u533A\u57DF\u4E0E\u5B83\u5468\u56F4\u7684\u4E00\u5207\u5B8C\u5168\u963B\u9694\u3002\u5207\u65AD\u6BCF\u4E00\u5904\u7EA0\u7F20\u76F8\u4E92\u4F5C\u7528\u3002\u53EA\u6709\u9ED1\u6D1E\u624D\u80FD\u81EA\u7136\u5730\u505A\u5230\u8FD9\u4E00\u70B9\u3002\u201D</p><p>\u201C\u6211\u6562\u80AF\u5B9A\uFF0C\u201D \u4E9A\u7EB1\u7F8E\u8BF4\uFF0C\u5E26\u7740\u4E00\u4E1D\u5F88\u96BE\u89E3\u91CA\u7684\u4E0D\u5B89 \u201C\u4F46\u6211\u53EA\u80FD\u8BF4\u3002\u8FD9\u79CD\u4E8B\u6211\u51ED\u76F4\u89C9\u7406\u89E3\u3002\u6211\u5FC5\u987B\u8FD9\u4E48\u505A\u3002\u8FD9\u662F\u6211\u81EA\u5DF1\u7684\u80FD\u529B\u3002\u4F60\u81EA\u5DF1\u770B\u770B\u6570\u636E\u5C31\u884C\u4E86\u3002\u201D</p><p>\u201C\u662F\u7684\uFF0C\u201D \u5F17\u62C9\u5FB7\u8BF4\uFF0C\u201C\u8FD9\u7EDD\u5BF9\u7B26\u5408\u4F60\u7684\u7406\u8BBA\u3002\u4F46\u8FD9\u5E76\u4E0D\u80FD\u89E3\u91CA\u4E3A\u4EC0\u4E48\u4ED6\u4EEC\u4F1A\u8FD9\u6837\u505A\u3002\u800C\u4E14\u6570\u91CF\u5982\u6B64\u4E4B\u591A\u3002\u201D</p><p>\u201C\u652F\u914D\u6211\u4EEC\u6240\u5728\u7684\u4E00\u822C\u65F6\u7A7A\u7684\u8DDD\u79BB\u5173\u7CFB\u662F\u5176\u5E95\u5C42\u7ED3\u6784\u7684\u4E00\u4E2A\u7A81\u663E\u7279\u6027\uFF0C\u201D \u9676\u8BF4 \u201C\u5BF9\u5B83\u4EEC\u6765\u8BF4\uFF0C\u8DDD\u79BB\u4E0D\u662F\u771F\u5B9E\u5B58\u5728\u7684\uFF0C\u65F6\u95F4\u53EA\u662F\u65B9\u5411\u7684\u4E0D\u786E\u5B9A\u6027\u3002\u5F85\u5728\u4E00\u4E2A\u4E0D\u77E5\u4F55\u6545\u4E0E\u5176\u4ED6\u4E00\u5207\u90FD\u5206\u79BB\u7684\u65F6\u7A7A\u533A\u57DF\u5185\u81EA\u7136\u4F1A\u60F3\u56DE\u5BB6\u3002\u4F46\u5BB6\u53EF\u4EE5\u5728\u4EFB\u4F55\u5730\u65B9\u3002\u201D</p><p>\u201C\u4F60\u5728\u8BF4\u4EC0\u4E48\uFF1F\u201D \u5F17\u62C9\u5FB7\u95EE\u3002</p><p>\u201C\u6211\u4E0D\u786E\u5B9A\uFF0C\u53EA\u662F\u628A\u6211\u601D\u8003\u7684\u4E1C\u897F\u5927\u58F0\u8BF4\u51FA\u6765\u3002\u201D\u9676\u8BF4\u3002</p><p>\u201C\u90A3\u6211\u4EEC\u5C31\u5148\u6536\u96C6\u5C3D\u53EF\u80FD\u591A\u7684\u6570\u636E\u3002\u201D \u5F17\u62C9\u5FB7\u8BF4\u3002</p><p>\u201C\u6211\u60F3\u4E5F\u8BB8\u662F\u65F6\u5019\u4F11\u606F\u4E00\u4E0B\u4E86\uFF1F\u201D \u9676\u5EFA\u8BAE\u9053\uFF0C\u58F0\u97F3\u91CC\u6709\u4E00\u4E1D\u5FE7\u8651\u3002\u201D \u8FD9\u80AF\u5B9A\u4F1A\u7ED9\u5979\u5E26\u6765\u4E0D\u5C0F\u7684\u538B\u529B\u3002\u201D</p><p>\u201C\u6211\u4E0D\u786E\u5B9A\u6211\u4EEC\u662F\u5426\u80FD\u628A\u4E00\u5207\u7EF4\u6301\u5728\u8FD9\u4E2A\u72B6\u6001\u3002\u201D \u5F17\u62C9\u5FB7\u8BF4\uFF0C\u201C\u8FD9\u5E76\u4E0D\u662F\u4E00\u4E2A\u7B80\u5355\u7684\u8FC7\u7A0B\u3002\u201D</p><p>\u826F\u5B50\u5FC3\u91CC\u4E00\u9635\u9A9A\u52A8\uFF0C\u610F\u8BC6\u5230\u4E00\u79CD\u9707\u60CA\u7684\u5185\u759A\u611F\uFF0C\u5979\u5FD8\u4E86\u68C0\u67E5\u4E9A\u7EB1\u7F8E\u7684\u7075\u9B42\u5B9D\u77F3\u72B6\u6001\uFF0C\u5979\u592A\u4E13\u6CE8\u4E8E\u770B\u7740\u773C\u524D\u7684\u8BD5\u9A8C\u3002</p><p>\u9B54\u529B\u5927\u6982\u4F7F\u7528\u4E86\u4E09\u5206\u4E4B\u4E00\u3002\u8FD8\u5728\u53EF\u4EE5\u63A7\u5236\u7684\u8303\u56F4\u5185\uFF0C\u4F46\u5DF2\u7ECF\u8D85\u51FA\u4E86\u6B63\u5E38\u975E\u6218\u6597\u72B6\u6001\u7684\u91CF\u3002</p><p>\u201C\u54E6\uFF0C\u6211\u4EEC\u4F11\u606F\u4E00\u4F1A\u5427\uFF0C\u201D \u826F\u5B50\u8BF4\uFF0C\u4E9A\u7EB1\u7F8E\u6CA1\u6709\u7ACB\u5373\u4F5C\u51FA\u56DE\u5E94\uFF0C\u201C\u4F60\u8FD8\u80FD\u518D\u6765\u4E00\u6B21\u8FD9\u4E2A\u5B9E\u9A8C\u561B\uFF1F\u201D</p><p>\u4E9A\u7EB1\u7F8E\u5F85\u4E86\u4E00\u4F1A\u624D\u610F\u8BC6\u5230\u8FD9\u53E5\u8BDD\u662F\u5BF9\u5979\u8BF4\u7684\u3002</p><p>\u201C\u54E6\uFF0C\u597D\uFF0C\u597D\u7684\uFF0C\u6211\u60F3\u7ECF\u8FC7\u8FD9\u6B21\u4ECB\u5165\u6211\u5DF2\u7ECF\u77E5\u9053\u5927\u6982\u7684\u60C5\u51B5\u4E86\uFF0C\u201D \u4E9A\u7EB1\u7F8E\u8BF4.\u201C\u4F11\u606F\u4E00\u4E0B\u4E5F\u6CA1\u4EC0\u4E48\u635F\u5931\u3002\u201D</p><p>\u5979\u7684\u58F0\u97F3\u660E\u663E\u5206\u5FC3\u4E86\u3002</p><p>\u201C\u90A3\u6211\u4EEC\u51E0\u4E2A\u5C0F\u65F6\u540E\u5728\u8FD9\u91CC\u91CD\u65B0\u96C6\u7ED3\u3002\u201D \u5F17\u62C9\u5FB7\u8BF4 \u201C\u8FD9\u4E5F\u5C06\u7ED9\u6211\u4EEC\u4E00\u4E9B\u65F6\u95F4\u6765\u68C0\u67E5\u6570\u636E\u548C\u505A\u66F4\u591A\u7684\u5206\u6790\u3002\u6211\u60F3\u8BA9\u4F60\u77E5\u9053\u8FD9\u662F\u4E00\u9879\u5177\u6709\u6218\u7565\u4EF7\u503C\u7684\u5DE5\u4F5C \u2014\u2014 \u6211\u5DF2\u7ECF\u4ECE\u4E0A\u7EA7\u90A3\u91CC\u5F97\u5230\u4E86\u5F88\u591A\u5173\u4E8E\u8FD9\u65B9\u9762\u7684\u8BE2\u95EE\uFF0C\u5982\u679C\u80FD\u591F\u7ED9\u51FA\u67D0\u79CD\u7B54\u6848\u4ED6\u4EEC\u4F1A\u5F88\u6EE1\u610F\u3002\u5DF4\u5143\u5E05\u7279\u522B\u611F\u5174\u8DA3\u3002\u201D</p><p>\u8BBE\u5907\u8BFB\u6570\u6D88\u5931\u5728\u826F\u5B50\u7684\u89C6\u91CE\u4E2D\uFF0C\u4E9A\u7EB1\u7F8E\u6F02\u79BB\u4E86\u8231\u5BA4\u4E2D\u5FC3\u7684\u5F02\u5E38\u5904\u3002\u5F02\u5E38\u73B0\u8C61\u672C\u8EAB\u5F00\u59CB\u6D88\u9000\uFF0C\u826F\u5B50\u611F\u89C9\u5230\u673A\u68B0\u7684\u62C9\u529B\u4E5F\u9010\u6E10\u51CF\u5F31\uFF0C\u540C\u65F6\u5B83\u4EEC\u5F00\u59CB\u6162\u6162\u5730\u5411\u5730\u9762\u6C89\u964D\u3002</p><p>\u4E9A\u7EB1\u7F8E\u5F84\u76F4\u6293\u4F4F\u4E86\u5979\u7684\u624B\u3002\u4E5F\u8BB8\u8FD9\u662F\u5979\u7684\u60F3\u8C61\uFF0C\u4F46\u662F\u4E9A\u7EB1\u7F8E\u770B\u4E0A\u53BB\u6B63\u5FAE\u5FAE\u501A\u9760\u7740\u5979\u3002\u60C5\u51B5\u771F\u7684\u90A3\u4E48\u7CDF\u561B\uFF1F</p><p><strong>\u4F60\u8FD8\u597D\u5417\uFF1F</strong>\u5979\u95EE\u3002</p><p><strong>\u6211\u5F88\u597D\u3002\u6211\u53EA\u662F\u60F3\u89E6\u6478\u4F60\uFF0C\u8FD9\u6837\u5C31\u591F\u4E86\u3002</strong></p><p>\u5979\u4EEC\u5728\u6C14\u9600\u91CC\u7AD9\u4E86\u4E00\u4F1A\uFF0C\u770B\u7740\u5979\u4EEC\u5934\u76D4\u91CC\u7684\u6C14\u538B\u8BFB\u6570\u3002</p><p><strong>\u6709\u70B9\u4E0D\u5BF9\u52B2\uFF0C</strong>\u4E9A\u7EB1\u7F8E\u7A81\u7136\u5FF5\u8BDD\u9053\uFF0C<strong>\u6211\u4ECE\u6CA1\u6709\u6210\u4E3A\u8FC7\u5927\u5BB6\u7684\u7126\u70B9\uFF0C\u4F46\u662F\u73B0\u5728\u4E00\u5207\u90FD\u9760\u6211\u3002\u611F\u89C9\u5C31\u50CF\u6211\u6B63\u5728\u5E72\u4E00\u756A\u5927\u4E8B\uFF0C\u662F\u5427\uFF1F\u8FD9\u611F\u89C9\u5BF9\u6211\u6765\u8BF4\u5F88\u65B0\u9C9C\u3002\u6211\u7406\u89E3\u4E86\u4F60\u559C\u6B22\u8FD9\u6837\u7684\u7406\u7531\uFF0C\u5373\u4FBF\u6211\u6709\u70B9\u88AB\u5413\u5230\u4E86\u3002</strong></p><p>\u826F\u5B50\u70B9\u70B9\u5934\uFF0C\u5979\u7684\u5934\u76D4\u7F13\u7F13\u6253\u5F00\uFF0C\u5979\u4E0D\u77E5\u9053\u8BE5\u8BF4\u4E9B\u4EC0\u4E48\u3002</p><p><strong>\u95EE\u9898\u662F\uFF0C\u90A3\u4E9B\u98DE\u8239\u771F\u7684\u8BA9\u6211\u5F88\u56F0\u6270\uFF0C</strong>\u4E9A\u7EB1\u7F8E\u60F3\uFF0C<strong>\u6211\u89C9\u5F97\u6211\u5E94\u8BE5\u77E5\u9053\u5B83\u4EEC\u5728\u505A\u4EC0\u4E48\uFF0C\u4F46\u73B0\u5728\u8FD8\u60F3\u4E0D\u5230\u3002\u771F\u76F8\u5F88\u8FD1\uFF0C\u5C31\u50CF\u6211\u7684\u624B\u6307\u51E0\u4E4E\u64E6\u8FC7\u4E86\u5B83\uFF0C\u4F46\u540C\u65F6\u6211\u4E5F\u53EA\u80FD\u5230\u8FD9\u4E2A\u5730\u6B65\u3002</strong></p><p>\u826F\u5B50\u8D70\u51FA\u4E86\u6C14\u9600\uFF0C\u5979\u89C9\u5F97\u5979\u4ECE\u6CA1\u89C1\u8FC7\u8FD9\u6837\u7684\u4E9A\u7F8E\u838E\u3002</p><p><strong>\u597D\u5427\uFF0C\u4F60\u8FD8\u53EF\u4EE5\u518D\u6765\u4E00\u6B21\u3002\u6211\u4EEC\u53EA\u8981\u7ED9\u4F60\u7684\u5B9D\u77F3\u5145\u4E2A\u80FD\uFF0C\u518D\u56DE\u8231\u5BA4\u4E00\u6B21\u3002\u8FD9\u4E00\u5207\u7ED3\u675F\u4E4B\u540E\uFF0C\u6211\u4EEC\u5C31\u6709\u8DB3\u591F\u7684\u65F6\u95F4\u53BB\u4EAB\u53D7\u4E86</strong>\u3002</p><p><strong>\u597D\u554A\uFF0C</strong>\u4E9A\u7EB1\u7F8E\u4E0D\u7F6E\u53EF\u5426\u3002</p><p><strong>\u8FD8\u662F\u8BA1\u5212\u4E00\u4E0B\u6211\u4EEC\u7684\u7EA6\u4F1A\uFF1F</strong>\u826F\u5B50\u60F3\u6311\u9017\u4E00\u4E0B\u5979\u3002</p><p><strong>\u518D\u597D\u4E0D\u8FC7\u4E86\uFF0C</strong>\u4E9A\u7EB1\u7F8E\u6709\u6C14\u65E0\u529B\u5730\u56DE\u7B54\uFF0C\u826F\u5B50\u5FC3\u5934\u4E00\u6696\u3002</p><p>\u5F53\u5979\u4EEC\u6362\u8863\u670D\u65F6\u5019\uFF0C\u4E9A\u7EB1\u7F8E\u4ECD\u7136\u663E\u5F97\u5FC3\u70E6\u610F\u4E71\uFF1B\u8FD9\u4E00\u6B21\uFF0C\u5979\u7684\u76EE\u5149\u5E76\u6CA1\u6709\u96C6\u4E2D\u5728\u826F\u5B50\u7684\u8EAB\u4F53\u4E0A\uFF0C\u800C\u662F\u8D8A\u8FC7\u4E86\u5979\uFF0C\u4EFF\u4F5B\u5979\u5B8C\u5168\u5728\u770B\u522B\u7684\u4E1C\u897F\u3002\u8001\u5B9E\u8BF4\uFF0C\u8FD9\u6709\u70B9\u4EE4\u4EBA\u4E0D\u5B89\u3002</p><p>\u5F53\u5979\u4EEC\u5728\u8D70\u5ECA\u91CC\u8D70\u56DE\u6765\uFF0C\u7ECF\u8FC7\u5E15\u7279\u91CC\u590F\u65F6\uFF0C\u4E9A\u7EB1\u7F8E\u7A81\u7136\u6293\u4F4F\u4E86\u826F\u5B50\u7684\u4E0A\u81C2\u3002</p><p>\u201C\u6211\u77E5\u9053\u4E86\uFF0C\u201D \u5979\u8BF4\uFF0C\u201C\u6211\u77E5\u9053\u5B83\u4EEC\u5728\u641E\u4EC0\u4E48\u9B3C\u4E86\u3002\u201D</p><hr><p>\u201C\u5B83\u5728\u4E3A\u5916\u661F\u95EA\u73B0\u6280\u672F\u63D0\u4F9B\u80FD\u91CF\uFF0C\u201D \u4F0F\u7F57\u970D\u592B\u4E3B\u4EFB\u8BF4\u3002\u201C\u8FD9\u662F\u6096\u8BBA\u5F15\u64CE\u6096\u8BBA\u7684\u89E3\u51B3\u65B9\u6848\uFF0C\u4E5F\u662F\u5B83\u4EEC\u95EA\u73B0\u5927\u70AE\u7684\u79D8\u5BC6\u6210\u56E0\u3002\u201D</p><p>\u4ED6\u505C\u987F\u4E86\u4E00\u4E0B\uFF0C\u82B1\u4E86\u70B9\u65F6\u95F4\u8C03\u6574\u4ED6\u7684\u6BDB\u8863\uFF0C\u8FD9\u662F\u6C83\u6D1B\u970D\u592B\u6700\u8457\u540D\u7684\u65F6\u5C1A\u914D\u9970\u4E4B\u4E00\u3002</p><p>\u201C\u6211\u901A\u5E38\u4E0D\u592A\u786E\u5B9A\u8FD9\u4E00\u70B9\uFF0C\u4F46\u9E23\u539F\u4E9A\u7EB1\u7F8E\u8BF4\uFF0C\u5979\u51E0\u4E4E\u767E\u5206\u4E4B\u767E\u786E\u5B9A\uFF0C\u8FD9\u4E0E\u6211\u548C\u9676\u4E3B\u4EFB\u4E00\u76F4\u5728\u505A\u7684\u4E00\u4E9B\u7406\u8BBA\u731C\u6D4B\u662F\u4E00\u81F4\u7684\u3002\u65B0\u7684\u6A21\u62DF\u663E\u793A\uFF0C\u201C\u6316\u6398\u201D \u5E76\u50A8\u5B58\u65F6\u7A7A\u788E\u7247\uFF0C\u5E76\u5C06\u5176\u7528\u4E8E\u8FDC\u7A0B\u4F20\u9001\u81F3\u5C11\u975E\u5E38\u56F0\u96BE\u4F46\u81F3\u5C11\u662F\u53EF\u80FD\u7684\u3002\u4F46\u4ECE\u4EC0\u4E48\u65F6\u5019\u8D77\u5B83\u4EEC\u653B\u514B\u4E86\u8FD9\u9053\u96BE\u5173\uFF1F\u201D</p><p>\u6C83\u6D1B\u970D\u592B\u505C\u5728\u90A3\u91CC\uFF0C\u793C\u8C8C\u5730\u5728\u4F1A\u8BAE\u684C\u4E0A\u7D27\u63E1\u53CC\u624B\uFF0C\u7B49\u5F85\u5927\u5BB6\u7684\u53CD\u9988\u3002</p><p>\u9EBB\u7F8E\u77A5\u4E86\u8D39\u5965\u591A\u7F57\u7EF4\u5947\u548C\u963F\u5357\u5FB7\u4E00\u773C\uFF0C\u5979\u4EEC\u662F\u8FD9\u6B21\u4F1A\u8BAE\u7684\u53E6\u5916\u4E24\u4F4D\u6210\u5458\u3002\u5979\u4EEC\u8138\u8272\u9547\u5B9A\uFF0C\u4F46\u5F88\u4E25\u8083\u3002\u603B\u53C2\u8C0B\u90E8\u5DF2\u4E3A\u6B64\u4E13\u9898\u6210\u7ACB\u4E86\u4E00\u4E2A\u5DE5\u4F5C\u5C0F\u7EC4\u59D4\u5458\u4F1A\uFF0C\u5C31\u5728\u521A\u521A\uFF0C\u5176\u91CD\u8981\u6027\u6025\u5267\u4E0A\u5347\u3002</p><p>\u201C\u4F60\u4EEC\u5BF9\u9E23\u539F\u7684\u5224\u65AD\u6709\u591A\u4FE1\u4EFB\uFF1F\u201D \u9EBB\u7F8E\u95EE\u9053\uFF0C\u4E00\u8FB9\u7ED9\u81EA\u5DF1\u5230\u4E86\u70B9\u865A\u62DF\u7EA2\u8336\u3002\u201C\u5982\u679C\u6CA1\u6709\u5979\u7684\u5EFA\u8BAE\u4F60\u4EEC\u5230\u5E95\u4F1A\u600E\u4E48\u60F3\uFF1F\u201D</p><p>\u201C\u53EA\u6709\u5927\u6982\u767E\u5206\u4E4B\u4E8C\u5341\uFF0C\u201D \u6C83\u6D1B\u970D\u592B\u8BF4\uFF0C\u201C\u73B0\u5728\u4ECD\u6709\u592A\u591A\u7684\u672A\u77E5\u6570\u3002\u4F46\u662F\u6709\u76F8\u5173\u4E13\u4E1A\u80CC\u666F\u7684\u9B54\u6CD5\u5C11\u5973\u4EEC\u9884\u8A00\u7684\u5386\u53F2\u8BB0\u5F55\u6CA1\u4EC0\u4E48\u95EE\u9898\uFF0C\u5C24\u5176\u662F\u5F53\u4ED6\u4EEC\u65E0\u6CD5\u6E05\u9664\u5730\u8BF4\u51FA\u903B\u8F91\u4E0A\u7684\u539F\u56E0\u65F6\u3002\u4F60\u53EF\u4EE5\u68C0\u67E5\u8BB0\u5F55\u4E2D\u7684\u7EDF\u8BA1\u6570\u636E\u3002\u201D</p><p>\u4ED6\u62FF\u51FA\u4E00\u4E2A\u8336\u676F\uFF0C\u5F00\u59CB\u7ED9\u81EA\u5DF1\u5012\u8336\uFF0C\u8BA9\u9EBB\u7F8E\u5927\u5403\u4E00\u60CA\u3002\u5927\u591A\u6570\u4EBA\u5DE5\u667A\u80FD\u90FD\u5F3A\u8C03\u4ECE\u4E0D\u98DF\u7528\u4EBA\u7C7B\u98DF\u7269\uFF0C\u4EE5\u786E\u4FDD\u5B83\u4EEC\u6C38\u8FDC\u4E0D\u4F1A\u88AB\u4EBA\u7C7B\u6240\u8FF7\u60D1\u3002</p><p>\u9EBB\u7F8E\u60F3\u4E86\u60F3\uFF0C\u62BF\u4E86\u4E00\u53E3\u8336\u3002\u8001\u5B9E\u8BF4\uFF0C\u5979\u4E0D\u9700\u8981\u6C83\u7F57\u970D\u592B\u544A\u8BC9\u5979\u8FD9\u4E00\u70B9\uFF0C\u4E5F\u4E0D\u9700\u8981\u6838\u5BF9\u6570\u636E\u3002\u5979\u5F88\u6E05\u695A\u3002\u4F46\u662F\uFF0C\u5BF9\u4E00\u4E2A\u5973\u5B69\u6709\u8FD9\u6837\u7684\u4FE1\u5FC3\u2026\u2026</p><p>\u201C\u6055\u6211\u76F4\u8A00\uFF0C\u9EBB\u7F8E\uFF0C\u8FD9\u4E0D\u662F\u4F60\u7684\u4E13\u957F\u5417\uFF1F\u201D \u8D39\u5965\u591A\u7F57\u7EF4\u5947\u7AEF\u7740\u4E00\u676F\u5496\u5561\u95EE\u9053\uFF0C\u201C\u4E5F\u8BB8\u4F60\u53EF\u4EE5\u548C\u5979\u8C08\u8C08\uFF1F\u201D</p><p>\u201C\u6211\u5927\u6982\u4F1A\u7684\uFF0C\u201D \u9EBB\u7F8E\u8BF4\uFF0C\u201C\u6BD5\u7ADF\u5979\u73B0\u5728\u5728\u6211\u7684\u6307\u6325\u4E0B\u3002\u201D</p><p>\u201C\u6211\u5F88\u4E50\u610F\u8FDB\u884C\u66F4\u591A\u7684\u5B9E\u9A8C\u548C\u6A21\u62DF\u3002\u201D \u6C83\u6D1B\u970D\u592B\u8BF4\uFF0C\u201C\u4E8B\u5B9E\u4E0A\uFF0C\u4ED6\u4EEC\u6B63\u5728\u8FDB\u884C\u8FD9\u79CD\u52AA\u529B\u3002\u201D</p><p>\u9EBB\u7F8E\u65E0\u8A00\u5730\u70B9\u70B9\u5934\uFF0C\u7ED9\u673A\u68B0\u5A18\u53D1\u4E86\u4E2A\u7B80\u8BAF\uFF0C\u60F3\u8BA9\u4E9A\u7EB1\u7F8E\u5347\u804C\u3002\u8003\u8651\u5230\u6700\u8FD1\u51E0\u5468\u5728 Adept Blue \u53D1\u751F\u7684\u4E8B\u60C5\u7684\u4E25\u91CD\u6027\uFF0C\u8FD9\u4E48\u505A\u4F3C\u4E4E\u624D\u662F\u5408\u9002\u7684\u3002</p><p>\u201C\u73B0\u5728\uFF0C\u8BA9\u6211\u4EEC\u5047\u8BBE\u5979\u662F\u5BF9\u7684\uFF0C\u201D \u5979\u8BF4\uFF0C\u201C\u540E\u679C\u662F\u4EC0\u4E48\uFF1F\u6211\u4EEC\u8981\u505A\u4EC0\u4E48\u5417\uFF1F\u201D</p><p>\u201C\u6211\u4EEC\u9700\u8981\u4E00\u4E9B\u53EF\u4EE5\u4E89\u53D6\u65F6\u95F4\u7684\u63AA\u65BD\uFF0C\u62D6\u5EF6\u5B83\u4EEC\u7684\u8BA1\u5212\uFF0C\u201D \u963F\u5357\u8BF4\uFF0C\u201C\u50CF\u8FD9\u6837\u7684\u8FD0\u8F93\u4E2D\u5FC3\u662F\u5B8C\u7F8E\u7684\u76EE\u6807\uFF0C\u5982\u679C\u6211\u4EEC\u80FD\u5BF9\u5B83\u505A\u70B9\u4EC0\u4E48\u7684\u8BDD\uFF0C\u5047\u8BBE\u8FD9\u662F\u8BE5\u5730\u533A\u552F\u4E00\u7684\u4E00\u4E2A\u8FD9\u6837\u7684\u8FD0\u8F93\u4E2D\u5FC3\u3002\u201D</p><p>\u5979\u770B\u5411\u6C83\u6D1B\u970D\u592B\uFF0C\u8FD9\u4E2A\u542B\u84C4\u7684\u95EE\u9898\u663E\u800C\u6613\u89C1\u3002</p><p>\u4ED6\u8BF4\uFF1A\u201C\u636E\u6211\u4EEC\u6240\u77E5\uFF0C\u8FD9\u662F\u552F\u4E00\u4E00\u4E2A\u8FDC\u79BB\u5E7C\u53D1\u62C9\u5E95\u6218\u533A\u7684\u8FD0\u8F93\u4E2D\u5FC3\u3002\u201D \u4E8B\u5B9E\u4E0A\uFF0C\u6211\u4EEC\u751A\u81F3\u8FD8\u6CA1\u6709\u627E\u5230\u7B2C\u4E8C\u4E2A\u3002\u6709\u53EF\u80FD\u5728\u6267\u653F\u4F53\u7A7A\u95F4\u9644\u8FD1\u4E5F\u4E0D\u4F1A\u6709\u4E86\uFF0C\u56E0\u4E3A\u6211\u4E2A\u4EBA\u6000\u7591\uFF0C\u6211\u4EEC\u53EF\u80FD\u4F1A\u9519\u8FC7\u5B83\u4EEC\uFF0C\u5C3D\u7BA1\u6211\u4EEC\u4ECD\u5728\u5BFB\u627E\u3002\u552F\u4E00\u4E00\u4E2A\u5408\u7406\u7684\u539F\u56E0\u662F\uFF1A\u5B83\u4EEC\u57FA\u7840\u8BBE\u65BD\u548C\u5EFA\u7B51\u4E2D\u6D89\u53CA\u7684\u65F6\u7A7A\u64CD\u7EB5\u7684\u89C4\u6A21\u8BA9\u4EBA\u96BE\u4EE5\u60F3\u8C61\u3002\u5373\u4F7F\u662F\u5728\u4E3A\u4E86\u62B5\u5FA1\u9644\u8FD1\u8109\u51B2\u661F\u6F6E\u6C50\u529B\u6240\u9700\u7684\u9632\u62A4\u7F69\u4E5F\u96BE\u4EE5\u7F6E\u4FE1 \u3002\u201D</p><p>\u201C\u5B83\u4ECD\u5728\u5916\u661F\u592A\u7A7A\u76F8\u5F53\u6DF1\u7684\u5730\u65B9\uFF0C\u201D \u8D39\u5965\u591A\u7F57\u7EF4\u5947\u8BF4\uFF0C\u201C\u8FD9\u4E0D\u4EC5\u4EC5\u662F\u8F68\u9053\u8BBE\u65BD\u3002\u8FD9\u662F\u4E00\u4E2A\u8BBE\u65BD\u9F50\u5168\u7684\u661F\u5EA7\uFF0C\u62E5\u6709\u672A\u77E5\u7684\u80FD\u529B\u548C\u53CA\u5176\u79BB\u8C31\u7684\u5C4F\u969C\u3002\u5982\u679C\u8D4C\u6CE8\u4E0D\u90A3\u4E48\u9AD8\u7684\u8BDD\uFF0C\u5C31\u7B97\u8003\u8651\u53D1\u52A8\u653B\u51FB\u4E5F\u6709\u70B9\u8352\u5510\u3002\u201D</p><p>\u201C\u597D\u5427\uFF0C\u8FD9\u662F\u6211\u4EEC\u4E00\u76F4\u5728\u60F3\u7684\u4E00\u4EF6\u4E8B\uFF0C\u201D \u6C83\u6D1B\u970D\u592B\u63C9\u7740\u8138\u988A\u8BF4\uFF0C\u201C\u4F5C\u4E3A\u519B\u4E8B\u4E8B\u52A1\u7684\u5916\u884C\uFF0C\u4F60\u660E\u767D\u7684\u3002\u5916\u661F\u4EBA\u82B1\u4E86\u76F8\u5F53\u5927\u7684\u52AA\u529B\u5411\u6211\u4EEC\u9690\u7792\u4ED6\u4EEC\u884C\u52A8\u7684\u771F\u6B63\u76EE\u7684\uFF0C\u751A\u81F3\u8003\u8651\u5230\u4E86\u4E00\u79CD\u6211\u4EEC\u8FD8\u672A\u62E5\u6709\u7684\u4FA6\u67E5\u5F62\u5F0F\u3002\u4ED6\u4EEC\u663E\u7136\u5F88\u62C5\u5FC3\u3002\u201D</p><p>\u201C\u8FD9\u53EF\u80FD\u662F\u4E00\u79CD\u5408\u7406\u7684\u504F\u6267\uFF0C\u201D \u963F\u5357\u5FB7\u6325\u7740\u624B\u8BF4\uFF0C\u201C\u5B83\u4EEC\u5BF9\u6211\u4EEC\u7684\u6280\u672F\u6C34\u5E73\u6709\u4E00\u5B9A\u7684\u4E86\u89E3\uFF0C\u53EF\u80FD\u5BF9\u6211\u4EEC\u5728\u4E0D\u4E45\u7684\u5C06\u6765\u7684\u53D1\u5C55\u65B9\u5411\u6709\u4E0D\u9519\u7684\u731C\u6D4B\u3002\u8FD9\u662F\u4E00\u4E2A\u5408\u7406\u7684\u5047\u8BBE\uFF0C\u7279\u522B\u662F\u8003\u8651\u5230\u6211\u4EEC\u6709\u8FC7\u53BB\u5E38\u5E38\u7ED9\u4ED6\u4EEC\u9020\u6210\u60CA\u559C\u7684\u2018\u738B\u724C\u2019\u3002\u201D</p><p>\u201C\u53EA\u662F\u731C\u6D4B\uFF0C\u201D \u6C83\u6D1B\u970D\u592B\u8BF4\u3002</p><p>\u201C\u6211\u4F1A\u8BA9\u519B\u4E8B\u987E\u95EE\u8FDB\u884C\u4E00\u4E9B\u6A21\u62DF\uFF0C\u201D \u8D39\u5965\u591A\u7F57\u7EF4\u5947\u8BF4\uFF0C\u201C\u4F46\u6211\u7684\u731C\u6D4B\u662F\uFF0C\u5BF9\u8FD9\u4E9B\u8109\u51B2\u661F\u77FF\u573A\u8FDB\u884C\u5E38\u89C4\u7684\u8230\u961F\u4F5C\u6218\u5C06\u975E\u5E38\u56F0\u96BE\u3002\u8230\u961F\u6839\u672C\u4E0D\u662F\u4E3A\u8FD9\u79CD\u89C4\u6A21\u7684\u6DF1\u5EA6\u5165\u4FB5\u800C\u5EFA\u9020\u7684\uFF0C\u5916\u661F\u4EBA\u8FD9\u6B21\u4E5F\u4E0D\u662F\u6BEB\u65E0\u51C6\u5907\u3002\u6211\u4EEC\u9700\u8981\u4ECE\u4E00\u4E9B\u65B0\u7684\u89C6\u89D2\u51FA\u53D1\u3002\u201D</p><p>\u201C\u6211\u4EEC\u8981\u628A\u8FD9\u4E2A\u6D88\u606F\u5E26\u7ED9\u5168\u4F53\u4EBA\u5458\u5417\uFF1F\u201D \u963F\u5357\u5FB7\u95EE\u9053 \u201C\u5373\u4F7F\u6211\u4EEC\u4E0D\u80FD\u5B9E\u65BD\u6253\u51FB\uFF0C\u5BF9\u5916\u661F\u4EBA\u7684\u95EA\u73B0\u6280\u672F\u7684\u4EFB\u4F55\u65B0\u89C1\u89E3\u90FD\u5E94\u8BE5\u503C\u5F97\u6309\u7167\u8230\u961F\u539F\u5219\u8FDB\u884C\u5F7B\u5E95\u5BA1\u67E5\u3002\u201D</p><p>\u9EBB\u7F8E\u628A\u624B\u653E\u5728\u684C\u5B50\u4E0A\uFF0C\u95ED\u4E0A\u773C\u775B\u4E00\u4F1A\u513F\u3002</p><p>\u201C\u6700\u540E\u518D\u8FD9\u6837\u5427\uFF0C\u201D \u5979\u8BF4\uFF0C\u201C\u4F46\u4E0D\u662F\u73B0\u5728\u3002\u6211\u89C9\u5F97\u6211\u4EEC\u518D\u505A\u4E00\u70B9\u8C03\u67E5\u5427\u3002\u201D</p><p>\u963F\u5357\u5FB7\u76B1\u8D77\u4E86\u7709\u5934\uFF0C\u53EA\u662F\u4E00\u70B9\u70B9\u5FAE\u5C0F\u7684\u52A8\u4F5C\uFF0C\u8FD9\u662F\u5979\u751A\u81F3\u6CA1\u60F3\u5230\u9EBB\u7F8E\u4F1A\u6CE8\u610F\u5230\u7684\u3002</p><p>\u201C\u597D\u5427\uFF0C\u6211\u60F3\u6211\u4EEC\u8D1F\u62C5\u5F97\u8D77\uFF0C\u201D \u5979\u8BF4\u3002</p><p>\u9EBB\u7F8E\u77E5\u9053\u963F\u5357\u5FB7\u5728\u62C5\u5FC3\u4EC0\u4E48\uFF0C\u4F46\u6709\u4EF6\u4E8B\u662F\u53E6\u4E00\u4E2A\u5973\u4EBA\u6240\u4E0D\u77E5\u9053\u7684\u3002</p><p>\u5979\u671B\u5411\u522B\u5904\uFF0C\u5047\u88C5\u5728\u68C0\u67E5\u5185\u90E8\u6570\u636E\u6D41\u3002</p><p>\u5979\u8FD8\u9700\u8981\u53C2\u4E0E\u4E00\u4EF6\u66F4\u5927\u7684\u4E8B\u60C5\u3002\u4E00\u5E45\u66F4\u5927\u7684\u56FE\u666F\uFF0C\u5728\u8FD9\u5E45\u753B\u9762\u4E2D\uFF0C\u7130\u548C\u674F\u5B50\u7684\u5973\u795E\u51C6\u786E\u5730\u9884\u8A00\u4E86\u5916\u661F\u7AE0\u9C7C\u6D3B\u52A8\u7684\u4E2D\u65AD\uFF0C\u5E76\u5728\u5047\u671F\u7ED3\u675F\u540E\u8981\u6C42\u5979\u518D\u6B21\u9020\u8BBF\u3002</p><p>\u5979\u5DF2\u7ECF\u5728\u8FD4\u56DE\u5730\u7403\u7684\u8DEF\u4E0A\u4E86\uFF0C\u5F53\u5979\u5230\u8FBE\u90A3\u91CC\u65F6\uFF0C\u5979\u6709\u4E00\u4E9B\u95EE\u9898\u60F3\u95EE\u3002\u5173\u4E8E\u672A\u6765\uFF0C\u6218\u4E89\u7684\u6027\u8D28\uFF0C\u548C\u7130\u53D1\u751F\u4E86\u4EC0\u4E48\uFF0C\u751F\u547D\u7684\u610F\u4E49\u2026 \u6240\u6709\u4EBA\u4EEC\u53EF\u80FD\u60F3\u95EE\u4E00\u4E2A\u6240\u8C13\u7684\u795E\u7684\u4E8B\u60C5\u3002\u5979\u6000\u7591\u81EA\u5DF1\u4F1A\u5F97\u5230\u5F88\u591A\u7B54\u6848\uFF0C\u4F46\u5973\u795E\u4E0D\u4F1A\u8BA9\u2026\u2026 \u5979\u8FD9\u4E48\u5BB9\u6613\u5C31\u4ECE\u56F0\u6270\u4E2D\u6446\u8131\u51FA\u6765\u3002</p><p>\u66F4\u5B9E\u9645\u5730\u8BF4\uFF0C\u5979\u6709\u4E00\u79CD\u611F\u89C9\uFF0C\u6709\u4E00\u6761\u901A\u5F80\u795E\u7684\u70ED\u7EBF\uFF0C\u5BF9\u603B\u53C2\u8C0B\u90E8\u7684\u4E3B\u5E2D\u6765\u8BF4\u662F\u4E00\u4EF6\u6709\u7528\u7684\u4E8B\u60C5\u3002</p><p>\u201C\u6211\u60F3\u4F1A\u8BAE\u5DF2\u7ECF\u4F11\u4F1A\u4E86\uFF0C\u201D \u5979\u8BF4\uFF0C\u201C\u4E0B\u6B21\u89C1\u3002\u201D</p><p>\u5F53\u6A21\u62DF\u5728\u5979\u5468\u56F4\u6D88\u5931\u65F6\uFF0C\u5979\u5FC3\u4E0D\u5728\u7109\u5730\u5728\u624B\u6307\u4E0A\u6469\u64E6\u7740\u7075\u9B42\u7684\u5B9D\u77F3\u3002</p><hr><p>\u66FE\u7ECF\uFF0C\u5F53\u5979\u5E74\u8F7B\u7684\u65F6\u5019\uFF0C\u9EBB\u7F8E\u53EA\u60F3\u4FDD\u4F4F\u81EA\u5DF1\u7684\u751F\u547D\u3002\u5979\u7684\u613F\u671B\u548C\u7075\u9B42\u5B9D\u77F3\u90FD\u80FD\u8BC1\u660E\u8FD9\u4E00\u70B9\u3002</p><p>\u5979\u7ACB\u523B\u540E\u6094\u4E86\u3002\u5F53\u7136\uFF0C\u4E0D\u662F\u540E\u6094\u6D3B\u4E0B\u6765\uFF0C\u800C\u662F\u56E0\u4E3A\u5979\u6CA1\u6709\u518D\u591A\u8BB8\u70B9\u613F\u3002\u4E5F\u8BB8\u5979\u7684\u7236\u6BCD\u53EF\u4EE5\u6D3B\u7740\uFF1F</p><p>\u5F53\u65F6\u5F88\u591A\u5973\u5B69\u90FD\u4F1A\u8FF7\u5931\u81EA\u6211\uFF0C\u4F46\u6700\u91CD\u8981\u7684\u662F\uFF0C\u662F\u4E18\u6BD4\u8BF4\u670D\u4E86\u5979\u3002\u63D0\u9192\u5979\uFF0C\u751F\u547D\u8FD8\u662F\u503C\u5F97\u7684\uFF0C\u5979\u662F\u4E00\u4E2A\u82F1\u96C4\uFF0C\u9664\u4E86\u5979\u6536\u96C6\u7684\u60B2\u53F9\u4E4B\u79CD\uFF0C\u6CA1\u6709\u522B\u7684\u4E1C\u897F\u80FD\u9632\u6B62\u5B87\u5B99\u7684\u70ED\u5BC2\u3002</p><p>\u60F3\u5230\u8FD9\u4E2A\uFF0C\u5979\u7684\u5634\u5507\u603B\u662F\u5411\u4E0A\u62BD\u52A8\u3002\u5979\u73B0\u5728\u77E5\u9053\uFF0C\u5B75\u5316\u8005\u5176\u5B9E\u5E76\u4E0D\u5728\u4E4E\uFF0C\u5728\u5B83\u4EEC\u773C\u91CC\uFF0C\u8FD9\u90FD\u662F\u4E00\u573A\u5DE8\u5927\u7684\u6295\u8D44\u6E38\u620F\uFF0C\u66F4\u591A\u9B54\u6CD5\u5C11\u5973\u6D3B\u7740\u610F\u5473\u7740\u66F4\u591A\u7684\u60B2\u53F9\u4E4B\u5757\u7684\u6536\u96C6\u3002\u5982\u679C\u5979\u591F\u8BDA\u5B9E\u7684\u8BDD\uFF0C\u90A3\u5979\u65E9\u5C31\u77E5\u9053\u4E86\u3002\u4F46\u77E5\u9053\u6709\u4EBA\u5173\u5FC3\u81EA\u5DF1\u7684\u611F\u89C9\u8FD8\u662F\u4E0D\u9519\u7684\u3002\u5982\u6B64\u5173\u5207\u4EE5\u81F3\u4E8E\u5728\u5979\u8033\u8FB9\u8F7B\u58F0\u8BF4\u51FA\u6709\u4E9B\u7578\u5F62\u7684\u5B89\u6170\u7684\u8BDD\u3002</p><p>\u5F53\u548C\u7130\u5206\u4EAB\u90A3\u4E2A\u6545\u4E8B\u65F6\uFF0C\u5979\u662F\u591A\u4E48\u5730\u8BBD\u523A\uFF01\u5979\u4EE5\u5979\u5178\u578B\u7684\u4EE4\u4EBA\u4E0D\u5B89\u7684\u4FDD\u8BC1\u79F0\uFF0C\u9EBB\u7F8E\u65E0\u8BBA\u5982\u4F55\u90FD\u4F1A\u597D\u8D77\u6765\u7684\uFF0C\u81F3\u5C11\u4F1A\u597D\u4E00\u6BB5\u65F6\u95F4\u3002</p><p>\u5728\u67D0\u79CD\u7A0B\u5EA6\u4E0A\uFF0C\u90A3\u65F6\u7684\u751F\u6D3B\u66F4\u7B80\u5355\u4E86\u3002\u5979\u6709\u4E00\u4E2A\u76EE\u6807\uFF0C\u4E00\u4E2A\u76F8\u5BF9\u7B80\u5355\u7684\u76EE\u6807\uFF0C\u4E00\u65E6\u5979\u586B\u8865\u4E86\u5FC3\u4E2D\u7684\u7A7A\u767D\uFF0C\u5C31\u522B\u65E0\u5B83\u6C42\u4E86\u3002</p><p>\u73B0\u5728\u60C5\u51B5\u4E0D\u540C\u4E86\u3002</p><p>\u5979\u5411\u4E0A\u770B\u4E86\u770B\uFF0C\u628A\u773C\u775B\u906E\u4F4F\u4E86\u900F\u8FC7\u674F\u5B50\u6559\u5802\u5F69\u8272\u73BB\u7483\u7684\u5FAE\u5F31\u5149\u7EBF\u3002\u8FD9\u79CD\u98CE\u683C\u5316\u7684\u753B\u50CF\uFF0C\u7B2C\u4E00\u6B21\u8BA9\u5979\u89C9\u5F97\u6709\u70B9\u9738\u9053\uFF0C\u4F46\u73B0\u5728\u4F3C\u4E4E\u53E6\u6709\u6DF1\u610F\uFF0C\u867D\u7136\u8FD8\u662F\u6709\u70B9\u62BD\u8C61\u3002\u4ECE\u7EDD\u671B\u548C\u5E0C\u671B\u7684\u89D2\u5EA6\u6765\u770B\u5F85\u751F\u6D3B\u662F\u4E00\u56DE\u4E8B\uFF0C\u5C31\u50CF\u9B54\u6CD5\u5C11\u5973\u7ECF\u5E38\u4E0D\u5F97\u4E0D\u505A\u7684\u90A3\u6837\uFF0C\u4F46\u5979\u5FC5\u987B\u9762\u5BF9\u7684\u5F80\u5F80\u662F\u8FDC\u79BB\u90A3\u79CD\u60C5\u7EEA\u7684\u3002</p><p>\u5728\u5979\u7684\u8981\u6C42\u4E0B\uFF0C\u674F\u5B50\u548C\u5176\u4ED6\u4E00\u4E9B\u4EBA\u518D\u6B21\u6E05\u7406\u4E86\u5927\u5385\uFF0C\u628A\u5979\u5077\u5077\u5E26\u5230\u4EFB\u4F55\u5979\u6216\u5979\u4EEC\u8BA4\u4E3A\u5979\u5728\u8FD9\u91CC\u54A8\u8BE2\u7684\u5730\u65B9\uFF0C\u5979\u4E0D\u80FD\u8BA9\u4EFB\u4F55\u6D88\u606F\u6CC4\u9732\u5979\u5728\u8FD9\u91CC\uFF0C\u81F3\u5C11\u4E0D\u80FD\u4EE5\u4EFB\u4F55\u53EF\u8BC1\u5B9E\u7684\u65B9\u5F0F\u3002\u4E0D\u8FC7\uFF0C\u5982\u679C\u5979\u5728\u8FD9\u91CC\u5446\u5F97\u592A\u4E45\u3001\u592A\u9891\u7E41\uFF0C\u8FD9\u4E2A\u79D8\u5BC6\u662F\u4E0D\u53EF\u80FD\u4FDD\u5B88\u7684\u3002\u6709\u592A\u591A\u597D\u5947\u7684\u9B54\u6CD5\u5973\u5B69\u64C5\u957F\u505A\u95F4\u8C0D\u3002</p><p>\u5979\u62CD\u4E86\u62CD\u5979\u6700\u559C\u6B22\u7684\u4E18\u6BD4\u7684\u5934\uFF0C\u4E18\u6BD4\u51B3\u5B9A\u5047\u88C5\u6210\u5728\u5979\u80A9\u8180\u4E0A\u7761\u7740\u7684\u6837\u5B50\u3002\u5B83\u505A\u4E86\u8FD9\u6837\u53EF\u7231\u7684\u59FF\u52BF\u6765\u964D\u4F4E\u5468\u56F4\u4EBA\u7C7B\u7684\u6212\u5907\uFF0C\u7C7B\u4F3C\u4E8E\u4E3A\u4EC0\u4E48\u7531\u9EBB\u4FDD\u7559\u4E86\u4E00\u4E2A\u5B69\u5B50\u822C\u7684\u8EAB\u4F53\u3002\u5373\u4F7F\u6BCF\u4E2A\u4EBA\u90FD\u77E5\u9053\u600E\u4E48\u56DE\u4E8B\uFF0C\u8FD9\u4ECD\u7136\u6709\u70B9\u8D77\u4F5C\u7528\u3002</p><p>\u5979\u53F9\u4E86\u53E3\u6C14\uFF0C\u8D70\u4E0A\u4E86\u7F0E\u5E26\u524D\u7684\u53F0\u9636\u3002\u5979\u6765\u8FD9\u91CC\u4F3C\u4E4E\u662F\u88AB\u8FEB\u7684\uFF0C\u800C\u4E14\u62D2\u7EDD\u4F3C\u4E4E\u5E76\u4E0D\u660E\u667A\uFF0C\u4F46\u4E8B\u5B9E\u4E0A\uFF0C\u5979\u6765\u8FD9\u91CC\u662F\u56E0\u4E3A\u5979\u60F3\u56DE\u6765\u3002\u8FD9\u5BF9\u4E00\u4E2A\u519B\u4E8B\u6218\u7565\u5BB6\u6765\u8BF4\u662F\u5982\u6B64\u4EE4\u4EBA\u5174\u594B\uFF0C\u8FD9\u4E0D\u4EC5\u4EC5\u662F\u5173\u4E8E\u5BF9\u672A\u6765\u7684\u524D\u666F\u4E0E\u660E\u786E\u56DE\u7B54\u7684\u5C55\u671B\u3002\u5979\u6709\u7740\u4EFB\u4F55\u4EBA\u90FD\u60F3\u95EE\u7684\u95EE\u9898\uFF0C\u5C3D\u7BA1\u5979\u5BF9\u6B64\u8868\u793A\u6000\u7591\uFF0C\u4F46\u5979\u4E0D\u53EF\u907F\u514D\u5730\u6E34\u671B\u77E5\u6653\u672A\u6765\u3002</p><p>\u5F53\u5979\u8D70\u8FD1\u76DB\u653E\u7F0E\u5E26\u7684\u5E95\u5EA7\u65F6\uFF0C\u4E18\u6BD4\u4ECE\u80A9\u4E0A\u8DF3\u4E86\u4E0B\u6765\uFF0C\u843D\u5728\u4E0B\u9762\u67D4\u8F6F\u7684\u7EA2\u6BEF\u4E0A\u3002</p><p><strong>\u6211\u4EE5\u4E3A\u4F60\u5728\u8FD9\u513F\u770B\u5230\u6211\u4F1A\u5F88\u60CA\u8BB6\u7684\uFF0C</strong>\u9EBB\u7F8E\u4F4E\u5934\u770B\u7740\u90A3\u4E2A\u751F\u7269\u5FC3\u60F3\u3002</p><p><strong>\u4E5F\u6CA1\u7279\u522B\u60CA\u8BB6\uFF0C</strong>\u4E18\u6BD4\u5FF5\u8BDD\u9053\uFF0C<strong>\u8FD9\u4EF6\u827A\u672F\u54C1\u5BF9\u9B54\u6CD5\u5C11\u5973\u6709\u7740\u8FF7\u4EBA\u7684\u5438\u5F15\u529B\uFF0C\u7279\u522B\u662F\u90A3\u4E9B\u53C2\u4E0E\u5173\u952E\u4E8B\u4EF6\u7684\u5973\u5B69\u3002\u6211\u60F3\u4F60\u5F88\u53EF\u80FD\u6700\u7EC8\u4F1A\u88AB\u5438\u5F15\u5230\u8FD9\u91CC\u6765\u3002</strong></p><p><strong>\u4F60\u6700\u8FD1\u8BF4\u8FC7\u4E00\u6B21\uFF0C\u674F\u5B50\u548C\u7130\u53EF\u80FD\u662F\u5BF9\u7684\uFF0C</strong>\u9EBB\u7F8E\u8BF4\uFF0C<strong>\u6211\u60F3\u8FD9\u5C31\u662F\u539F\u56E0\u3002</strong></p><p><strong>\u8FD9\u662F\u4E2A\u76F8\u5F53\u9760\u4E0D\u4F4F\u7684\u8BC1\u636E\uFF0C\u4F46\u8FD9\u5C31\u662F\u8BC1\u636E\uFF0C\u4E00\u822C.....</strong> \u4E18\u6BD4\u8BF4\uFF0C <strong>\u66F4\u6709\u8DA3\u7684\u662F\uFF0C\u90A3\u4E9B\u6765\u8FC7\u8FD9\u513F\u7684\u5C11\u5973\u90FD\u5728\u540E\u6765\u6709\u8FC7\u5947\u602A\u7684\u8868\u73B0\u3002</strong></p><p><strong>\u4F60\u4EEC\u603B\u662F\u90A3\u4E48\u8C28\u614E\u3002\u4F60\u8BD5\u8FC7\u81EA\u5DF1\u68C0\u67E5\u7F0E\u5E26\u5417\uFF1F\u5F53\u7136\uFF0C\u5B83\u88AB\u5BC6\u5C01\u4E86\uFF0C\u4F46\u5BF9\u50CF\u4F60\u8FD9\u6837\u7684\u751F\u7269\u6765\u8BF4\uFF0C\u5B83\u4E0D\u53EF\u80FD\u90A3\u4E48\u96BE\u3002</strong></p><p>\u4E18\u6BD4\u5728\u5730\u677F\u4E0A\u8F6C\u4E86\u5708\u3002</p><p><strong>\u8FD9\u6761\u7F0E\u5E26\u88AB\u8FDC\u8D85\u4E8E\u4F60\u4EEC\u7684\u6280\u672F\u4FDD\u62A4\u3002\u5B83\u88AB\u8FD9\u4E2A\u5B97\u6559\u7EC4\u7EC7\u4E2D\u9B54\u6CD5\u5C11\u5973\u7684\u9B54\u529B\u91CD\u91CD\u4FDD\u62A4\u3002\u8FD9\u4E48\u505A\u975E\u5E38\u56F0\u96BE\u3002</strong></p><p><strong>\u56F0\u96BE\uFF0C\u4F46\u4E0D\u662F\u4E0D\u53EF\u80FD\u3002\u4F60\u8BD5\u8FC7\u4E86\uFF1F</strong>\u9EBB\u7F8E\u4E0D\u60F3\u653E\u8FC7\u773C\u524D\u8D28\u95EE\u7684\u7EBF\u7D22\u3002</p><p><strong>\u7ECF\u8FC7\u614E\u91CD\u8003\u8651\uFF0C</strong>\u4E18\u6BD4\u8BF4\uFF0C<strong>\u6211\u6700\u7EC8\u6CA1\u6709\u8DE8\u51FA\u8FD9\u4E00\u6B65\u3002</strong></p><p>\u8FD9\u5C31\u662F\u5B83\u7684\u56DE\u7B54\uFF0C\u4F46\u5979\u5FC3\u4E2D\u5DF2\u7ECF\u6709\u4E86\u7B54\u6848\uFF0C\u5979\u4E0D\u518D\u786E\u5B9A\u662F\u5426\u8981\u5E26\u7740\u5B83\u4E00\u8D77\u8FDB\u53BB\u3002</p><p><strong>\u7EE7\u7EED\u4F60\u7684\u4EEA\u5F0F\uFF0C\u9EBB\u7F8E\uFF0C</strong>\u4E18\u6BD4\u8BF4\uFF0C<strong>\u6211\u4F1A\u770B\u7740\u7684\u3002</strong></p><p>\u5B83\u6D88\u5931\u4E86\u4E00\u77AC\uFF0C\u4E00\u79D2\u4E4B\u540E\u53C8\u51FA\u73B0\u5728\u7F0E\u5E26\u7684\u7F69\u5B50\u4E0A\uFF0C\u50CF\u732B\u4E00\u6837\u8737\u7F29\u7740\u7761\u89C9\u3002</p><p>\u9EBB\u7F8E\u5BF9\u4E18\u6BD4\u7684\u5927\u80C6 \u2014\u2014 \u5C24\u5176\u662F\u5B83\u76F4\u547C\u81EA\u5DF1\u7684\u5927\u540D\u800C\u6447\u5934\uFF0C\u7136\u540E\u4F4E\u4E0B\u5934\u95ED\u4E0A\u773C\u775B\uFF0C\u62D2\u7EDD\u505A\u7948\u7977\u59FF\u52BF\u3002</p><p>\u5979\u672C\u4EE5\u4E3A\u4F1A\u7B49\u4E00\u4F1A\uFF0C\u4F46\u7A81\u7136\u4E00\u9635\u98CE\u5439\u8FC7\u4E86\u5979\u7684\u8138\u5E9E\uFF0C\u5979\u5413\u4E86\u4E00\u8DF3\uFF0C\u518D\u6B21\u7741\u5F00\u773C\u775B\u3002</p><p>\u5F53\u7136\u5979\u5DF2\u7ECF\u4E0D\u5728\u674F\u5B50\u7684\u6559\u5802\u91CC\u4E86\u3002</p><p>\u5979\u5728\u51B7\u98CE\u4E2D\u957F\u547C\u4E00\u53E3\u6C14\uFF0C\u770B\u7740\u773C\u524D\u7684\u8FF7\u96FE\u3002\u5979\u8BB0\u5F97\u8FD9\u4E2A\u5730\u65B9\u3002\u4E0D\u4F1A\u9519\u7684\uFF0C\u5706\u5F62\u7684\u67F1\u5ECA\uFF0C\u5B8F\u4F1F\u7684\u96D5\u50CF\u548C\u591C\u7A7A\u822C\u9ED1\u6697\u7684\u5927\u7406\u77F3\u5730\u9762\u3002</p><p>\u5979\u6447\u4E86\u6447\u5934\uFF0C\u95ED\u4E86\u4F1A\u773C\u775B\u4EE5\u963B\u6321\u89C6\u7EBF\u3002\u8FD9\u662F\u7EDF\u6CBB\u59D4\u5458\u4F1A\u7684\u4E2D\u5EAD\uFF0C\u771F\u6B63\u7684\u5B9E\u6743\u6CD5\u6848\u59D4\u5458\u4F1A\uFF0CMSY \u7ACB\u6CD5\u6743\u7684\u8C61\u5F81\u6027\u5B85\u90B8\u3002</p><p>\u5B83\u662F\u5728\u7EDF\u4E00\u6218\u4E89\u524D\u7684\u9F0E\u76DB\u65F6\u671F\u6309\u7167\u5979\u7684\u6307\u793A\u5EFA\u9020\u7684\uFF0C\u90A3\u65F6\u5373\u4F7F\u5728\u89C1\u6CF7\u539F\u5E02\u7684\u6469\u5929\u5927\u697C\u9876\u4E0A\uFF0C\u4E5F\u6CA1\u6709\u4EBA\u4F1A\u4E0D\u60CA\u8BB6\u4E8E\u8FD9\u6837\u4E00\u5EA7\u6D6E\u534E\u7684\u8D85\u7EA7\u5BAB\u6BBF\u3002\u8FD9\u4E9B\u96D5\u50CF\u4EE3\u8868\u4E86\u5386\u53F2\u4E0A\u8457\u540D\u800C\u5F3A\u5927\u7684\u9B54\u6CD5\u5C11\u5973\uFF0C\u5730\u677F\u4EE3\u8868\u591C\u7A7A\uFF0C\u67F1\u5B50\u2026\u2026 \u55EF\uFF0C\u67F1\u5B50\u5C31\u662F\u4E3A\u4E86\u7ED9\u6574\u4E2A\u5EFA\u7B51\u589E\u6DFB\u4E00\u79CD\u5E84\u4E25\u7684\u6C14\u6C1B\u3002</p><p>\u4E0D\u8FC7\uFF0C\u8FD9\u662F\u66FE\u7ECF\u4E0D\u662F\u8FD9\u6837\u7684\u3002\u8FD9\u91CC\u7684\u5730\u677F\u66FE\u7ECF\u95EA\u70C1\u7740\u661F\u661F\uFF0C\u5927\u7406\u77F3\u7684\u9ED1\u8272\u5982\u540C\u592A\u7A7A\u6DF1\u5904\u3002\u8FD9\u4E9B\u96D5\u50CF\u7684\u539F\u8C8C\u662F\u7528\u9C9C\u8273\u7684\u989C\u8272\u88C5\u9970\u7684\uFF0C\u4F46\u540E\u6765\u5374\u892A\u8272\u4E86\uFF0C\u6709\u4E9B\u5730\u65B9\u7684\u6CB9\u6F06\u8131\u843D\u4E86\uFF0C\u4F46\u4E0D\u77E5\u4F55\u6545\u66F4\u52A0\u751F\u673A\u52C3\u52C3\uFF0C\u4EE5\u81F3\u4E8E\u65E0\u6CD5\u64BC\u52A8\u4EBA\u4EEC\u5BF9\u5B83\u4EEC\u7684\u5370\u8C61\uFF0C\u597D\u50CF\u8FD9\u4E9B\u96D5\u50CF\u968F\u65F6\u90FD\u4F1A\u52A8\u8D77\u6765\u4E00\u6837\u3002</p><p>\u5979\u4EEC\u8EAB\u4E0A\u4E5F\u957F\u6EE1\u4E86\u85E4\u8513\uFF0C\u8346\u68D8\u5BC6\u5E03\uFF0C\u5979\u5F88\u786E\u5B9A\u8FD9\u4E9B\u96D5\u50CF\u6709\u70B9\u504F\u5DEE\u3002\u90A3\u96BE\u9053\u662F\u674F\u5B50\uFF1F\u62FF\u7740\u957F\u77DB\u2026\uFF1F</p><p>\u5979\u5411\u524D\u8D70\u53BB\uFF0C\u772F\u8D77\u773C\u775B\u4EE5\u770B\u5F97\u66F4\u6E05\u695A\u3002\u662F\u7684\uFF0C\u90A3\u5C31\u662F\u674F\u5B50\uFF0C\u5979\u8FB9\u4E0A\u7684\u662F\u7130\uFF0C\u63A5\u7740\u662F\u9EBB\u7F8E\u81EA\u5DF1\u3002</p><p>\u201C\u771F\u6F02\u4EAE\uFF0C\u201D \u4E00\u4E2A\u5B69\u5B50\u6C14\u7684\u58F0\u97F3\u5728\u5979\u8EAB\u8FB9\u54CD\u8D77\u3002</p><p>\u9EBB\u7F8E\u8F6C\u8EAB\u5411\u4E0B\u770B\uFF0C\u76EF\u7740\u73B0\u5728\u719F\u6089\u7684\u90A3\u4E2A\uFF0C\u7559\u7740\u8FAB\u5B50\u3001\u6EE1\u773C\u661F\u5149\u7684\u5B69\u5B50\u770B\u4E86\u4E00\u4F1A\u513F\u3002</p><p>\u201C\u54E6\uFF0C\u673A\u68B0\u5A18\uFF0C\u201D \u5979\u8BF4\uFF0C\u5FC3\u4E2D\u5145\u6EE1\u4E86\u4E00\u79CD\u65E0\u6CD5\u5F62\u5BB9\u7684\u6E29\u6696\uFF0C\u5979\u4E0D\u77E5\u9053\u8FD9\u79CD\u6E29\u6696\u7684\u6E90\u6CC9\u3002\u201D \u5BF9\u4E0D\u8D77\uFF0C\u6211\u4E00\u76F4\u5FD8\u4E86\u4F60\u3002\u201D</p><p>\u5979\u4FEF\u4E0B\u8EAB\uFF0C\u628A\u5973\u5B69\u62B1\u5728\u81EA\u5DF1\u7684\u81C2\u8180\u91CC\uFF0C\u91CD\u91CF\u548C\u5F88\u4E45\u4EE5\u524D\u5DEE\u4E0D\u591A\uFF0C\u90A3\u65F6\u5019\u5979\u7ECF\u5E38\u62B1\u8D77\u7531\u9EBB \u2014\u2014 \u5979\u53EF\u80FD\u6709\u70B9\u592A\u91CD\u4E86\uFF0C\u8BF4\u5B9E\u8BDD\uFF0C\u4F46\u662F\u9B54\u6CD5\u5C11\u5973\u7684\u589E\u76CA\u8BA9\u8FD9\u4E0D\u518D\u662F\u4EC0\u4E48\u95EE\u9898\u3002</p><p>\u5979\u62AC\u5934\u5411\u4E0A\u770B\uFF0C\u518D\u6B21\u611F\u53D7\u5230\u9635\u9635\u51B7\u98CE\u3002\u6700\u521D\u7684\u6CD5\u5F8B\u957F\u5ECA\u662F\u5728\u5BA4\u5185\u7684\uFF0C\u5F53\u7136\u4E5F\u6CA1\u6709\u4E00\u4E2A\u53EF\u4EE5\u901A\u5411\u6C38\u6052\u591C\u7A7A\u7684\u5929\u82B1\u677F \uFF0C\u90A3\u662F\u4E00\u5757\u6620\u7167\u51FA\u4E0B\u65B9\u5730\u677F\u7684\u5B8C\u7F8E\u955C\u9762\u3002\u5979\u8FD8\u633A\u559C\u6B22\u8FD9\u4E2A\u5149\u666F\u7684\u3002</p><p>\u201C\u6211\u60F3\u8FD9\u6B21\u6211\u4F1A\u628A\u8FD9\u513F\u6253\u626E\u5F97\u66F4\u6F02\u4EAE\u4E00\u70B9\uFF0C\u201D \u4E00\u4E2A\u58F0\u97F3\u4EE5\u4E00\u79CD\u7C7B\u4F3C\u53E4\u65E5\u8BED\u7684\u65B9\u5F0F\u8BF4\u3002\u201C\u8FD9\u6837\u66F4\u5408\u9002\u4E00\u70B9\u3002\u201D</p><p>\u8FD9\u4E00\u6B21\uFF0C\u51FA\u73B0\u5728\u5979\u9762\u524D\u7684\u90A3\u4E2A\u5B9E\u4F53\u660E\u663E\u5177\u6709\u67D0\u79CD\u9B54\u6CD5\uFF0C\u4E00\u4E2A\u7A7F\u7740\u767D\u8272\u8FDE\u8863\u88D9\u7684\u5973\u4EBA\uFF0C\u5E26\u7740\u4E00\u79CD\u4F3C\u4E4E\u5728\u9EBB\u7F8E\u8111\u6D77\u6DF1\u5904\u547C\u558A\u7740\u7684\u795E\u6027\u7684\u5149\u73AF\u3002\u5979\u90A3\u7C89\u7EA2\u8272\u7684\u5934\u53D1\u5728\u5730\u677F\u4E0A\u5947\u602A\u5730\u98D8\u52A8\u7740\uFF0C\u4F3C\u4E4E\u6CA1\u6709\u5C3D\u5934\uFF0C\u800C\u662F\u6D88\u5931\u4E86\uFF0C\u5B8C\u5168\u6D88\u5931\u5728\u522B\u7684\u5730\u65B9\u3002\u8FD8\u6709\u5979\u7684\u8138\u2026 \u9EBB\u7F8E\u4E0D\u6562\u53BB\u770B\u3002</p><p>\u201C\u653E\u5F03\u8868\u6F14\u53EF\u7231\u540E\u8F88\u7684\u89D2\u8272\u4E86\uFF1F\u201D \u9EBB\u7F8E\u56DE\u60F3\u8D77\u6765\uFF0C\u201C\u6211\u89C9\u5F97\u8FD9\u8FD8\u6709\u70B9\u9002\u5408\u64CD\u7EB5\u4EBA\u5FC3\u7684\u3002\u4F60\u5F53\u7136\u77E5\u9053\u600E\u4E48\u6233\u6211\u7684\u70B9\u3002\u201D</p><p>\u201C\u6211\u4E0D\u4F1A\u8D23\u602A\u4F60\u7684\u591A\u7591\uFF0C\u201D \u5973\u4EBA\u8BF4\u3002\u201C\u4F46\u90A3\u4E0D\u662F\u8868\u6F14\u3002\u6211\u90A3\u65F6\u8BF4\u7684\u4E00\u5207\u90FD\u662F\u5B9E\u8BDD\u3002\u6211\u6709\u5F88\u591A\u7684\u5916\u8868\u3002\u90A3\u53EA\u662F\u5176\u4E2D\u4E4B\u4E00\uFF0C\u8FD9\u662F\u53E6\u4E00\u4E2A\u3002\u4E0E\u5176\u8BF4\u662F\u4EA4\u66FF\u7684\u9762\u5B54\u4E0D\u5982\u8BF4\u662F\u4EA4\u66FF\u7684\u65F6\u95F4\u70B9\u3002\u5728\u8FD9\u4E2A\u5730\u65B9\u7528\u8FD9\u4E2A\u5916\u8868\u4F3C\u4E4E\u66F4\u5408\u9002\u4E00\u70B9\u201D</p><p>\u9EBB\u7F8E\u76B1\u4E86\u76B1\u7709\u5934\uFF0C\u4E3E\u8D77\u624B\u81C2\u8BA9\u673A\u68B0\u5A18\u770B\u5F97\u66F4\u6E05\u695A\u3002</p><p>\u201C\u6211\u5E0C\u671B\u4F60\u662F\u5728\u8FD9\u6837\u5766\u7387\u5730\u4E3A\u6211\u6548\u52B3\uFF0C\u201D \u9EBB\u7F8E\u8BF4 \u201C\u6211\u6765\u8FD9\u91CC\u662F\u60F3\u5F97\u5230\u7B54\u6848\uFF0C\u6240\u4EE5\u4F60\u8868\u73B0\u5F97\u6709\u70B9\u8BDA\u5B9E\u3002\u4F46\u4F60\u4E0D\u4F1A\u628A\u4E00\u5207\u90FD\u544A\u8BC9\u6211\u7684\u3002\u201D</p><p>\u5979\u6CA1\u628A\u540E\u534A\u53E5\u8BDD\u5F53\u4F5C\u4E00\u4E2A\u95EE\u9898\u3002</p><p>\u5979\u9762\u524D\u7684\u4EBA\u4E3E\u8D77\u4E00\u53EA\u6234\u7740\u624B\u5957\u7684\u624B\u6342\u7740\u5634\u7B11\u4E86\u8D77\u6765\uFF0C\u8FD9\u662F\u4E00\u79CD\u60A0\u626C\u6B22\u5FEB\u7684\u58F0\u97F3\uFF0C\u9690\u9690\u4EE4\u4EBA\u4E0D\u5B89\uFF0C\u4EFF\u4F5B\u5979\u5468\u56F4\u7684\u7A7A\u6C14\u4E5F\u5728\u7B11\u3002</p><p>\u9EBB\u7F8E\u6447\u4E86\u6447\u5934\uFF0C\u4E5F\u89C9\u5F97\u5F88\u597D\u7B11\u3002\u5979\u5728\u671F\u5F85\u7740\u4EC0\u4E48\uFF1F\u8BD5\u56FE\u8BA9\u4E00\u4E2A\u53EF\u80FD\u662F\u795E\u660E\u7684\u4EBA\u653E\u4E0B\u6212\u5907\uFF1F\u5979\u90FD\u89C9\u5F97\u81EA\u5DF1\u8001\u6210\u7684\u6709\u70B9\u8BA9\u4EBA\u53D7\u4E0D\u4E86\u4E86\u3002</p><p>\u201C\u5F53\u7136\u4E0D\u662F\uFF0C\u201D \u5973\u4EBA\u8F6C\u8FC7\u8EAB\u8D70\u8FDB\u5927\u95E8\u5F84\u76F4\u8D70\u5411\u5927\u5385\u3002\u201C\u600E\u4E48\u53EF\u80FD\u6709\u4EBA\u80FD\u544A\u8BC9\u4F60\u4E00\u5207\u5462\uFF1F\u201D</p><p>\u673A\u68B0\u5A18\u5728\u6000\u91CC\u5750\u5367\u4E0D\u5B81\uFF0C\u793A\u610F\u81EA\u5DF1\u60F3\u88AB\u653E\u4E0B\u6765\uFF0C\u9EBB\u7F8E\u7167\u529E\u4E86\uFF0C\u770B\u7740\u5973\u5B69\u8DDF\u7740\u5973\u4EBA\u8D70\u8FDB\u4E86\u5176\u4ED6\u7684\u623F\u95F4\u3002</p><p>\u201C\u6211\u4EEC\u90FD\u5E26\u7740\u4F2A\u88C5\uFF0C\u9EBB\u7F8E\u3055\u3093\uFF0C\u201D \u5973\u4EBA\u8BF4\uFF0C\u8F6C\u8EAB\u62CD\u62CD\u5973\u5B69\u7684\u5934\uFF0C\u201C\u514B\u83B1\u4E3D\u65AF\u3061\u3083\u3093\uFF0C\u5FD7\u7B51\u826F\u5B50\u7684\u6218\u672F\u7535\u8111\uFF0C\u9009\u62E9\u4E00\u4E2A\u4E0D\u6BD4\u81EA\u5DF1\u4E3B\u4EBA\u5E74\u8F7B\u591A\u5C11\u7684\u5F62\u8C61\uFF0C\u4F46\u662F\u673A\u68B0\u5A18\u3061\u3083\u3093\u6CA1\u6709\u8FD9\u4E48\u505A\u3002\u201D</p><p>\u201C\u5FD7\u7B51\u3055\u3093\u8FD8\u5E74\u8F7B\uFF0C\u201D \u673A\u68B0\u5A18\u8BF4\uFF0C\u58F0\u97F3\u6BEB\u65E0\u611F\u60C5\uFF0C\u597D\u50CF\u5728\u53D1\u5446\u3002</p><p>\u201C\u5979\u9700\u8981\u770B\u8D77\u6765\u66F4\u6210\u719F\u7684\u4EBA\uFF0C\u6765\u5E2E\u5979\u5EA6\u8FC7\u96BE\u5173\u3002\u9EBB\u7F8E\u4E00\u5B9A\u4E0D\u9700\u8981\u8FD9\u6837\u7684\u5E2E\u52A9\uFF0C\u4E5F\u8BB8\u76F8\u53CD\u3002\u6211\u4EEC\u7684\u7A0B\u5E8F\u8BA9\u6211\u4EEC\u53D8\u6210\u6211\u4EEC\u4E3B\u4EBA\u5E0C\u671B\u7684\u6837\u5B50\u3002\u4F46\u8FD9\u5F88\u96BE\u4E0D\u5F71\u54CD\u6211\u4EEC\u5BF9\u81EA\u5DF1\u7684\u5370\u8C61\u3002\u6BD5\u7ADF\u6211\u4EEC\u90FD\u8FD9\u4E48\u5E74\u8F7B\u201D</p><p>\u5973\u4EBA\u62B1\u8D77\u4E86\u673A\u68B0\u5A18\uFF0C\u8D70\u51FA\u4E86\u9EBB\u7F8E\u7684\u89C6\u7EBF\uFF0C\u5979\u7684\u6B65\u4F10\u4F3C\u4E4E\u6CA1\u6709\u78B0\u5230\u5730\u9762\u3002</p><p>\u201C\u4F60\u5BF9\u5916\u754C\u5C55\u73B0\u7684\u9762\u5B54\u539F\u6CA1\u6709\u4F60\u5BF9\u81EA\u5DF1\u5C55\u73B0\u7684\u9762\u5B54\u6765\u7684\u91CD\u8981\uFF0C\u201D \u5979\u8BF4\u3002\u201C\u8FD9\u5B9A\u4E49\u4E86\u4F60\u662F\u8C01\uFF0C\u5B9A\u4E49\u4E86\u4F60\u5982\u4F55\u5BA1\u89C6\u81EA\u5DF1\u7684\u4EBA\u751F\uFF0C\u5B9A\u4E49\u4E86\u4F60\u6709\u591A\u5E78\u798F\u3002\u201D</p><p>\u5979\u505C\u5728\u90A3\u513F\u8BA9\u9EBB\u7F8E\u601D\u8003\uFF0C\u63A5\u7740\u5411\u4EEC\u53E3\u4F38\u51FA\u4E86\u4E00\u53EA\u624B\u9080\u8BF7\u5979\u4E00\u540C\u5411\u524D\u3002</p><p>\u201C\u6765\u5427\u3002\u6211\u4E0D\u4F1A\u54AC\u4EBA\u7684\u3002\u201D</p><p>\u9EBB\u7F8E\u5446\u7ACB\u4E86\u4E00\u77AC\uFF0C\u63A5\u7740\u7F13\u7F13\u5411\u524D\u8D70\u53BB\uFF0C\u5BF9\u81EA\u5DF1\u6447\u7740\u5934\u3002\u95E8\u69DB\u5413\u4E86\u5979\u4E00\u8DF3\uFF0C\u4EFF\u4F5B\u5979\u662F\u4E00\u4F4D\u8D70\u8FDB\u6821\u957F\u529E\u516C\u5BA4\u7684\u5B66\u751F\uFF0C\u8FD9\u8BA9\u5979\u6709\u70B9\u82E6\u607C\uFF0C\u5979\u6709\u70B9\u592A\u8FC7\u4E0D\u5B89\u4E86\u3002\u5979\u5B81\u613F\u76F8\u4FE1\u5979\u7684\u5185\u5FC3\u6B63\u5728\u88AB\u9B54\u6CD5\u64CD\u63A7\uFF0C\u8FD9\u662F\u4E00\u4E2A\u4E3A\u5979\u5FC3\u7406\u6CBB\u7597\u7684\u6B22\u8FCE\u628A\u620F\uFF0C\u4F46\u5979\u4E0D\u80FD\u786E\u5B9A\u3002</p><p>\u5F53\u7136\uFF0C\u7EDF\u6CBB\u59D4\u5458\u4F1A\u7684\u5168\u4F53\u4F1A\u8BAE\u5385\u4E3B\u8981\u662F\u4E00\u4E2A\u4EEA\u5F0F\u6027\u5EFA\u7B51\u3002\u7531\u4E8E\u5927\u793C\u5802\u7684\u89C4\u6A21\uFF0C\u5B83\u751A\u81F3\u8FDE\u6BCF\u4E2A\u653F\u6CBB\u6D3E\u522B\u7684\u4E3B\u8981\u6210\u5458\u90FD\u96BE\u4EE5\u5C31\u5EA7\uFF0C\u66F4\u4E0D\u7528\u8BF4\u7EDF\u6CBB\u59D4\u5458\u4F1A\u8FC7\u591A\u7684\u6210\u5458\u4E86\u3002\u5373\u4F7F\u5728\u5EFA\u8BBE\u4E4B\u65F6\uFF0C\u5B83\u4E5F\u53EA\u662F\u4E00\u4E2A\u91CD\u8981\u6F14\u8BB2\u7684\u5730\u65B9\uFF0C\u4E00\u4E2A\u9AD8\u7BA1\u6216\u9886\u5BFC\u5728\u865A\u62DF\u4E16\u754C\u7684\u5E37\u5E55\u540E\u9762\u8C61\u5F81\u6027\u5730\u5411\u6574\u4E2A\u673A\u6784\u53D1\u8868\u6F14\u8BB2\u7684\u5730\u65B9\u3002</p><p>\u56E0\u6B64\uFF0C\u5B83\u662F\u4E3A\u5916\u89C2\u800C\u4E0D\u662F\u529F\u80FD\u800C\u8BBE\u8BA1\u7684\uFF0C\u5929\u82B1\u677F\u4E0A\u7ED8\u6709 MSY \u5386\u53F2\u4E0A\u7684\u5173\u952E\u65F6\u523B\uFF0C\u5899\u58C1\u4E0A\u96D5\u523B\u7740\u5404\u79CD\u9B54\u6CD5\u5C11\u5973\u8BB8\u613F\u7684\u8FC7\u7A0B\u4E2D\u7684\u753B\u50CF\uFF0C\u8FD9\u4E9B\u6D6E\u96D5\u76F4\u89C6\u7740\u8BB2\u53F0\u3002</p><p>\u8FD9\u6837\u505A\u7684\u76EE\u7684\u662F\u6FC0\u53D1\u6F14\u8BB2\u8005\u7684\u8D23\u4EFB\u611F\uFF0C\u8FD9\u662F\u4E00\u79CD\u5F88\u523B\u610F\u7684\u6548\u679C\uFF0C\u662F\u9EBB\u7F8E\u81EA\u5DF1\u8981\u6C42\u7684\u3002\u540E\u6765\u5979\u540E\u6094\u4E86\uFF0C\u56E0\u4E3A\u5979\u53D1\u73B0\u8FD9\u6837\u5F88\u4EE4\u4EBA\u4E0D\u5B89\u3002</p><p>\u201C\u8FD9\u6B63\u662F\u4F60\u4E0E\u5176\u4ED6\u4EBA\u4E0D\u540C\u7684\u5730\u65B9\uFF0C\u201D \u5973\u4EBA\u8BF4\uFF0C\u201C\u8FD9\u4E9B\u4E8B\u5BF9\u4F60\u6765\u8BF4\u90FD\u662F\u90A3\u4E48\u771F\u5B9E\u3002\u6211\u4E0D\u662F\u8BF4\u522B\u4EBA\u4E0D\u5728\u4E4E\uFF0C\u4F46\u662F\u5979\u4EEC\u4ECE\u672A\u62B1\u6709\u8FD9\u79CD\u60C5\u611F\u3002\u7130\u3061\u3083\u3093\uFF0C\u7531\u771F\u3061\u3083\u3093\uFF0C\u751A\u81F3\u674F\u5B50\u3061\u3083\u3093\uFF0C\u90FD\u80FD\u8BA9\u751F\u6D3B\u56DE\u5230\u6B63\u8F68\uFF0C\u5E76\u6700\u7EC8\u6D3B\u6210\u81EA\u5DF1\u7684\u6837\u5B50\uFF0C\u4F46\u662F\u4F60\u6CA1\u6709</p><p>\u9EBB\u7F8E\u8F6C\u8EAB\u770B\u7740\u8FD9\u4F4D\u5973\u4EBA\uFF0C\u5979\u6B63\u4E0E\u673A\u68B0\u5A18\u4E00\u8D77\u5750\u5728\u89C2\u4F17\u5E2D\u7684\u957F\u6905\u4E0A\u3002\u8FB9\u4E0A\u8FD8\u6709\u4E00\u76D8\u997C\u5E72\uFF0C\u673A\u68B0\u5A18\u4F3C\u4E4E\u6B63\u5728\u52AA\u529B\u5730\u89E3\u51B3\u8FD9\u4E9B\u70B9\u5FC3\uFF0C\u4F5C\u4E3A\u4E00\u4E2A\u5B69\u5B50\u5979\u5403\u5F97\u6709\u70B9\u5FEB\uFF0C\u53EF\u80FD\u9700\u8981\u70B9\u725B\u5976\u6216\u81F3\u5C11\u6765\u676F\u6C34\u3002</p><p>\u5973\u4EBA\u8F7B\u62CD\u7740\u673A\u68B0\u5A18\u8FB9\u4E0A\u7684\u997C\u5E72\u5C51\uFF0C\u9EBB\u7F8E\u8D70\u4E0A\u524D\u53BB\uFF0C\u5FC3\u60F3\uFF0C\u8FD9\u6837\u770B\u6765\uFF0C\u8FD9\u4E2A\u5973\u4EBA\u53EF\u6CA1\u90A3\u4E48\u5A01\u4E25\u3002\u5979\u51E0\u4E4E\u53EF\u4EE5\u88AB\u8BA4\u4E3A\u662F\u4E00\u4E2A\u666E\u901A\u4EBA\uFF0C\u5373\u4F7F\u98D8\u9038\u7684\u5934\u53D1\u548C\u6A21\u7CCA\u7684\u8138\u6709\u70B9\u8BA9\u4EBA\u5728\u610F\u3002</p><p>\u201C\u6211\u4E00\u76F4\u89C9\u5F97\u6211\u6BD4\u4ED6\u4EEC\u66F4\u70E6\u607C\u8FD9\u4E9B\u4E8B\uFF0C\u201D \u9EBB\u7F8E\u8BF4\u7740\u5750\u4E86\u4E0B\u6765\uFF0C\u201C\u6211\u4ECE\u6765\u6CA1\u6709\u8BD5\u8FC7\u9690\u85CF\u5B83\u3002\u6211\u4E00\u76F4\u5728\u52AA\u529B\u786E\u4FDD\u6211\u4EEC\u4E0D\u4F1A\u5FD8\u8BB0\u4EBA\u7C7B\u7684\u4EE3\u4EF7\u3002\u4F46\u6211\u4ECE\u6CA1\u60F3\u8FC7\u6211\u4F1A\u6BD4\u4ED6\u4EEC\u5F3A\u3002\u76F8\u53CD\uFF0C\u6211\u7ECF\u5E38\u60F3\uFF0C\u5982\u679C\u6211\u66F4\u50CF\u4ED6\u4EEC\uFF0C\u6211\u662F\u5426\u4F1A\u6210\u4E3A\u4E00\u4E2A\u66F4\u597D\u7684\u7ACB\u6CD5\u8005\u3001\u66F4\u597D\u7684\u5916\u4EA4\u5B98\u3001\u66F4\u597D\u7684\u9886\u5BFC\u4EBA\u3002\u5982\u679C\u6CA1\u6709\u522B\u7684\u4E8B\uFF0C\u4E5F\u8BB8\u6211\u4F1A\u66F4\u5FEB\u4E50\u3002\u201D</p><p>\u201C\u5927\u5BB6\u90FD\u77E5\u9053\uFF0C\u9EBB\u7F8E\u3002\u4ED6\u4EEC\u80FD\u770B\u51FA\u4F60\u6BD4\u5176\u4ED6\u4EBA\u66F4\u5728\u4E4E\u3002\u8FD9\u5C31\u662F\u4E3A\u4EC0\u4E48\u4ED6\u4EEC\u76F8\u4FE1\u4F60\uFF0C\u4E3A\u4EC0\u4E48\u4F60\u4E00\u76F4\u88AB\u5B89\u6392\u5728\u8FD9\u4E9B\u89D2\u8272\u4E0A\u3002\u56E0\u4E3A\u4F60\u5728\u4E4E\u4ED6\u4EEC\u8DDF\u968F\u4F60\u3002\u4ED6\u4EEC\u77E5\u9053\u4F60\u4E0D\u4F1A\u4E3A\u4E86\u4E00\u70B9\u5C0F\u4E8B\u5C31\u628A\u51FA\u5356\u4ED6\u4EEC\u3002\u4F46\u4ED6\u4EEC\u5FD8\u8BB0\u4E86\u8FD9\u4F1A\u4F24\u5BB3\u5230\u4F60\uFF0C\u6709\u65F6\uFF0C\u4F60\u5F97\u77E5\u9053\u4F60\u4E0D\u5F97\u4E0D\u770B\u7740\u4F60\u4E3A\u4E4B\u8D1F\u8D23\u7684\u751F\u547D\u88AB\u6467\u6BC1\u3002\u201D</p><p>\u9EBB\u7F8E\u53D1\u73B0\u81EA\u5DF1\u6B63\u76EF\u7740\u673A\u68B0\u5A18\u5403\u997C\u5E72\uFF0C\u63A5\u7740\u5979\u81EA\u5DF1\u4E5F\u6293\u4E86\u4E00\u5757\u5C1D\uFF0C\u4E0E\u5176\u8BF4\u662F\u51FA\u4E8E\u60F3\u5403\u4E1C\u897F\uFF0C\u4E0D\u5982\u8BF4\u662F\u4E3A\u4E86\u586B\u8865\u8C08\u8BDD\u7684\u7A7A\u767D\u3002\u5979\u4E0D\u77E5\u9053\u8BE5\u8BF4\u4EC0\u4E48\u3002</p><p>\u201C\u628A\u4E8B\u60C5\u804A\u804A\u660E\u767D\u5427\uFF0C\u201D \u5973\u4EBA\u8BF4\uFF0C\u201C\u4F60\u4E00\u76F4\u90FD\u628A\u81EA\u5DF1\u770B\u4F5C\u82F1\u96C4\u3002\u5728\u4F60\u5C0F\u65F6\u5019\u7684\u906D\u9047\u4E4B\u540E\uFF0C\u4F3C\u4E4E\u522B\u65E0\u4ED6\u6CD5\u3002\u4F46\u662F\u8FD9\u79CD\u62C5\u5FC3\u6298\u78E8\u7740\u4F60\uFF0C\u4F60\u53EF\u80FD\u4E0D\u662F\u82F1\u96C4\uFF0C\u4F60\u53EF\u80FD\u505A\u5F97\u592A\u591A\uFF0C\u6216\u8005\u4F60\u6839\u672C\u4E0D\u591F\u597D\u3002\u4F60\u53EA\u60F3\u8BA9\u5168\u4E16\u754C\u7684\u95EE\u9898\u5F97\u5230\u89E3\u51B3\uFF0C\u5E76\u4E14\u8FD9\u4E2A\u4E16\u754C\u4E0D\u518D\u9700\u8981\u4F60\uFF0C\u8FD9\u6837\u4F60\u5C31\u4E0D\u5FC5\u9762\u5BF9\u8FD9\u79CD\u98CE\u9669\u3002\u201D</p><p>\u201C\u6240\u4EE5\u5462\uFF1F\u201D \u9EBB\u7F8E\u95EE\uFF0C\u5979\u611F\u53D7\u5230\u4E86\u673A\u68B0\u5A18\u7684\u76EE\u5149\uFF0C\u201C\u4F60\u8FD9\u4E2A\u65E0\u6240\u4E0D\u77E5\u7684\u795E\u7075\uFF0C\u80FD\u8BFB\u61C2\u6211\u7684\u5FC3\u601D\uFF0C\u544A\u8BC9\u6211\u6211\u7684\u5FC3\u7ED3\u662F\u4EC0\u4E48\uFF0C\u6211\u4F1A\u88AB\u611F\u52A8\u5417\uFF1F\u5373\u4F7F\u4F60\u53EF\u4EE5\uFF0C\u8FD9\u4E5F\u4E0D\u4F1A\u8BA9\u4EFB\u4F55\u95EE\u9898\u6D88\u5931\u3002\u6211\u5C31\u662F\u6211\u3002\u201D</p><p>\u90A3\u5973\u4EBA\u6B6A\u7740\u5934\u3002</p><p>\u201C\u4F46\u4F60\u662F\u6765\u627E\u6211\u7684\uFF0C\u4E0D\u662F\u5417\uFF1F\u4E0D\u4EC5\u4EC5\u56E0\u4E3A\u6211\u95EE\u4E86\uFF0C\u8FD8\u56E0\u4E3A\u4F60\u6709\u95EE\u9898\u60F3\u95EE\u3002\u5173\u4E8E\u8FD9\u4E00\u5207\u7684\u610F\u4E49\uFF0C\u8FD9\u4E9B\u5E74\u6765\u4F60\u6240\u505A\u7684\u4E8B\u60C5\u7684\u610F\u4E49\uFF0C\u4EE5\u53CA\u4F60\u5C06\u8981\u505A\u7684\u4E8B\u60C5\u7684\u610F\u4E49\u3002\u4F60\u5E0C\u671B\u6709\u4E00\u4E2A\u4EE4\u4EBA\u6EE1\u610F\u7684\u7B54\u6848\uFF0C\u4E00\u4E2A\u8BA9\u4E00\u5207\u90FD\u503C\u5F97\u7684\u7B54\u6848\u3002\u9664\u6B64\u4E4B\u5916\uFF0C\u5373\u4F7F\u4F60\u771F\u7684\u662F\u4E2A\u82F1\u96C4\uFF0C\u4F60\u4ECD\u60F3\u542C\u542C\u6211\u7684\u610F\u89C1\u3002\u201D</p><p>\u9EBB\u7F8E\u95ED\u4E0A\u4E86\u773C\u775B\uFF0C\u518D\u6B21\u9677\u5165\u81EA\u8D23\u3002\u5979\u4E3A\u4EC0\u4E48\u5927\u53D1\u96F7\u9706\uFF1F\u90A3\u4E2A\u5973\u4EBA\u6CA1\u8BF4\u9519\u4EC0\u4E48\uFF0C\u53EA\u662F\u2026\u2026 \u5F62\u8C61\u5730\u8BF4\uFF0C\u628A\u5979\u6252\u5149\u4E86\u3002\u5F53\u7136\uFF0C\u8FD9\u5C31\u662F\u95EE\u9898\u6240\u5728\u3002</p><p>\u201C\u989D\uFF0C\u662F\u5417\uFF1F\u201D \u5979\u671B\u7740\u8BB2\u53F0\u95EE\u9053\uFF0C\u201C\u6211\u4E0D\u4F1A\u6492\u8C0E\u3002\u6211\u60F3\u77E5\u9053\u3002\u201D</p><p>\u5973\u4EBA\u62FF\u8D77\u4E86\u4E00\u5757\u997C\u5E72\u53C8\u77AC\u95F4\u653E\u4E0B\u4E86\u3002</p><p>\u201C\u8FD9\u662F\u4F60\u7684\u98DF\u8C31\uFF0C\u9EBB\u7F8E\u3055\u3093\uFF0C\u201D \u5979\u8BF4\uFF0C\u201C\u4F60\u4E0D\u8BB0\u5F97\u4E86\uFF1F\u201D</p><p>\u5988\u5988\u4F4E\u5934\u770B\u4E86\u770B\uFF0C\u88AB\u8FD9\u660E\u663E\u7684\u4E0D\u7B26\u5408\u903B\u8F91\u7684\u4E8B\u60C5\u5F04\u7CCA\u6D82\u4E86\uFF0C\u7136\u540E\u62FF\u8D77\u4E00\u4E2A\u54AC\u4E86\u53E3\u3002</p><p>\u201C\u6211\u5DF2\u7ECF\u5F88\u4E45\u6CA1\u6709\u70E4\u8FC7\u4E86\uFF0C\u201D \u5979\u8BF4\uFF0C\u201C\u4F46\u6211\u60F3\u4F60\u77E5\u9053\u3002\u5766\u7387\u5730\u8BF4\uFF0C\u5B83\u4ECE\u6765\u6CA1\u6709\u90A3\u4E48\u7279\u522B\u3002\u201D</p><p>\u5979\u60F3\u77E5\u9053\u8FD9\u6709\u4EC0\u4E48\u5173\u7CFB\u3002\u90A3\u4E2A\u5973\u4EBA\u5728\u56DE\u907F\u95EE\u9898\u5417\uFF1F</p><p>\u201C\u8BA9\u6211\u7ED9\u4F60\u8BB2\u4E2A\u6545\u4E8B\uFF0C\u201D \u5973\u4EBA\u62AC\u8D77\u5934\u770B\u7740\u5929\u82B1\u677F\u8BF4\uFF0C\u201C\u5F88\u77ED\u7684\u6545\u4E8B\uFF0C\u6240\u4EE5\u6211\u5E0C\u671B\u4F60\u8FC1\u5C31\u4E00\u4E0B\u6211\u3002\u201D</p><p>\u5979\u5927\u58F0\u62CD\u4E86\u62CD\u624B\uFF0C\u9EBB\u7F8E\u51E0\u4E4E\u5413\u4E86\u4E00\u8DF3\uFF0C\u56E0\u4E3A\u5728\u62C9\u5F00\u4E00\u5757\u8001\u5F0F\u7684\u6295\u5F71\u5C4F\u5E55\u4E4B\u524D\uFF0C\u8FD9\u513F\u4E4B\u524D\u80AF\u5B9A\u6CA1\u6709\u8FD9\u4E1C\u897F\u3002\u5C4F\u5E55\u5728\u793C\u5802\u7684\u4E2D\u5FC3\uFF0C\u4ECE\u5730\u9762\u4E0A\u98D8\u4E86\u51E0\u82F1\u5C3A\u3002\u8D70\u9A6C\u706F\u5F0F\u7684\u4EBA\u751F\u753B\u9762\u5F00\u59CB\u95EA\u70C1\uFF0C\u534E\u4E3D\u7684\u6570\u5B57\u5F00\u59CB\u5012\u6570\uFF0C\u8FD9\u662F\u4E00\u90E8\u7535\u5F71\u7684\u5F00\u5934\u3002</p><p>\u51FA\u73B0\u5728\u5C4F\u5E55\u4E0A\u7684\u4EBA\u7269\u88AB\u62BD\u8C61\u5730\u5448\u73B0\u51FA\u6765\uFF0C\u50CF\u662F\u4E00\u672C\u5947\u5E7B\u7684\u56FE\u753B\u4E66\u88AB\u8D4B\u4E88\u4E86\u751F\u547D\u3002\u90A3\u5973\u4EBA\u8FB9\u8BB2\u8FB9\u8868\u6F14\u6545\u4E8B\u3002</p><p>\u201C\u66FE\u51E0\u4F55\u65F6\uFF0C\u5728\u9B54\u6CD5\u5C11\u5973\u4EEC\u4E3A\u81EA\u5DF1\u7684\u613F\u671B\u4ED8\u51FA\u9AD8\u6602\u4EE3\u4EF7\u7684\u9ED1\u6697\u7684\u65E7\u65F6\u4EE3\uFF0C\u4E00\u4E2A\u5931\u53BB\u4E00\u5207\u7684\u5973\u5B69\u628A\u4E00\u4E2A\u65B0\u7684\u5973\u5B69\u653E\u5728\u81EA\u5DF1\u7684\u7FC5\u8180\u4E0B\uFF0C\u628A\u5979\u6240\u77E5\u9053\u7684\u4E00\u5207\u90FD\u6559\u7ED9\u5979\uFF0C\u544A\u8BC9\u5979\u4EC0\u4E48\u662F\u9B54\u6CD5\u5C11\u5973\u3002\u662F\u5BF9\u6297\u9ED1\u6697\u7684\u6218\u58EB\u3002\u662F\u5B88\u62A4\u5929\u4F7F\u3002\u662F\u65E0\u52A9\u8005\u7684\u634D\u536B\u8005\u3002\u201D</p><p>\u6700\u540E\u7684\u8FD9\u4E9B\u77ED\u8BED\u88AB\u7AE5\u8BDD\u6545\u4E8B\u822C\u7684\u56FE\u50CF\u6240\u5F3A\u8C03\uFF0C\u4E00\u4E2A\u5973\u5B69\u62FF\u7740\u5251\u7AD9\u5728\u591C\u8272\u4E2D\uFF0C\u4E00\u4E2A\u5929\u4F7F\u6F02\u6D6E\u5728\u57CE\u5E02\u666F\u89C2\u4E0A\uFF0C\u4E00\u4E2A\u5973\u5B69\u7528\u94C1\u94FE\u5899\u4FDD\u62A4\u7740\u53E6\u4E00\u4E2A\u5973\u5B69\u3002\u7136\u800C\uFF0C\u9EBB\u7F8E\u770B\u5230\u4E86\u4E00\u4E9B\u592A\u719F\u6089\u7684\u526A\u5F71\u3002</p><p>\u201C\u6709\u4E00\u5929\uFF0C\u4E00\u573A\u707E\u96BE\u88AD\u51FB\u4E86\u8FD9\u5EA7\u57CE\u5E02\uFF0C\u8FD9\u573A\u524D\u6240\u672A\u6709\u7684\u707E\u96BE\u5A01\u80C1\u7740\u6467\u6BC1\u5979\u4EEC\u6240\u7231\u7684\u4E00\u5207\u3002\u8FD9\u79CD\u5A01\u80C1\u662F\u96BE\u4EE5\u60F3\u8C61\u7684\uFF0C\u4E0D\u53EF\u963B\u6321\u7684\u3002\u5979\u4EEC\u4E0D\u505C\u6218\u6597\uFF0C\u6CA1\u6709\u7F18\u7531\uFF0C\u6CA1\u6709\u5E0C\u671B\uFF0C\u76F4\u5230\u6700\u540E\u6700\u5E74\u957F\u7684\u90A3\u4E2A\u5973\u5B69\u6B7B\u53BB\u4E86\uFF0C\u4F46\u662F\u65B0\u7684\u5973\u5B69\u7262\u8BB0\u7740\u5979\u542C\u8FC7\u7684\u8BDD\u548C\u5BF9\u5979\u7684\u6559\u5BFC\uFF0C\u4E49\u65E0\u53CD\u987E\u5730\u8DF3\u5165\u4E86\u6DF1\u6E0A\u3002\u201D</p><p>\u94F6\u5E55\u4E0A\u7684\u5973\u5B69\u7D27\u63E1\u7740\u4E00\u53EA\u6F06\u9ED1\u7684\u624B\uFF0C\u7136\u540E\u5411\u524D\u4FEF\u51B2\uFF0C\u8DF3\u8FDB\u4E86\u67D0\u79CD\u5927\u7B11\u7740\u7684\u751F\u7269\u7684\u809A\u5B50\u91CC\u3002</p><p>\u201C\u5F53\u7136\uFF0C\u5979\u4E5F\u6B7B\u4E86\u3002\u8FD9\u4E0D\u662F\u7AE5\u8BDD\uFF0C\u4E5F\u4E0D\u662F\u82F1\u96C4\u7535\u5F71\uFF0C\u5979\u751A\u81F3\u6CA1\u80FD\u62EF\u6551\u8FD9\u5EA7\u88AB\u968F\u610F\u5937\u4E3A\u5E73\u5730\u7684\u57CE\u5E02\u3002\u6BD5\u7ADF\uFF0C\u5979\u53EA\u662F\u4E00\u4E2A\u666E\u901A\u7684\u9B54\u6CD5\u5C11\u5973\uFF0C\u5B87\u5B99\u5E76\u4E0D\u5728\u4E4E\u3002\u201D</p><p>\u8FD9\u90E8\u7535\u5F71\u5728\u90A3\u91CC\u653E\u6162\u4E86\u8282\u594F\uFF0C\u9ED1\u8272\u7684\u753B\u9762\u5360\u636E\u4E86\u957F\u65F6\u95F4\uFF0C\u771F\u662F\u4EE4\u4EBA\u5FC3\u9178\u7684\u4E00\u523B\u3002</p><p>\u201C\u4F46\u662F\uFF0C\u867D\u7136\u5B87\u5B99\u53EF\u80FD\u4E0D\u5728\u4E4E\uFF0C\u4F46\u4EBA\u7C7B\u5374\u5728\u4E4E\uFF0C\u90A3\u91CC\u8FD8\u6709\u53E6\u4E00\u4E2A\u5973\u5B69\u89C1\u8BC1\u4E86\u8FD9\u4E00\u5207\u3002\u5979\u54ED\u4E86\uFF0C\u4E3A\u5979\u6240\u770B\u5230\u7684\u4E00\u5207\uFF0C\u5E76\u70ED\u5207\u5730\u5E0C\u671B\u6709\u53E6\u4E00\u6B21\u673A\u4F1A\u3002\u8BA9\u5979\u4EEC\u90FD\u80FD\u518D\u6765\u4E00\u6B21\u3002\u201D</p><p>\u56FE\u50CF\u65CB\u8F6C\u8D77\u6765\uFF0C\u9ED1\u8272\u53D8\u6210\u4E86\u67D4\u548C\u7684\u719F\u6089\u7684\u8F89\u5149\u3002</p><p>\u201C\u867D\u7136\u65C5\u7A0B\u6F2B\u957F\u800C\u5B64\u72EC\uFF0C\u4F46\u6700\u540E\u8FD8\u662F\u6709\u5E0C\u671B\uFF0C\u901A\u8FC7\u5E0C\u671B\u5979\u4EEC\u90FD\u5F97\u6551\u4E86\u3002\u8FD9\u4E00\u5207\u90FD\u662F\u56E0\u4E3A\u6709\u4E00\u4E2A\u5973\u5B69\u6218\u80DC\u4E86\u5185\u5FC3\u7684\u9ED1\u6697\uFF0C\u52AA\u529B\u5728\u4E00\u4E2A\u7A7A\u865A\u7684\u4E16\u754C\u91CC\u6D3B\u4E0B\u53BB\u3002\u201D</p><p>\u5C4F\u5E55\u53D8\u6210\u4E86\u6DE1\u767D\u8272\uFF0C\u4E00\u5F00\u59CB\u5E74\u957F\u7684\u5973\u5B69\u72EC\u81EA\u4ECE\u5B66\u6821\u8D70\u56DE\u5BB6\uFF0C\u624B\u91CC\u62FF\u7740\u4E00\u4E2A\u5305\uFF0C\u4F4E\u7740\u5934\u3002\u7EC6\u8282\u88AB\u5236\u7247\u4EBA\u7528\u827A\u672F\u624B\u6CD5\u63A9\u76D6\u4E86\u3002</p><p>\u201C\u90A3\u4E48\u4F60\u544A\u8BC9\u6211\uFF0C\u8FD9\u4E2A\u5973\u5B69\u662F\u82F1\u96C4\u5417\uFF1F\u5982\u679C\u4F60\u95EE\u5979\uFF0C\u5979\u4E0D\u4F1A\u8FD9\u4E48\u8BF4\u3002\u5979\u6709\u5F88\u591A\u9057\u61BE\uFF0C\u5979\u7684\u5F71\u54CD\u4E0D\u5927\u3002\u5F53\u7ED3\u5C40\u6765\u4E34\uFF0C\u5979\u77E5\u9053\u81EA\u5DF1\u65E0\u80FD\u4E3A\u529B\u65F6\uFF0C\u5979\u8FD8\u662F\u7AD9\u4E86\u51FA\u6765\uFF0C\u56E0\u4E3A\u8FD9\u662F\u5BF9\u7684\u3002\u5C3D\u7BA1\u5979\u4ECE\u4E0D\u77E5\u9053\uFF0C\u4F46\u4E00\u5207\u90FD\u4E0D\u540C\u4E86\u3002\u6211\u771F\u5E0C\u671B\u6709\u529E\u6CD5\u544A\u8BC9\u5979\u3002\u201D</p><p>\u9EBB\u7F8E\u7B49\u4E86\u4E00\u4F1A\u513F\uFF0C\u62E8\u5F04\u8D77\u4E86\u81EA\u5DF1\u7684\u624B\uFF0C\u7136\u540E\u8BF4\uFF1A</p><p>\u201C\u8FD9\u7B26\u5408\u4E00\u4E2A\u82F1\u96C4\u7684\u6240\u6709\u901A\u5E38\u5B9A\u4E49\u3002\u6211\u60F3\uFF0C\u8FD9\u662F\u4E00\u4E2A\u60B2\u5267\uFF0C\u5728\u5BFB\u6C42\u67D0\u79CD\u6551\u8D4E\u3002\u4F46\u4F60\u544A\u8BC9\u6211\u8FD9\u4E2A\u6545\u4E8B\u662F\u6709\u539F\u56E0\u7684\u3002\u90A3\u4E2A\u5973\u5B69\u662F\u6211\u5417\uFF1F\u8FD9\u4E2A\u6545\u4E8B\u88AB\u6211\u4EEC\u7684\u4E16\u754C\u62B9\u53BB\u4E86\uFF0C\u53D6\u800C\u4EE3\u4E4B\u7684\u662F\u884C\u4F1A\u5417\uFF1F\u6211\u52AA\u529B\u4F7F\u8FD9\u4E9B\u7247\u6BB5\u5408\u7406\uFF0C\u4F46\u6709\u4E9B\u613F\u671B\u6539\u53D8\u4E86\u73B0\u5B9E\u2026\u2026\u201D</p><p>\u201C\u4F60\u4E00\u76F4\u90FD\u662F\u4E2A\u82F1\u96C4\uFF0C\u201D \u5973\u4EBA\u62C9\u7740\u5979\u7684\u624B\u8BF4\uFF0C\u201C\u60F3\u60F3\u6545\u4E8B\u662F\u600E\u4E48\u53D1\u5C55\u7684\u3002\u82F1\u96C4\u4E0D\u4EC5\u56E0\u4E3A\u5979\u4EEC\u7684\u4E8B\u8FF9\uFF0C\u800C\u4E14\u56E0\u4E3A\u5979\u4EEC\u6709\u82F1\u96C4\u7684\u7075\u9B42\u800C\u88AB\u9882\u626C\u3002\u4F60\u6709\u82F1\u96C4\u7684\u7075\u9B42\u3002\u800C\u4E14\u6211\u77E5\u9053\uFF0C\u4EBA\u7C7B\u4ECD\u7136\u9700\u8981\u90A3\u4E2A\u82F1\u96C4\u3002\u201D</p><p>\u6700\u540E\u90A3\u53E5\u8BDD\u60CA\u52A8\u4E86\u4E00\u76F4\u9677\u5165\u81EA\u7701\u7684\u9EBB\u7F8E\u3002\u5979\u65E9\u4E9B\u65F6\u5019\u8BF4\u7684\u90FD\u662F\u5B9E\u8BDD\uFF0C\u5373\u4F7F\u5979\u4E0D\u77E5\u9053\u6709\u591A\u771F\u5B9E\u3002</p><p>\u5979\u4E0D\u80FD \u2014\u2014 \u6216\u8005\u4E0D\u60F3\u88AB\u8FD9\u79CD\u60F3\u6CD5\u5F71\u54CD\u3002</p><p>\u4F46\u5979\u8FD8\u505A\u4E0D\u5230\u3002</p><p>\u201C\u786E\u5B9E\uFF0C\u201D \u9EBB\u7F8E\u8BF4\uFF0C\u201C\u8FD9\u4E0D\u4EC5\u4EC5\u662F\u5728\u8C08\u8BBA\u6211\uFF0C\u8FD9\u4E5F\u5E94\u8BE5\u5728\u8C08\u8BBA\u8FD9\u573A\u6218\u4E89\u3002\u201D</p><p>\u201C\u6211\u6700\u7EC8\u4F1A\u63D0\u9192\u4F60\u7684\uFF0C\u201D \u673A\u68B0\u5A18\u4E00\u8FB9\u8BF4\uFF0C\u4E00\u8FB9\u542E\u5438\u7740\u4E00\u76D2\u679C\u6C41\uFF0C\u663E\u7136\uFF0C\u8FD9\u662F\u5979\u4ECE\u9EBB\u7F8E\u7684\u8BB0\u5FC6\u4E2D\u5F97\u5230\u7684\uFF0C\u201C\u4F60\u4F1A\u77E5\u9053\u5979\u662F\u5426\u4F1A\u544A\u8BC9\u4F60\u672A\u6765\u7684\u3002\u201D</p><p>\u201C\u8FD9\u662F\u4E00\u79CD\u7279\u6743\uFF0C\u9EBB\u7F8E\u3055\u3093\uFF0C\u201D \u90A3\u5973\u4EBA\u8BF4\uFF0C\u4E3E\u8D77\u4E00\u6839\u624B\u6307\u53CD\u9A73\u9053\uFF0C\u201C\u6211\u771F\u7684\u4E0D\u80FD\u8F7B\u6613\u5730\u653E\u5F03\u3002\u4F60\u77E5\u9053\u56E0\u679C\u5173\u7CFB\u95EE\u9898\u4F1A\u5E26\u6765\u4EC0\u4E48\u6837\u7684\u540E\u679C\u5417\uFF1F\u201D</p><p>\u5979\u4E0D\u7965\u5730\u6325\u624B\u3002</p><p>\u201C\u60A8\u4E0D\u4F1A\u60F3\u770B\u5230\u56E0\u679C\u5F52\u96F6\u7684\u90A3\u4E00\u523B\u3002\u201D</p><p>\u9EBB\u7F8E\u76B1\u4E86\u76B1\u7709\u5934\uFF0C\u88AB\u5173\u4E8E\u8FD9\u4EF6\u4E8B\u548C\u5B83\u7684\u8352\u8C2C\u6240\u9707\u60CA\u3002\u5979\u2026\u2026 \u88AB\u4EBA\u53D6\u7B11\u4E86\u5417\uFF1F</p><p>\u201C\u90A3\u4E48\u6653\u7F8E\u7130\u600E\u4E48\u6837\u4E86\uFF1F\u201D \u673A\u68B0\u5A18\u95EE\uFF0C\u201C\u4F60\u80FD\u544A\u8BC9\u6211\u4EEC\u5979\u7684\u4E8B\u5417\uFF1F\u6211\u4EEC\u786E\u4FE1\u5979\u8FD8\u6D3B\u7740\uFF0C\u4F46\u662F\u5979\u5728\u505A\u4EC0\u4E48\uFF1F\u5979\u8FD8\u597D\u5417\uFF1F\u201D</p><p>\u9EBB\u7F8E\u4ECE\u4E0D\u4F1A\u60F3\u5230\u4E00\u4E2A\u9762\u76EE\u5168\u975E\u7684\u795E\u7075\u4F1A\u663E\u5F97\u5982\u6B64\u614C\u4E71\uFF0C\u4F46\u4E0D\u77E5\u600E\u7684\uFF0C\u5979\u786E\u5B9E\u6709\u70B9\u614C\u4E71\uFF0C\u80A9\u8180\u5FAE\u5FAE\u4E0B\u5782\uFF0C\u6240\u4EE5\u9EBB\u7F8E\u4E0D\u786E\u5B9A\u5979\u662F\u4E0D\u662F\u5728\u60F3\u8C61\u3002</p><p>\u201C\u60C5\u51B5\u5F88\u590D\u6742\uFF0C\u201D \u5973\u4EBA\u8BF4\u3002</p><p>\u5979\u505C\u987F\u4E86\u4E00\u4E0B\uFF0C\u4F3C\u4E4E\u51B3\u5B9A\u5979\u9700\u8981\u4E00\u4E2A\u4E0D\u540C\u7684\u7B54\u6848\u3002</p><p>\u201C\u6211\u77E5\u9053\u5728\u4F60\u5FC3\u4E2D\uFF0C\u5979\u6C38\u8FDC\u662F\u4F60\u7684\u540E\u8F88\u3002\u90A3\u79CD\u5173\u5FC3\u662F\u4F60\u4E0E\u751F\u4FF1\u6765\u7684\u3002\u4F46\u4F60\u53EF\u4EE5\u76F8\u4FE1\u6211\u4E5F\u5728\u5173\u5FC3\u7740\u5979\uFF0C\u5979\u6B63\u5728\u505A\u7684\u4E8B\u60C5\u5F88\u91CD\u8981\u3002\u5426\u5219\u5979\u5C31\u4E0D\u4F1A\u79BB\u5F00\u4E86\u3002\u201D</p><p>\u201C\u6700\u540E\u4E00\u6B21\u6211\u770B\u5230\u5979\u7684\u65F6\u5019\uFF0C\u5979\u6709\u70B9\u2026\u2026\u5FEB\u75AF\u4E86\u7684\u611F\u89C9\u201D \u9EBB\u7F8E\u52AA\u529B\u60F3\u8BA9\u81EA\u5DF1\u7684\u58F0\u97F3\u81EA\u7136\u4E00\u70B9\u3002\u201C\u6211\u5F88\u96BE\u76F8\u4FE1\u5979\u7684\u8111\u5B50\u8FD8\u6E05\u9192\u7740\u3002\u201D</p><p>\u9EBB\u7F8E\u53C8\u4E00\u6B21\u89C9\u5F97\u773C\u524D\u7684\u90A3\u4E2A\u5973\u4EBA\u6709\u70B9\u52A8\u6447\u4E86\u3002</p><p>\u201C\u4E5F\u8BB8\u4E0D\u662F\u3002\u5979\u8D70\u7684\u8DEF\u5E76\u4E0D\u5BB9\u6613\uFF0C\u4ECE\u67D0\u79CD\u610F\u4E49\u4E0A\u8BF4\u662F\u6211\u7684\u9519\u3002\u5979\u9700\u8981\u4F60\u7684\u5E2E\u52A9\uFF0C\u7B49\u5979\u5411\u4F60\u6C42\u63F4\u65F6\uFF0C\u4F60\u5C31\u4F1A\u627E\u5230\u5979\u3002\u201D</p><p>\u201C\u90A3\u5979\u5230\u5E95\u9700\u8981\u6211\u5E2E\u4EC0\u4E48\u5FD9\u5462\uFF1F\u201D \u9EBB\u7F8E\u575A\u6301\u8BF4 \u201C\u4F60\u5728\u9003\u907F \u2014\u2014\u201D</p><p>\u5979\u751A\u81F3\u8FD8\u6CA1\u8BF4\u5B8C\u5C31\u9000\u7F29\u4E86\uFF0C\u5BF9\u5979\u7075\u9B42\u7684\u6124\u6012\u5982\u7802\u7EB8\u822C\u6298\u78E8\u7740\u5979\uFF0C\u5979\u754F\u7F29\u7740\uFF0C\u5973\u4EBA\u7684\u4E0D\u5FEB\u4F3C\u4E4E\u6D78\u900F\u4E86\u6BCF\u4E00\u79CD\u611F\u89C9\u3001\u6BCF\u4E00\u79CD\u601D\u60F3\u3002\u5979\u8FD8\u4E0D\u77E5\u9053\u73B0\u5B9E\u672C\u8EAB\u4E5F\u80FD\u591F\u8B66\u544A\u4F60\u3002</p><p>\u201C\u4F60\u8FD8\u8BB0\u5F97\u6211\u8BF4\u8FC7\u7684\u56E0\u679C\u62A5\u5E94\u5417\uFF1F\u201C\u6211\u6CA1\u6709\u56DE\u7B54\u8FD9\u4E2A\u95EE\u9898\uFF0C\u5C31\u50CF\u6211\u4E0D\u662F\u6765\u7ED9\u4F60\u63D0\u4F9B\u4E00\u4E2A\u6218\u4E89\u8DEF\u7EBF\u56FE\u4E00\u6837\uFF0C\u4E0D\u7BA1\u4F60\u8BA4\u4E3A\u4F60\u591A\u4E48\u60F3\u8981\u5F97\u5230\u5B83\uFF0C\u201D \u5973\u4EBA\u8BF4 \u201C\u4F60\u60F3\u8981\u4E00\u4E2A\u76F4\u622A\u4E86\u5F53\u7684\u8C08\u8BDD\uFF0C\u6240\u4EE5\u8FD9\u5C31\u662F\u4E3A\u4EC0\u4E48\u6211\u5728\u8FD9\u91CC\u4EE5\u6211\u66F4\u2026 \u81EA\u79C1\u7684\u5F62\u5F0F\u3002\u8FD9\u662F\u6240\u6709\u4EBA\u4ECE\u6211\u8FD9\u91CC\u5F97\u5230\u7684\u6700\u76F4\u63A5\u7684\u7B54\u6848\u3002\u8981\u505A\u5230\u8FD9\u4E00\u70B9\u5E76\u4E0D\u5BB9\u6613\u3002\u5982\u679C\u6211\u662F\u4F60\uFF0C\u6211\u5C06\u4E0D\u80DC\u611F\u6FC0\u3002\u201D</p><p>\u9EBB\u7F8E\u5411\u65C1\u8FB9\u770B\u4E86\u4E00\u4F1A\u513F\uFF0C\u5E0C\u671B\u5979\u81F3\u5C11\u80FD\u770B\u7740\u8FD9\u4E2A\u5973\u4EBA\u7684\u773C\u775B\u3002\u4F46\u8FD9\u663E\u7136\u4E0D\u662F\u5E73\u7B49\u7684\u8C08\u5224\uFF0C\u5979\u88AB\u7C97\u66B4\u5730\u63D0\u9192\uFF0C\u8FD9\u662F\u5979\u7684\u4E16\u754C\uFF0C\u5728\u67D0\u79CD\u7A0B\u5EA6\u4E0A\uFF0C\u5979\u5B81\u613F\u4E0D\u518D\u53D1\u8BDD\u3002</p><p>\u201C\u6211\u60F3\u90A3\u662F\u516C\u5E73\u7684\uFF0C\u201D \u5979\u6700\u540E\u8BF4 \u201C\u6211\u4F1A\u8BF4\uFF0C\u6211\u4E0D\u4F1A\u505C\u6B62\u8C03\u67E5\u8FD9\u4EF6\u4E8B\uFF0C\u4F46\u6211\u60F3\u65E0\u8BBA\u6211\u505A\u4EC0\u4E48\uFF0C\u90FD\u662F\u4F60\u5E0C\u671B\u6211\u505A\u7684\uFF0C\u5982\u679C\u4F60\u771F\u7684\u80FD\u770B\u5230\u672A\u6765\u7684\u8BDD\u3002\u201D</p><p>\u5973\u4EBA\u53D1\u51FA\u4E00\u58F0\u5632\u5F04\u7684\u58F0\u97F3\uFF0C\u51E0\u4E4E\u662F\u4E00\u58F0\u5927\u7B11\uFF0C\u4F46\u6CA1\u6709\u5426\u8BA4\uFF0C\u4E00\u4F1A\u513F\u4E5F\u6CA1\u6709\u518D\u56DE\u7B54\u3002</p><p>\u8FD9\u5F88\u96BE\u8BF4\uFF0C\u4F46\u5979\u597D\u50CF\u6709\u522B\u7684\u60F3\u6CD5\u3002</p><p>\u6700\u540E\uFF0C\u5973\u4EBA\u53F9\u4E86\u53E3\u6C14\uFF0C\u7A81\u7136\u4E00\u9635\u5FAE\u98CE\u5439\u8FC7\u4ED6\u4EEC\uFF0C\u5E26\u8D70\u4E86\u7A7A\u6C14\u4E2D\u7684\u7D27\u5F20\u3002\u9EBB\u7F8E\u4ECD\u7136\u770B\u4E0D\u89C1\u5979\u7684\u8138\uFF0C\u4F46\u6709\u4E00\u4E2A\u660E\u663E\u7684\u5370\u8C61\uFF0C\u90A3\u5973\u4EBA\u73B0\u5728\u6B63\u5728\u5BF9\u5979\u5FAE\u7B11\u3002\u5979\u4E0D\u518D\u751F\u6C14\u4E86\u3002</p><p>\u201C\u6211\u53EB\u4F60\u56DE\u6765\u662F\u56E0\u4E3A\u6211\u77E5\u9053\u4F60\u9700\u8981\u4E00\u4E9B\u652F\u6301\uFF0C\u201D \u90A3\u5973\u4EBA\u8BF4\uFF0C\u201C\u597D\u5427\uFF0C\u544A\u8BC9\u4F60\u4E00\u70B9\u5173\u4E8E\u6218\u4E89\u7684\u4E8B\u3002\u8109\u51B2\u661F\u7684\u91C7\u77FF\u4F5C\u4E1A\u8BA9\u4F60\u5F88\u62C5\u5FC3\uFF0C\u4E0D\u662F\u5417\uFF1F\u201D</p><p>\u9EBB\u7F8E\u77E5\u9053\u8BDD\u9898\u53D8\u4E86\u3002</p><p>\u201C\u4E0D\u4EC5\u4EC5\u662F\u8109\u51B2\u661F\u91C7\u77FF\u4F5C\u4E1A\uFF0C\u8FD8\u6709\u7AE0\u9C7C\u7684\u96C6\u7ED3\uFF0C\u201D \u9EBB\u7F8E\u8BF4\uFF0C\u201C\u4E5F\u8BB8\u653B\u51FB\u77FF\u573A\u53EA\u662F\u53CD\u51FB\u7684\u4E00\u79CD\u65B9\u5F0F\u3002\u6211\u5E0C\u671B\u6211\u4EEC\u6709\u66F4\u591A\u65B9\u5F0F\u3002\u6211\u5E0C\u671B\u8FD9\u573A\u6218\u4E89\u6CA1\u6709\u2026\u2026\u3002\u201D</p><p>\u5979\u505C\u987F\u4E86\u4E00\u4E0B\uFF0C\u659F\u914C\u4E0B\u4E00\u53E5\u8BDD\uFF0C\u5373\u4F7F\u5728\u5EA7\u7684\u5176\u4ED6\u4E24\u4F4D\u90FD\u80FD\u542C\u5230\u5979\u7684\u60F3\u6CD5\u3002</p><p>\u201C\u6211\u771F\u5E0C\u671B\u6211\u4EEC\u4E0D\u662F\u6E29\u6C34\u91CC\u7684\u9752\u86D9\u3002\u201D \u5979\u8BF4\uFF0C\u201C\u5230\u76EE\u524D\u4E3A\u6B62\uFF0C\u5BF9\u5E38\u89C4\u6218\u4E89\u7684\u519B\u4E8B\u9884\u6D4B\u5F88\u5DEE\u3002\u6211\u90FD\u4E0D\u9700\u8981\u544A\u8BC9\u4F60\uFF0C\u76EE\u524D\u7684\u7B56\u7565\u662F\u62D6\u5EF6\u65F6\u95F4\uFF0C\u7B49\u5F85\u6280\u672F\u7A81\u7834\u6216\u771F\u6B63\u7684\u5947\u8FF9\u3002\u8FD9\u611F\u89C9\u4E0D\u597D\u6216\u8005\u4E0D\u5BF9\u3002\u6211\u89C9\u5F97\u6211\u5E94\u8BE5\u505A\u70B9\u4EC0\u4E48\uFF0C\u6211\u5728\u52AA\u529B\u3002\u5982\u679C\u89E3\u51B3\u95EE\u9898\u5C31\u50CF\u53D1\u5C04\u5927\u70AE\u90A3\u6837\u7B80\u5355\uFF0C\u90A3\u5C31\u5BB9\u6613\u591A\u4E86\u3002\u201D</p><p>\u201C\u9EBB\u7F8E\uFF0C\u4F60\u5DF2\u7ECF\u5C3D\u529B\u4E86\uFF0C\u201D \u5973\u4EBA\u8BF4\uFF0C\u201C\u4F60\u4ECE\u6765\u6CA1\u6709\u5728\u524D\u7EBF\u5145\u5206\u53D1\u6325\u8FC7\u81EA\u5DF1\u7684\u80FD\u529B\uFF0C\u65E0\u8BBA\u90A3\u611F\u89C9\u591A\u4E48\u68D2\u3002\u5173\u4E8E\u8109\u51B2\u661F\u2026\u2026 \u6211\u6709\u4E00\u79CD\u4E50\u89C2\u7684\u9884\u611F\u3002\u201D</p><p>\u9EBB\u7F8E\u60F3\u4E86\u4E00\u4F1A\u513F\u3002\u5979\u77E5\u9053\u53EA\u6709\u6697\u793A\u4E5F\u662F\u4E00\u4E2A\u91CD\u5927\u7684\u8BA9\u6B65\uFF0C\u4F46\u662F\u2026</p><p>\u201C\u5C31\u8FD9\u6837\u5417\uFF1F\u201D</p><p>\u5973\u4EBA\u8038\u8038\u80A9\uFF0C\u4E00\u79CD\u5947\u602A\u800C\u7684\u6D41\u7545\u7684\u52A8\u4F5C\uFF0C\u4F3C\u4E4E\u6709\u70B9\u4E0D\u53EF\u601D\u8BAE\uFF0C\u5C31\u50CF\u662F\u666E\u901A\u4EBA\u7C7B\u4E00\u6837\u3002</p><p>\u201C\u544A\u8BC9\u6211\uFF0C\u9EBB\u7F8E\uFF0C\u4F60\u5BF9\u7AE0\u9C7C\u6709\u4EC0\u4E48\u770B\u6CD5\uFF1F\u201D</p><p>\u201C\u5916\u661F\u4EBA\uFF1F\u201D \u9EBB\u7F8E\u95EE\uFF0C\u8BDD\u9898\u7684\u53D8\u5316\u8BA9\u5979\u5F88\u4E0D\u5B89\uFF0C\u201C\u5B83\u4EEC\u662F\u4E00\u79CD\u706D\u7EDD\u79CD\u65CF\u7684\u5A01\u80C1\u3002\u4ED6\u4EEC\u4E0D\u77E5\u4ECE\u54EA\u91CC\u5192\u51FA\u6765\u653B\u51FB\u6211\u4EEC\uFF0C\u4ED6\u4EEC\u6740\u6B7B\u4E86\u6570\u767E\u4E07\u4EBA\uFF0C\u4ED6\u4EEC\u662F\u602A\u7269\u3002\u6211\u80FD\u6709\u4EC0\u4E48\u770B\u6CD5\uFF1F\u201D</p><p>\u201C\u4E0D\u8FC7\uFF0C\u4ED6\u4EEC\u7684\u884C\u4E3A\u6709\u70B9\u5947\u602A\uFF0C\u4E0D\u662F\u5417\uFF1F\u201D \u5973\u4EBA\u95EE \u201C\u4ED6\u4EEC\u4F3C\u4E4E\u771F\u7684\u5BF9\u5B8C\u6210\u8FD9\u9879\u5DE5\u4F5C\u6CA1\u90A3\u4E48\u611F\u5174\u8DA3\u3002\u201D</p><p>\u201C\u4F60\u548C\u90A3\u4E9B\u4E0D\u5F97\u4E0D\u548C\u5B83\u4EEC\u6218\u6597\u7684\u4EBA\u8BF4\u53BB\uFF0C\u201D \u9EBB\u7F8E\u6447\u6447\u5934\uFF0C\u201C\u4ED6\u4EEC\u663E\u7136\u5BF9\u5B8C\u6210\u8FD9\u9879\u5DE5\u4F5C\u5F88\u611F\u5174\u8DA3\uFF01\u6211\u77E5\u9053\u7684\u2026\u2026\u201D</p><p>\u5979\u505C\u987F\u4E86\u4E00\u4E0B\uFF0C\u610F\u8BC6\u5230\u5979\u53EF\u80FD\u5DF2\u7ECF\u5F00\u59CB\u6709\u70B9\u6FC0\u52A8\u4E86\u3002</p><p>\u201C\u6211\u77E5\u9053\u5F53\u4F60\u4ECE\u5168\u5C40\u8003\u8651\u7684\u65F6\u5019\uFF0C\u5B83\u4EEC\u7684\u884C\u4E3A\u65B9\u5F0F\u4F3C\u4E4E\u6CA1\u6709\u4EC0\u4E48\u610F\u4E49\uFF0C\u4F46\u6709\u4E00\u4E9B\u7406\u8BBA\u5BF9\u6B64\u8868\u793A\u6000\u7591\u3002\u4E5F\u8BB8\u4ED6\u4EEC\u53EA\u662F\u4E0D\u60F3\u628A\u6211\u4EEC\u903C\u4E0A\u7EDD\u8DEF\uFF0C\u9664\u975E\u4ED6\u4EEC\u786E\u4FE1\u4ED6\u4EEC\u80FD\u4E00\u6B21\u6027\u628A\u6211\u4EEC\u5E72\u6389\u3002\u89E6\u53D1\u4E00\u6B21\u6211\u4E0D\u60F3\u4E0E\u4E4B\u4E3A\u654C\u7684\u8BB8\u613F\u4F3C\u4E4E\u662F\u4E2A\u5F88\u597D\u7684\u529E\u6CD5\u3002\u201D</p><p>\u201C\u4ED6\u4EEC\u600E\u4E48\u77E5\u9053\u8BB8\u613F\u7CFB\u7EDF\u5462\uFF1F\u201D \u5973\u4EBA\u95EE\uFF0C\u201C\u4ED6\u4EEC\u89C1\u8FC7\u5417\uFF1F\u201D</p><p>\u201C\u4E0D\u80FD\u6392\u9664\uFF0C\u4F46\u4E0D\u4E00\u5B9A\u662F\u613F\u671B\u3002\u4ED6\u4EEC\u53EF\u80FD\u53EA\u662F\u6CE8\u610F\u5230\u6211\u4EEC\u4E0D\u53EF\u80FD\u5377\u571F\u91CD\u6765\u3002\u6709\u53EF\u80FD\u4ED6\u4EEC\u53EA\u662F\u5728\u51C6\u5907\u4E00\u652F\u65E0\u6CD5\u60F3\u8C61\u7684\u5F3A\u5927\u7684\u90E8\u961F\u540C\u65F6\u5BF9\u524D\u7EBF\u4FDD\u6301\u538B\u529B\u3002\u5982\u679C\u6709\u4E00\u5929\u9192\u6765\uFF0C\u53D1\u73B0\u6BCF\u4E00\u6761\u6218\u7EBF\u77AC\u95F4\u574D\u584C\u4E86\uFF0C\u8FD9\u2026\u2026 \u8FD9\u7B80\u76F4\u5C31\u662F\u6211\u7684\u5669\u68A6\u3002\u201D</p><p>\u76F4\u5230\u5979\u5F00\u53E3\uFF0C\u5979\u624D\u6253\u7B97\u8BF4\u6700\u540E\u90A3\u53E5\u8BDD\uFF0C\u4F46\u8FD9\u662F\u81EA\u7136\u800C\u7136\u7684\u60F3\u6CD5\uFF0C\u597D\u5427\uFF0C\u9664\u4E86\u8FD9\u4E24\u4E2A\u4EBA\uFF0C\u5979\u8FD8\u80FD\u544A\u8BC9\u8C01\u5462\uFF1F</p><p>\u201C\u6211\u89C9\u5F97\u8FD9\u4E0D\u5927\u53EF\u80FD\uFF0C\u201D \u90A3\u5973\u4EBA\u8BF4\uFF0C\u201C\u4F46\u4F60\u5BF9\u8FD9\u4E9B\u5916\u661F\u4EBA\u4E0D\u592A\u4E86\u89E3\uFF0C\u662F\u5417\uFF1F\u201D</p><p>\u201C\u6211\u4EEC\u4E0D\u4E86\u89E3\uFF0C\u201D \u9EBB\u7F8E\u5BF9\u8FD9\u8BDD\u4E0D\u6109\u5FEB\u5730\u76B1\u7709\uFF0C\u201C\u4F60\u5462\uFF1F\u201D</p><p>\u5979\u5E26\u7740\u604D\u7136\u5927\u609F\u7684\u795E\u60C5\u8BF4\u4E86\u6700\u540E\u4E00\u4E2A\u95EE\u9898\u3002\u4E8B\u5B9E\u4E0A\uFF0C\u5979\u4E00\u76F4\u4E13\u6CE8\u4E8E\u6709\u5173\u6218\u4E89\u548C\u672A\u6765\u7684\u95EE\u9898\uFF0C\u4EE5\u81F3\u4E8E\u5979\u6CA1\u6709\u610F\u8BC6\u5230\u5979\u53EF\u4EE5\u95EE\u6709\u5173\u5916\u661F\u4EBA\u7684\u95EE\u9898\u3002</p><p>\u201C\u5728\u6218\u4E89\u521D\u671F\uFF0C\u5916\u661F\u4EBA\u786E\u5B9E\u5F88\u5FEB\u9002\u5E94\u4E86\u6211\u4EEC\u7684\u6218\u672F\uFF0C\u4E0D\u662F\u5417\uFF1F\u201D \u5973\u4EBA\u6CA1\u6709\u76F4\u63A5\u56DE\u7B54\u95EE\u9898\uFF0C\u201D \u81EA\u6211\u6BC1\u706D\u6A21\u5757\uFF0C\u4F7F\u6355\u83B7\u66F4\u56F0\u96BE\uFF0C\u5B64\u7ACB\u4FE1\u606F\uFF0C\u4F7F\u8BFB\u5FC3\u672F\u66F4\u4E0D\u5177\u6218\u7565\u6548\u529B\uFF0C\u5404\u79CD\u5C0F\u6280\u5DE7\u3002\u201D</p><p>\u201C\u6211\u4EEC\u8BA4\u4E3A\u4ED6\u4EEC\u7684\u4EBA\u5DE5\u667A\u80FD\u975E\u5E38\u64C5\u957F\u5E94\u6025\u53CD\u5E94\u3002\u201D\u9EBB\u7F8E\u8BF4\u3002</p><p>\u201C\u4ECE\u90A3\u4EE5\u540E\u7684\u4E8C\u5341\u5E74\u91CC\uFF0C\u5B83\u4EEC\u662F\u5426\u66FE\u8BA9\u4F60\u89C9\u5F97\u7279\u522B\u5584\u4E8E\u9002\u5E94\u73AF\u5883\uFF1F\u201D \u5973\u4EBA\u8BF4 \u201C\u53EA\u662F\u5076\u5C14\uFF0C\u5BF9\u5427\uFF1F\u201D</p><p>\u201C\u662F\u7684\uFF0C\u53EA\u662F\u6709\u4E9B\u65F6\u5019\uFF0C\u201D \u9EBB\u7F8E\u540C\u610F\u4E86\u3002</p><p>\u201C\u8FD9\u5C31\u662F\u6211\u7684\u5FC3\u5F97\u3002\u201D \u5973\u4EBA\u8BF4\u3002</p><p>\u5979\u4F4E\u4E0B\u5934\uFF0C\u9EBB\u7F8E\u70E6\u607C\u4E86\u4E00\u4F1A\u513F\uFF0C\u56E0\u4E3A\u5979\u770B\u4E0D\u61C2\u8FD9\u4E2A\u4EBA\u7684\u8868\u60C5\u3002</p><p>\u201C\u4F60\u662F\u8C01\uFF1F\u201D \u9EBB\u7F8E\u5FCD\u4E0D\u4F4F\u95EE\u3002</p><p>\u201C\u53EA\u662F\u4E00\u4F4D\u670B\u53CB\u800C\u5DF2\uFF0C\u201D \u90A3\u5973\u4EBA\u7AD9\u8D77\u8EAB\u6765\u62CD\u5979\u7684\u8863\u670D\uFF0C\u201C\u5BF9\u4E0D\u8D77\uFF0C\u6211\u771F\u7684\u5E94\u8BE5\u8D70\u4E86\u3002\u201D</p><p>\u9EBB\u7F8E\u5F00\u59CB\u6297\u8BAE\uFF0C\u4F46\u5F53\u5979\u8F6C\u8FC7\u8EAB\u6765\u65F6\uFF0C\u5979\u54D1\u53E3\u65E0\u8A00\uFF0C\u9EBB\u7F8E\u770B\u6E05\u4E86\u5979\u7684\u773C\u775B\u3002</p><p>\u5979\u5468\u56F4\u7684\u4E16\u754C\u53D1\u751F\u4E86\u53D8\u5316\uFF0C\u96C6\u4F53\u4F1A\u8BAE\u5BA4\u5F00\u59CB\u626D\u66F2\uFF0C\u5468\u56F4\u7684\u5EFA\u7B51\u53D8\u5F97\u964C\u751F\uFF0C\u4F3C\u4E4E\u8FC7\u4E8E\u5706\u4E86\u3002\u5979\u5468\u56F4\u7684\u5EA7\u4F4D\u4E0A\u5750\u6EE1\u4E86\u5916\u661F\u4EBA\uFF0C\u5B83\u4EEC\u5446\u5728\u539F\u5730\uFF0C\u7A7F\u7740\u6709\u56FE\u6848\u7684\u957F\u888D\u3002\u5728\u5979\u4E0B\u9762\uFF0C\u5728\u6F14\u8BB2\u8005\u7684\u8BB2\u53F0\u4E0A\uFF0C\u7AD9\u7740\u4E00\u53EA\u7AE0\u9C7C\uFF0C\u7A7F\u7740\u548C\u5176\u4ED6\u7684\u7AE0\u9C7C\u4E00\u6837\uFF0C\u5C31\u50CF\u90A3\u4E9B\u7AD9\u7ACB\u5728\u5468\u56F4\u7684\u7AE0\u9C7C\u4E00\u6837\uFF0C\u8138\u671D\u4E0A\uFF0C\u62AC\u8D77\u4E86\u89E6\u89D2\u3002</p><p>\u5728\u5979\u8EAB\u65C1\uFF0C\u5973\u4EBA\u8F6C\u8FC7\u4E86\u8EAB\uFF0C\u9EBB\u7F8E\u7684\u773C\u775B\u5947\u602A\u5730\u76EF\u7740\u5979\u7684\u5934\u53D1\uFF0C\u5B83\u4EEC\u5F00\u59CB\u7F13\u7F13\u6446\u52A8\uFF0C\u5728\u7A7A\u4E2D\u5F62\u6210\u4E86\u4E00\u4E2A\u5708\u3002</p><p><strong>\u8D70\u5427\uFF0C</strong>\u90A3\u5973\u4EBA\u7684\u58F0\u97F3\u5FF5\u8BDD\uFF0C\u5979\u7684\u5171\u9E23\u6DF9\u6CA1\u4E86\u6240\u6709\u7684\u601D\u60F3\uFF0C\u4EE5\u4E00\u79CD\u4F3C\u4E4E\u975E\u4EBA\u7684\u97F5\u5F8B\uFF0C<strong>\u4F60\u8FD8\u6709\u5DE5\u4F5C\u8981\u505A</strong>\u3002</p><p>\u7136\u540E\u5979\u56DE\u5230\u4E86\u73B0\u5B9E\u4E16\u754C\uFF0C\u7741\u5F00\u773C\u775B\uFF0C\u4E18\u6BD4\u6B63\u770B\u7740\u5979\uFF0C\u5979\u53C8\u611F\u89C9\u5230\u673A\u68B0\u5A18\u56DE\u5230\u4E86\u5979\u7684\u8111\u6D77\u91CC\u3002</p>',485),g=[s];function e(_,a){return t(),r("div",null,g)}const h=p(n,[["render",e],["__file","056-\u7B2C\u4E94\u5341\u516D\u7AE0-\u8109\u51B2\u661F.html.vue"]]);export{h as default};
